INSERT INTO `category` VALUES (3, 'ดูดวง', '2012822130904-horo.png', 'ดูดวง ดวงวันนี้ ดวงความรัก ดวง ทำนายฝัน ดูดวงไพ่ยิบซี', 'ดูดวง ดวง ทำนายฝัน ดูดวงไพ่ยิบซี ดูดวงฟรี ดวงวันนี้ ดูดวงแม่นๆ ดูดวงเนื้อคู่ ดูดวงความรัก ดูดวงรายวัน ดูดวงรายปักษ์ ดูดวงรายเดือน ดูดวงรายปี เซียมซี ทายนิสัย แบบทดสอบ กราฟชีวิต ฮวงจุ้ย', 'ดูดวง, ดวง, ทำนายฝัน, ดูดวงความรัก, ดูดวงไพ่ยิปซี, ดูดวงเนื้อคู่, ดูดวงวันเกิด, ดูดวงกราฟชีวิต, กราฟชีวิต, ดูดวงรายวัน, เซียมซี, ดูดวงรายสัปดาห์, ดูดวงรายปี, ดูดวงตามราศี, ไพ่ยิปซี, ดวงวันนี้');
