INSERT INTO `post` VALUES (5, 8, 'เม้าท์สนั่น !! สายป่าน รับแล้วหนุ่มในภาพคือหวานใจตัวจริง', 'เม้าท์สนั่น !! สายป่าน รับแล้วหนุ่มในภาพคือหวานใจตัวจริง', '<span class="Apple-style-span" style="font-family: Tahoma, CordiaUPC; background-color: rgb(255, 255, 255); "><table border="0" bgcolor="#ffffff" width="670" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><p><font size="3"><font color="#3366ff"><strong>เม้าท์สนั่น !! สายป่าน รับแล้วหนุ่มในภาพคือหวานใจตัวจริง<br></strong></font><br>เกิดภาพหลุดสุดสวีทกับฝรั่งตาน้ำข้าว สำหรับสาว<font color="#ff0000"><strong>สายป่าน อภิญญา</strong></font>&nbsp;และก็มีข่าวล่าสุดออกมาว่า&nbsp;<font color="#0000ff"><strong>ดาราสาวยอมรับว่ากำลังคบกับหนุ่มในรูปมาได้ประมาณ 3 เดือน และหนุ่มคนนี้ก็เป็นนักกีฬาพารามอร์เตอร์ ครูสอนของโจอี้ บอย ซะด้วย</strong></font>&nbsp;เอาเป็นว่าหนุ่มในรูปก็คือแฟนหนุ่มของสายป่าน ถือว่าเคลียร์ซะนะ</font></p><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img8/694991.jpg" ''="" align="center" alt="สายป่าน อภิญญา - แฟนหนุ่ม"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">สายป่าน อภิญญา - แฟนหนุ่ม</font></td></tr></tbody></table><font color="#003399"><b></b></font><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img8/694992.jpg" ''="" align="center" alt="สายป่าน อภิญญา - แฟนหนุ่ม"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">สายป่าน อภิญญา - แฟนหนุ่ม<br><br></font></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></span>', '2012821200036-694990.jpg', 2, '2012-08-21 20:00:36', 3);
INSERT INTO `post` VALUES (6, 8, 'ชมพู่โดนใจปมแม่ผัว- ลูกสะใภ้รักคุณฯ', 'ชมพู่โดนใจปมแม่ผัว- ลูกสะใภ้รักคุณฯ', '<span class="Apple-style-span" style="font-family: Tahoma, CordiaUPC; background-color: rgb(255, 255, 255); "><table border="0" bgcolor="#ffffff" width="670" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img4/694965.jpg" ''="" align="center" alt="ชมพู่ อารยา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">ชมพู่ อารยา</font></td></tr></tbody></table><font color="#003399"><b></b></font><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><font color="#003399"><b></b></font><div><font size="3" color="#3366ff"><strong>เปิดตัวแล้วในฐานะนางเอกคนที่สามของ พี่เคน-ธีรเดช&nbsp;</strong></font></div><div><font size="3"><br></font></div><div><font size="3">ในละครเรื่อง<strong>&nbsp;<font color="#ff0000">“รักคุณเท่าฟ้า”</font>&nbsp;</strong>นางเอกฮอต&nbsp;<strong><font color="#333399">ชมพู่-อารยา เอ ฮาร์เก็ต</font></strong>&nbsp;พูดถึงบท “ข้าวตู” ซึ่งแตกต่างจากบทนางเอกคนอื่นว่า</font></div><div><font size="3"><br></font></div><div><font size="3">“บทข้าวตูในเรื่อง จะเป็นเด็กนอก มั่นใจ เคยรักพี่เคนตั้งแต่สมัยเด็กๆพอกลับมาเจอกันอีกครั้ง เห็นว่าเขาเป็นโสด ก็จีบเลย แต่เขาเชิดใส่ ต้องใช้การตามตื๊อ แต่เขาก็ไม่สน จนถอดใจ ผู้ชายก็ใจอ่อนกลับมาชอบ ก็ดูเป็นรักที่สมหวังนะ เพราะครั้งนี้ผู้ชายจะไม่ค่อยสนแม่เท่าไหร่ แล้วข้าวตูจะปะทะกับแม่ไม่ใช่ก้าวร้าวนะ แต่คิดยังไงก็พูดอย่างนั้น บอกแม่ไปเลยว่าจะอยู่กับลูกไปได้อีกกี่นานทำไมไม่ทำให้ทุกคนมีความสุข&nbsp;</font></div><div><font size="3"><br></font></div><div><font size="3">แต่พอพ้นเรื่องแม่ ก็กลายเป็นเรื่องของคนสองคนแล้ว<strong><font color="#ff0000">&nbsp;กัปตันธีระที่พี่เคนเล่น ในฐานะคนกลาง</font></strong>&nbsp;ก็ต้องบางลานซ์เรื่องชีวิตส่วนตัวและชีวิตครอบครัวให้ได้ เวลาอยู่กับแฟนก็ต้องดูแลเขาในสิ่งที่เขาต้องการ เวลาอยู่กับแม่ก็อีกแบบหนึ่ง</font></div><div><font size="3"><br></font></div><div><font size="3">&nbsp;ต้องยอมรับข้อจำกัดของแต่ละคนด้วย ละครเรื่องนี้ก็มีมุมโดนๆ เรื่องแม่ผัวลูกสะใภ้ เรื่องของคนสองคนที่รักกันแล้วมีปัญหารอบด้านต่างๆ ด้วย”</font></div><div><font size="3"><br></font></div><div><font size="3">“ชมพู่ว่าดูละครเรื่องนี้แล้วคุ้มค่านะคะ เพราะไม่เคยมีละครแนวนี้มาก่อน&nbsp;<strong><font color="#333399">นางเอกมีถึง 4 คน ภาพสวย ดูดีหมด</font></strong>แล้วได้แง่คิดทั้งเรื่องความรักและเรื่องครอบครัว ดูแล้วย้อนไปดูว่าเราปฏิบัติกับคนที่บ้านดีแล้วหรือยัง ทั้งแม่และภรรยา อยากเชียร์ให้ตามดูกันค่ะ”</font></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img4/694967.jpg" ''="" align="center" alt="ชมพู่ อารยา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">ชมพู่ อารยา</font></td></tr></tbody></table><font color="#003399"><b></b></font><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img4/694968.jpg" ''="" align="center" alt="ชมพู่ อารยา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">ชมพู่ อารยา</font></td></tr></tbody></table><font color="#003399"><b></b></font><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img4/694969.jpg" ''="" align="center" alt="ชมพู่ อารยา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">ชมพู่ อารยา</font></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></span>', '2012821200548-694965.jpg', 3, '2012-08-21 20:05:48', 2);
INSERT INTO `post` VALUES (7, 8, 'วีเจจ๋า ยังปากแข็งควง หนุ่มกึ้ง ดูหนัง', 'วีเจจ๋า ยังปากแข็งควง หนุ่มกึ้ง ดูหนัง', '<span class="Apple-style-span" style="font-family: Tahoma, CordiaUPC; background-color: rgb(255, 255, 255); "><table border="0" bgcolor="#ffffff" width="670" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><font size="3" color="#3366ff"><strong>คนตาดีแอบเห็นวีเจคนดัง จ๋า ณัฐฐาวีรนุช ทองมี ควงไฮโซหนุ่ม กึ้ง เฉลิมชัย มหากิจศิริ&nbsp;</strong></font><div><font size="3"><br></font></div><div><font size="3">ไปดูหนังที่เซ็นทรัลลาดพร้าว แถมยังกระหนุงกระหนิงสวีทกัน ซะจนมดไต่ รายการไนน์เอ็นเตอร์เทนเลยไปถามวีเจจ๋าให้หายข้องใจ ว่ากำลังคบกับหนุ่มกึ้งอยู่รึเปล่า แต่นางยังปากแข็งอยู่ค่า</font></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><font color="#003399"><b></b></font><div><object classid="clsid:d27cdb6e-ae6d-11cf-96b8-444553540000" codebase="http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0" width="640" height="480"><embed type="application/x-shockwave-flash" src="http://www.youtube.com/v/LagjOKXQfQI?version=3&amp;hl=th_TH&amp;autoplay=1" width="640" height="480" allowscriptaccess="always" allowfullscreen="true"></object></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img5/694973.jpg" ''="" align="center" alt="“วีเจจ๋า ณัฐฐาวีรนุช” "></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">“วีเจจ๋า ณัฐฐาวีรนุช”</font></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></span>', '2012821200933-694973.jpg', 1, '2012-08-21 20:09:33', 3);
INSERT INTO `post` VALUES (8, 8, 'แพทณปภาควงแฟนหนุ่มไฮโซนิว เปิดตัว', 'แพท-ณปภาควงแฟนหนุ่มไฮโซนิว เปิดตัว', '<span class="Apple-style-span" style="font-family: Tahoma, CordiaUPC; background-color: rgb(255, 255, 255); "><table border="0" bgcolor="#ffffff" width="670" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img7/695095.jpg" ''="" align="center" alt="แพท-ณปภา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">แพท-ณปภา</font></td></tr></tbody></table><font color="#003399"><b></b></font><font size="3"><strong style="font-family: ''MS Sans Serif'', CordiaUPC; ">แพทควงไฮโซนิวยินดีร้านตำแหลกั๊กแฟน แพท ณปภา ควง ไฮโซนิว ยินดีร้านตำแหลครบ 1 ปี ปัดเปิดตัว กั๊กเรียกแฟน โบ้ยถามฝ่ายชายเอง</strong><font color="#800080" style="font-family: ''MS Sans Serif'', CordiaUPC; ">หลังจากที่ แพท ณปภา เลิกรากับ ไฮโซฮิม อิสริยะ คูหาเปรมกิจ ซึ่งตอนนี้กำลังกุ๊กกิ๊กอยู่กับ บี น้ำทิพย์ จงรัชตวิบูลย์&nbsp;</font><span style="font-family: ''MS Sans Serif'', CordiaUPC; ">ซึ่งก็ทำให้มีข่าวว่าสาวแพท ปิดหัวใจอยู่นานถึง 1 ปีเต็ม แต่ล่าสุด ก็กลับมาเปิดหัวใจให้สดใสซาบซ่าอีกครั้ง เมื่อแพทให้สัมภาษณ์ว่า กำลังคบหาดูใจอยู่กับนัท หนุ่มนอกวงการ แต่สถานะยังเป็นเพื่อนสนิท ยังไม่้เป็นแฟนแต่อย่างใด</span><br><br><br><span style="font-family: ''MS Sans Serif'', CordiaUPC; ">ทั้งนี้ แพทให้สัมภาษณ์ว่า</span><span style="font-family: ''MS Sans Serif'', CordiaUPC; color: rgb(128, 0, 0); "><span style="font-weight: bold; ">&nbsp;รู้จักกับหนุ่มนัทมาประมาณ 3-4 เดือนแล้ว ที่ได้เจอกันเพราะเพื่อนเก่าไปกินข้าวด้วยกัน แล้วแนะนำให้รู้จัก จนถึงตอนนี้ก็สนิทกัน แต่ไม่ถึงขั้นเรียกว่าแฟน</span>&nbsp;ส่วนตัวนัทเองก็เป็นคนน่ารัก ตั้งใจทำงาน มีความรับผิดชอบ เข้าใจโลกมาก และปรึกษาได้ทุกเรื่อง</span><br><br><span style="font-family: ''MS Sans Serif'', CordiaUPC; ">ส่วนเรื่องระวังตัวนั้น แพทยอมรับว่าระวังตัว แต่ไม่ได้กลัวผิดพลาดอะไร หากวันไหนเรียก นัท ว่าแฟนเเล้วเดี๋ยวจะบอก</span></font>&nbsp;<br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img7/695096.jpg" ''="" align="center" alt="แพท-ณปภา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">แพท-ณปภา</font></td></tr></tbody></table><font color="#003399"><b></b></font><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img7/695097.jpg" ''="" align="center" alt="แพท-ณปภา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">แพท-ณปภา</font></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table></span>', '2012821201104-695095.jpg', 1, '2012-08-21 20:11:04', 0);
INSERT INTO `post` VALUES (9, 8, 'เวียร์ ปัดสาวนอกวงการตามคุม กั๊กเรียกแฟน', 'เวียร์ ปัดสาวนอกวงการตามคุม กั๊กเรียกแฟน', '<span class="Apple-style-span" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 17px; background-color: rgb(255, 255, 255); "><div><font size="3" color="#3366ff"><strong>หนุ่ม "เวียร์" ปัด สาวนอกวงการตามคุม อุบชื่อแคท กั๊กเรียกแฟน ลั่น หยุดเจ้าชู้ คุยทีละคน รับโสดแต่ไม่สนิท</strong></font></div><div><font size="3"><br></font></div><div><font size="3">เปิดปากกำลังคบหาอยู่กับสาวนอกวงการ สำหรับพระเอกหนุ่ม&nbsp;<strong><font color="#ff0000">"เวียร์-ศุกลวัฒน์"&nbsp;</font></strong>พอเจอ หนุ่มเวียร์ มาร่วมงาน แกรนด์โอเพ่นนิ่ง โอมาซ แกลเลอรี่ ที่ชั้น 1 โซนเมกาโฮม เมกาบางนา งานนี้หนุ่มเวียร์ ก็อัพเดทความสัมพันธ์ให้ฟังว่า&nbsp;</font></div><div><font size="3"><br></font></div><div><font size="3">"วันนี้ไม่ได้มาด้วยกัน แต่ปกติเราก็ไปไหนด้วยกันบ้าง เพราะเราไม่ได้ไปซุกไว้<strong><font color="#333399">&nbsp;อย่างเวลาไปทานข้าว ช็อปปิ้ง หรือออกกำลังกาย&nbsp;</font></strong>ถ้าว่าง เราก็ไปด้วยกันได้ เรื่องพามาเปิดตัวกับสื่อ ก็คงยัง เพราะเขาไม่ใช่คนในวงการบันเทิงด้วย และเขาก็เรียนภาษาอยู่ด้วย เลยไม่ค่อยมีเวลา ถามว่าชื่อแคทไหม ก็ไม่ขอบอกดีกว่า ใช่ ไม่ใช่ ก็ไม่ขอบอกดีกว่า เขาไม่ได้ทำงานในวงการบันเทิง ไม่ได้ทำอยู่ที่เอ็กแซ็กท์ คือ เขาทำงานอยู่นอกวงการบันเทิง</font></div><div><font size="3"><br></font></div><div><font size="3">&nbsp;แต่ตอนนี้ก็ออกแล้ว รอไปเรียนต่อ ถามว่าเป็นแฟนหรือยัง ก็ยังไม่ใช่แฟน ตอนนี้ก็กำลังคุยๆ กันอยู่ ก็พยายามดูแลกันไปเรื่อยๆ ก่อน เราไม่รีบ ก็ค่อยๆ ดูกันไปดีกว่า ตอนนี้ก็เหมือนเป็นช่วงพิสูจน์ตัวเอง<strong><font color="#ff0000">&nbsp;ถามว่า ตอนนี้ผมหยุดหรือยัง</font></strong></font><span style="font-size: medium; "><strong><font color="#ff0000">ตอนนี้ก็หยุดแล้ว&nbsp;</font></strong></span></div><div><span style="font-size: medium; "><br></span></div><div><span style="font-size: medium; ">เพราะเวลาผมคุยกับใครก็คุยทีละคน เพราะเดี๋ยวมันจะดูไม่ดี ด้านครอบครัวของเขา ผมก็ยังไม่มีโอกาสได้เจอ แต่ผู้ใหญ่ก็รับรู้ ว่าเราคุยกันอยู่ เราก็เข้าตามตรอก ออกตามประตู ตอนนี้ก็ถือว่า โลกเป็นสีชมพู โสดแต่ไม่สนิทครับ"</span></div></span>', '', 1, '2012-08-21 20:12:46', 0);
INSERT INTO `post` VALUES (10, 8, 'วี โต้ออกลาย คาสโนว่าตามจีบ ซาร่า ดาราใหม่', 'วี โต้ออกลาย คาสโนว่าตามจีบ ซาร่า ดาราใหม่', '<span class="Apple-style-span" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 17px; background-color: rgb(255, 255, 255); "><div><font size="3" color="#3366ff"><strong>หนุ่ม ''วี วีรภาพ'' โต้จีบ ''ซาร่า'' ดาราใหม่ติดใจ ร้องแร็ป วง V.I.P ปลื้ม พริกกับเกลือ กระแสดี</strong></font></div><div><font size="3"><br></font></div><div><font size="3">เจอตัวหนุ่ม<strong><font color="#ff0000">&nbsp;"วี - วีรภาพ สุภาพไพบูลย์"&nbsp;</font></strong>มาร่วมงาน "ซุปตาร์ชวนเปลี่ยนเป็น ทรูมูฟ เอช 3G+ แรงทั่วไทย" ที่ จังหวัดนครปฐม ทั้งที เลยขออัพเดทเรื่องผลงาน ที่แอบไปทำเซอร์ไพรส์ เปลี่ยนลุคมาเป็นหนุ่มฮิปฮอป ร่วม Featuring ในเพลง Brand News ของศิลปินใหม่วง V.I.P และหัวใจซะหน่อย หลังมีกระแสข่าวว่า หนุ่มวี แอบเชียร์สาว&nbsp;<strong><font color="#333399">"ซาร่า - ธนพร"</font></strong>&nbsp;ผู้เข้าประกวดมีเดีย เซิร์ซ จนออกนอกหน้า แถมยังขอแชะภาพคู่ และคุยอย่างใกล้ชิดอีกด้วย งานนี้หนุ่มวีก็เคลียร์เรื่องทั้งหมดให้ฟังว่า</font></div><div><font size="3"><br></font></div><div><font size="3">"ร้องแร็ปกับวง V.I.P ก็ดี คือเราก็ได้ทำมันอะไรแปลกๆ อีกอย่างหนึ่ง สนุกดี แต่ก็ไม่ได้จริงจังอะไรขนาดนั้น ขอแค่เพลงเขาไม่เจ๊งก็ดีแล้ว ซึ่งตัวผมเองเป็นคนไม่ชอบร้องเพลง แต่เป็นคนชอบฟังเพลง ชอบดนตรีแบบฮิปฮอป, R&amp;B แล้วที่ได้ไปแจมก็เพราะเรารู้จักกันอยู่ เขาชวนมาผมก็ไปเลย วันอัดก็ถ่ายอยู่ประมาณ 2 ชั่วโมง ก็ดี สนุกดี มีสาวๆ ล้อมหน้าล้อมหลัง ก็เพราะเนื้อเพลงมันเป็นแบบนั้น&nbsp;<strong><font color="#ff6600">ถ้าเป็นผู้ชายหมด ก็คงไม่ไหว&nbsp;</font></strong></font></div><div><font size="3"><br></font></div><div><font size="3">อย่างไรดนตรีฮิปฮอปก็คู่กับสาวเซ็กซี่อยู่แล้ว ถามว่า อนาคตคิดอยากมีอัลบั้มเป็นของตัวเองเลยไหม ก็คิดอยู่ แต่คงเป็นเพลงช้าๆ แนว R&amp;B แต่ก็ต้องดูเวลาก่อน เพราะช่วงนี้ไม่มีเวลา งานเยอะ สำหรับกระแสตอบรับ ละครพริกกับเกลือ ก็ดี เพิ่งออกอากาศไปได้ประมาณ 3 - 4 ตอน ฟีดแบกก็ถือว่าดีมากเลย<strong><u><font color="#333399">&nbsp;ส่วนเรื่องกระแสข่าวที่ว่า ใบเตย ไปเล่นละครแล้วมาปรึกษาผมนั้น เรื่องนี้ไปกันใหญ่แล้ว</font></u></strong>&nbsp;ก็ไม่มีอะไร มาถามผมเสร็จ เดี๋ยวก็ต้องไปถามน้อง ก็เขียนกันยาวไปอีก มันเป็นเรื่องที่นานมาแล้ว และตอนนี้ผมก็ไม่ได้คุยกับน้องเขาเลย&nbsp;</font></div><div><font size="3"><br></font></div><div><font size="3">ไม่ได้คุยกันมาเป็นเดือนๆ แล้ว นานมากแล้ว ถามว่าตอนนี้ผมมีคนคุยด้วยไหม มันก็ต้องมีบ้าง โตปานนี้แล้ว และผมก็ไม่ใช่คนไม่มีสังคม แต่มันก็ต้องคุยกันไปก่อน เป็นพี่เป็นน้องกันไปก่อน เรื่องแบบนี้ต้องใจเย็นๆ อย่าไปใจร้อน<strong><font color="#ff0000">ส่วนเรื่องซาร่า เรื่องนี้ก็ไปกันใหญ่ ข่าวก็เข้าใจเขียน&nbsp;</font></strong>ผมเป็นกรรมการ และพอนักข่าวถามคนไหนน่ารัก เชียร์คนไหนเป็นพิเศษ ผมก็ตอบรวมๆ ไม่ได้จีบ เพราะทำหน้าที่กรรมการเสร็จก็กลับ"</font></div></span>', '2012821201545-img_2_210.jpg', 1, '2012-08-21 20:15:45', 2);
INSERT INTO `post` VALUES (11, 8, 'ย้อนเส้นทางรักของ พิ้งกี้ สาวิกา ก่อนจะพัวพันข้อหา มือที่สาม', 'ย้อนเส้นทางรักของ พิ้งกี้ สาวิกา ก่อนจะพัวพันข้อหา มือที่สาม', '<span class="Apple-style-span" style="font-family: Tahoma, CordiaUPC; background-color: rgb(255, 255, 255); "><table border="0" bgcolor="#ffffff" width="670" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><div><font size="3">ตกเป็นข่าวคราวมือที่สามของคู่สามีภรรยา&nbsp;<strong><font color="#ff0000">''เป๊ก สัญชัย''&nbsp;</font></strong>และ<strong><font color="#ff0000">&nbsp;''ธัญญ่า ธัญเรศ'' เองตระกูล</font></strong>&nbsp;มายาว นาน ข้ามปี ตั้งแต่ ลูกสาวของ ''เป๊ก''-''ธัญญ่า''อย่าง''น้องลียา'' เพิ่งคลอด จนตอนนี้ ก็สามขวบแล้ว แต่นางเอกสาว''พิ้งกี้ สาวิกา ไชยเดช'' ก็ยังไม่สามารถ จะสลัดข้อหานี้พ้นซักกะที...</font></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img1/694564.jpg" ''="" align="center" alt="พิ้งกี้ สาวิกา"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">พิ้งกี้ สาวิกา</font></td></tr></tbody></table><font color="#003399"><b></b></font><div><span style="font-size: medium; ">แถมเรื่องราวยังบานปลายขึ้นเรื่อยๆ โดยเฉพาะเรื่องล่าสุดอย่างคลิปเสียง ฉะแหลก! ระหว่าง ''ธัญญ่า'' และ ''คุณแม่อ้อย'' ของสาวกี้ โดยเนื้อหาใจความหลักๆ ก็ เนื่องจาก ''สาวธัญญ่า'' นั้นเกิดอาการไม่พอใจที่บังเอิญไปเปิดเจอ ข้อความในมือถือของสามีอย่าง'' เป๊ก สัญชัย'' ซึ่งมีข้อความ ว่า&nbsp;<strong><font color="#ff0000">MISS YOU</font></strong>&nbsp; และถูกส่งมาจากมือถือของ ''สาว พิ้งกี้'' เข้า นั่นเอง!! และนั่นก็เป็นการเริ่มต้น ''ศึก'' คลิปเสียงระลอกใหม่ ที่เป็นข่าวฮือฮา บนหน้าสื่ออีกครั้ง!!</span></div><div><font size="3"><br></font></div><div><font size="3">&nbsp; &nbsp;เรียกได้ว่า มหากาพย์เรื่องนี้ลากยาวมานานนมจน ใครหลายๆคนแทบจะหลงลืมไปแล้วว่า ก่อนจะตกมาเป็นข่าวพัวพันกับ''ธัญญ่า -เป๊ก'' &nbsp;นั้น&nbsp;<strong><font color="#333399">''พิ้งกี้''</font></strong>&nbsp;เคยมีข่าวว่าคบหาดูใจกับ''หนุ่ม'' คนไหนมาบ้าง วันนี้จะขอรวบรวมรายชื่อบรรดา''หนุ่มๆ'' ที่เคยตกเป็นข่าวกับ''สาวกี้'' มาให้ดูกัน..</font></div><div><br></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img1/694565.jpg" ''="" align="center" alt="เบนซ์ นิพิธ"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">เบนซ์ นิพิธ</font></td></tr></tbody></table><font color="#003399"><b></b></font><div><font size="3">เริ่มที่คนแรก&nbsp;<strong><font color="#ff0000">''เบนซ์ เบนซ์ นิพิธ ฮันเก้''&nbsp;</font></strong>นายแบบหนุ่มลูกครึ่ง อดีตนักแสดงในสังกัด ''กันตนา'' สังกัดเดียวกันกับ ''สาวกี้'' ที่มีข่าวว่า คบหากันอยู่ระยะหนึ่งตั้งแต่สมัยที่''สาวกี้'' เพิ่งเริ่มโด่งดัง ในฐานะ ''นางเอกตอนโต'' หลังมีผลงานในฐานะ''นักแสดงเด็ก'' มาหลายปี แต่เป็นข่าวด้วยกันไม่เท่าไหร่ &nbsp;ข่าวระหว่าง &nbsp;พิ้งกี้ และ เบนซ์ นิพิธ ก็มีอันต้องเงียบหายไป ตามระยะเวลา...</font></div><div><br></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img1/694566.jpg" ''="" align="center" alt="พิ้งกี้ สาวิกา - อั้ม อธิชาติ"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">พิ้งกี้ สาวิกา - อั้ม อธิชาติ</font></td></tr></tbody></table><font color="#003399"><b></b></font><div><font size="3">หลังจากนั้นข่าวเรื่องรักๆของ<strong><font color="#333399">&nbsp;''พิ้งกี้''&nbsp;</font></strong>ก็เงียบไปอีกซักระยะ ก่อนจะ มาเปิดตัวโครมครามว่า กำลังปลูกต้นรักครั้งใหม่ กับ พระเอกล่ำปากแดง ต่างวิก อย่าง&nbsp;<strong><font color="#ff0000">''อั้ม อธิชาติ ชุมนานนท์''&nbsp;</font></strong>เรียกว่าความรักครั้งนั้นของพิ้งกี้ หวานชื่น และดูเหมาะสม กันมากมาย แต่ถึงอย่างนั้น &nbsp;ก็ยังไปไม่ถึงฝั่งฝันอยู่ดี เพราะหลังจากคบหาดูใจกันมาหลายปี สุดท้ายทั้ง พิ้งกี้ และ อั้ม ก็ ออกมาประกาศว่า ได้เลิกลาจบความสัมพันธ์กันลงไปจนได้</font></div><div><br></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img1/694567.jpg" ''="" align="center" alt="พิ้งกี้ สาวิกา - แอฟ - สงกรานต์"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">พิ้งกี้ สาวิกา - แอฟ - สงกรานต์</font></td></tr></tbody></table><font color="#003399"><b></b></font><div><font size="3">หลังเลิกกับ&nbsp;<strong style="color: rgb(255, 0, 0); ">''อั้ม อธิชาติ''&nbsp;</strong>ไป พิ้งกี้ ก็ตกเป็นข่าว ฮือฮา อีกครั้ง เมื่อมีข่าวปูดมาว่า เจ้าตัว กำลัง ซุ่มเงียบปลูกต้นรักครั้งใหม่กับ หนุ่มไฮโซ ทายาท ''โบนันซ่า''&nbsp;<strong><font color="#333399">สงกรานต์ &nbsp;เตชะณรงค์ &nbsp;</font></strong>ซึ่งตอนนั้น เมื่อมีนักข่าวไปถาม ''พิ้งกี้'' ก็ตอบไปในลักษณะว่า กำลัง เริ่มเปิดใจศึกษากันจริง ในขณะที่ฟากของ''สงกรานต์'' กลับ บอกว่า ความสัมพันธ์กับ''พิ้งกี้'' นั้น ก็แค่ คนรู้จัก ธรรมดา ๆ แถมหลังจากนั้นไม่นาน ''สงกรานต์'' ยังไปเปิดตัวว่า กำลังคบหากับ นางเอก''แอฟ ทักษอร ภักดิ์สุขเจริณ'' อีกต่างหาก ก่อนที่หลังจากนั้น ''แอฟ-สงกรานต์'' จะลงเอยกันด้วยการแต่งงานไป &nbsp;เรียกว่าเรื่องราวครั้งนั้น ทำให้ พิ้งกี้ โดนหนักหลายข้อหาเลยทีเดียว ทั้ง ''ออกตัวแรง'' ''คิดเองเออเอง'' และ''โดนผู้ชายหักหน้า''</font></div><div><br></div><div><br></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img1/694568.jpg" ''="" align="center" alt="พิ้งกี้ สาวิกา - ธัญญ่า - เป๊ก"></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399">พิ้งกี้ สาวิกา - ธัญญ่า - เป๊ก</font></td></tr></tbody></table><font color="#003399"><b></b></font><span style="font-size: medium; ">และหลังจาก&nbsp;<strong><font color="#ff0000">''เจ็บช้ำ''&nbsp;</font></strong>จาก<strong><font color="#ff0000">&nbsp;''ความรัก''?</font></strong>&nbsp;คราวนั้น พิ้งกี้ ก็มาตกเป็นข่าวพัวพันกับ ''ครอบครัวเองตระกูล'' จนเรื่องราวลากยาวเป็นมหากาพย์อีกเรื่องของวงการบันเทิงดังกล่าว...</span><div><span style="font-size: medium; "><br></span></div><div><font size="3">และเมื่อเรื่องราวข่าวคราวของ''พิ้งกี้'' และ ''เป๊ก'' -''ธัญญ่า''ได้ ดำเนินมาจนถึงจุดที่ทุกฝ่าย บอกว่า จะจบแล้วจริงๆ! ก็ได้แต่เอาใจช่วยว่า ''พิ้งกี้'' จะสมหวังในความรักซักทีนะ...!</font></div></td></tr></tbody></table></td></tr></tbody></table></span>', '2012821201817-ping_story.jpg', 1, '2012-08-21 20:18:17', 11);
INSERT INTO `post` VALUES (12, 11, 'แปลกตา! พายุงวงช้าง 5 ลูกในทะเลสาบมะกัน', 'คลิป พายุงวงช้าง 5 ลูกในทะเลสาบมะกัน เปิดเผยภาพปรากฏการณ์สุดแปลกตา พายุงวงช้างเกิดขึ้นพร้อม ๆ กันถึง 5 ลูก เหนือพื้นผิวทะเลสาบมิชิแกน', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); ">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>เมื่อวันที่ 20 สิงหาคมที่ผ่านมา เว็บไซต์นิวยอร์กเดลี่ของสหรัฐฯ เปิดเผยภาพปรากฏการณ์สุดแปลกตา พายุงวงช้างเกิดขึ้นพร้อม ๆ กันถึง 5 ลูก เหนือพื้นผิวทะเลสาบมิชิแกน</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">ภาพปรากฏการณ์ดังกล่าวได้รับการเผยแพร่โดยเว็บไซต์สตอร์มเชสซิ่งวิดีโอ ถูกบันทึกได้เมื่อวันที่ 18 สิงหาคมที่ผ่านมา โดยช่างภาพรายหนึ่งที่โดยสารบนเรือขนส่งสินค้า ขณะที่กำลังล่องอยู่ในทะเลสาบ ห่างจากเมืองมิลวอกี รัฐวิสคอนซินของสหรัฐฯ ไปราว ๆ 72 กิโลเมตร&nbsp;<b>จู่ ๆ พายุงวงช้างก็ก่อตัวขึ้นระหว่างผิวน้ำและเมฆ ก่อนที่จะเกิดพายุงวงช้างอีก 4 ลูก ขึ้นในเวลาไล่เลี่ยกัน</b>&nbsp;นับเป็นปรากฏการณ์แปลกตาที่หาดูได้ยากมาก</span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; อย่างไรก็ดี ศูนย์พยากรณ์อากาศแห่งชาติสหรัฐฯ ได้เปิดเผยว่า จริง ๆ แล้วมีพายุงวงช้างเกิดขึ้นถึง 9 ลูก โดยเกิดขึ้น 2 ระลอก แต่ช่างภาพคนดังกล่าวสามารถเก็บภาพปรากฏการณ์มาได้ ขณะเกิดพายุงวงช้างพร้อมกัน 5 ลูกเท่านั้น<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(128, 0, 0); "><b>ทั้งนี้ สำหรับพายุงวงช้าง หรือ Waterspout เป็นปรากฏการณ์ที่มีลักษณะคล้ายท่อน้ำขนาดใหญ่เชื่อมต่อระหว่างผืนฟ้าและพื้นน้ำ ระหว่างที่เกิดฝนฟ้าคะนอง&nbsp;</b>เกิดจากการที่มวลอากาศเย็นเคลื่อนผ่านเหนือผิวน้ำที่อุ่นกว่า โดยบริเวณใกล้ๆ ผิวน้ำมีความชื้นสูง และไม่ค่อยมีลมพัด (หรือถ้ามีก็พัดเบาๆ) ผลก็คืออากาศที่อยู่ติดกับผืนน้ำซึ่งอุ่นในบางบริเวณจะยกตัวขึ้นอย่างรวดเร็วและรุนแรง ทำให้อากาศโดยรอบไหลเข้ามาแทนที่ จากนั้นจึงพุ่งเป็นเกลียวขึ้นไป แบบนี้เรียกว่า&nbsp;<b>"นาคเล่นน้ำ"</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>พายุชนิดนี้สามารถเคลื่อนที่ได้เร็วตั้งแต่ 3 - 130 กิโลเมตรต่อชั่วโมง แต่ส่วนใหญ่จะเคลื่อนที่ค่อนข้างช้าประมาณ 18 - 28 กิโลเมตรต่อชั่วโมง</b>&nbsp;ทั้งนี้ พายุนี้มีอายุไม่ยืนยาวนัก คืออยู่ในช่วง 2 - 20 นาที จากนั้นก็จะสลายตัวไปในอากาศอย่างรวดเร็ว<br><br><br><div align="center"><embed width="550" height="373" type="application/x-shockwave-flash" src="http://www.youtube.com/v/g2Zszsxn-Dw?version=3&amp;hl=th_TH&amp;rel=0"><br></div><div align="center">&nbsp;</div><a target="_blank" href="http://www.youtube.com/watch?v=g2Zszsxn-Dw" style="text-decoration: none; "><div align="center">คลิป Five simultaneous waterspouts filmed at Lake Michigan โพสต์โดย คุณ RussiaToday</div></a></span>', '2012821202409-ST.jpg', 1, '2012-08-21 20:24:09', 0);
INSERT INTO `post` VALUES (13, 11, 'โอ๋ ภัคจีรา โพสต์ขอโทษโดนด่าหลุดขำระหว่างอ่านข่าวคนตาย', 'โอ๋ ภัคจีรา โพสต์ขอโทษโดนด่าหลุดขำระหว่างอ่านข่าวคนตาย', '<span class="Apple-style-span" style="font-family: Tahoma, CordiaUPC; background-color: rgb(255, 255, 255); "><table border="0" bgcolor="#ffffff" width="670" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><div><font size="3" color="#3366ff"><strong>เป็นเรื่องอีกจนได้!หลัง นางร้ายสาว ''โอ๋ ภัคจีรา วรรณสุทธิ์'' &nbsp;ซึ่งรับหน้าที่เป็น พิธีกร ช่วงครอบครัวข่าวบันเทิง&nbsp;</strong></font></div><div><font size="3"><br></font></div><div><font size="3">ในรายการเรื่องเล่าเช้านี้ ได้เผลอ หัวเราะ ออกมาในการ อ่านข่าว การเสียชีวิต ของ คนในวงการบันเทิง ซึ่ง ภายหลังจาก ช่วงดังกล่าวได้ออกอากาศไป เพียงไม่นานบรรดาชาวอินเตอร์เน็ต ก็ได้ คอมเม้นท์ เกี่ยวกับข่าวดังกล่าว ด้วยข้อความสุดเผ็ดร้อนแทบจะทันที ว่า</font></div><div><br></div><div><font size="3">"<strong><font color="#ff0000">กูเบื่อมึงมากเลยอีโอ๋</font></strong>&nbsp;อ่านข่าวแม่งหัวเราะไปด้วย เต็มบาทรึเปล่า อ่านผิดอ่านถูก ไม่ได้เรื่องเลย อ่านข่าวคนในวงการเสียชีวิตเผลอหัวเราะออกมาได้สองข่าวรวด มึงไม่รู้หรอกว่าคนเค้านั่งฟังมึงอ่านข่าวตอนนี้เค้าวิจารย์มึงว่าไงบ้าง อิค_ าย"</font></div><div><br></div><br></td></tr></tbody></table></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><table width="668" border="0" cellspacing="2" cellpadding="2" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td valign="top" class="A2" style="font-family: ''MS Sans Serif'', CordiaUPC; font-size: 13pt; color: rgb(0, 0, 0); "><table border="0" align="center" cellspacing="2" cellpadding="2" bgcolor="#F5F5F5" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><tbody><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><img src="http://entertain.teenee.com/thaistar/img6/694656.jpg" ''="" align="center" alt=""></td></tr><tr style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><td align="center" style="font-family: Tahoma, CordiaUPC; font-size: 10pt; color: rgb(0, 0, 0); "><font color="#003399"></font></td></tr></tbody></table><font color="#003399"><b></b></font><div><font size="3">ซึ่งหลังจากเป็นประเด็น สาวโอ๋ ก็ได้ออกโรงมาขอโทษ ถึงความผิดพลาดที่เกิดขึ้น ผ่านการโพสต์ในอินสตาร์แกรมส์ ว่า</font></div><div><font size="3" color="#333399"><strong><br></strong></font></div><div><font size="3" color="#333399"><strong>"ขอโทษจริงๆ สำหรับญาติของผู้เสียชีวิตถ้าโอ๋แสดงกริยาแบบที่คนนี้บอก ขอโทษอีกครั้งค่ะพอดีเสียงไม่มี"</strong></font></div><div><font size="3"><br></font></div><div><font size="3">และหลังจากนั้น ในเฟชบุ๊ค แฟนเพจ<strong><font color="#ff0000">&nbsp;"เรื่องเล่าเช้านี้"&nbsp;</font></strong>&nbsp;พิธีกรคนดังอย่าง "สรยุทธ สุทัศนะจินดา" ก็ได้ ออกโรง มาแถลงการณ์พร้อมทั้งให้เหตุผลกับผู้ชมทางบ้านถึงกรณีดังกล่าวว่า....</font></div><div><font size="3"><br></font></div><div><font size="3">"จากการนำเสนอบางข่าวในช่วงครอบครัวบันเทิงวันนี้ ผมกราบขออภัยครอบครัวของผู้ที่เสียชีวิตในข่าว และผู้ชมที่อาจจะมีความรู้สึกกับการนำเสนอข่าวนี้ เหตุการณ์ที่เกิดขึ้นเป็นเหตุฉุกเฉิน เนื่องจากคุณโอ๋ มีอาการโรคประจำตัวกำเริบ&nbsp;<strong><font color="#333399">ซึ่งคุณโอ๋ พยายามประคับประคองสถานการณ์อย่างเต็มที่&nbsp;</font></strong></font></div><div><font size="3"><br></font></div><div><font size="3">ขณะที่ผู้ร่วมดำเนินรายการทุกคนพยายามแก้ไขปัญหาเฉพาะหน้า ผมขอยืนยันว่าคุณโอ๋ ไม่ได้มีเจตนาแสดงท่าทีที่อาจกระทบกระเทือนต่อความรู้สึกของครอบครัวผู้เสียชีวิตแต่อย่างใด หวังว่าผู้ชมจะเข้าใจนะครับ ... สรยุทธ สุทัศนะจินดา"</font></div><div><span style="font-size: medium; "><br></span></div><div><span style="font-size: medium; ">ยังไงก็ ถือซะว่า ความผิดด้วยความไม่ตั้งใจดังกล่าวเป็นบทเรียนที่ดีก็แล้วกันนะจ๊ะ.. ยังไงก็ขอเอาใจช่วยสาวโอ๋ ให้ผ่านเรื่องร้ายๆไปได้ดีด้วยจ้า!! ...&nbsp;</span></div></td></tr></tbody></table></td></tr></tbody></table></span>', '2012821202621-250px.jpg', 1, '2012-08-21 20:26:21', 0);
INSERT INTO `post` VALUES (14, 11, 'อุกอาจ! ยิงรถ ส.ส.ชาดา ลูกชายรับเคราะห์ ดับคาที่', 'ชาดา ไทยเศรษฐ์ ถูกคนร้าย กระหน่ำยิง รถ ส.ส.ชาดา ไทยเศรษฐ์ กระสุนโดน ฟารุต ไทยเศรษฐ์ ลูกชาย ส.ส.ชาดา ไทยเศรษฐ์ เสียชีวิต', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><div align="center"><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/user/rungtip/news/100.jpg"><br><br><br><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/user/rungtip/news/72947-new-942288.jpg"><br><br><br><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/user/rungtip/politics/399202-01.jpg"><br><br><br><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/user/rungtip/politics/100_1.jpg"><br><br><br><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/user/rungtip/politics/151.jpg"><br><br><br><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/user/rungtip/news/399168-02.jpg"><br><br><br><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/user/rungtip/politics/92.jpg"><br><br></div><br><b>อุกอาจ!ยิงรถชาดาสส.ชทพ.กระสุนโดนลูกตายปากช่อง (ไอเอ็นเอ็น)<br></b>ขอขอบคุณภาพประกอบจาก&nbsp;<a target="_blank" href="http://www.krobkruakao.com/%E0%B8%82%E0%B9%88%E0%B8%B2%E0%B8%A7/60558/%E0%B8%94%E0%B8%B1%E0%B8%81%E0%B8%A2%E0%B8%B4%E0%B8%87-%E0%B8%AA-%E0%B8%AA-%E0%B8%AD%E0%B8%B8%E0%B8%97%E0%B8%B1%E0%B8%A2%E0%B8%AF-%E0%B8%9A%E0%B8%B8%E0%B8%95%E0%B8%A3%E0%B8%8A%E0%B8%B2%E0%B8%A2%E0%B8%A3%E0%B8%B1%E0%B8%9A%E0%B9%80%E0%B8%84%E0%B8%A3%E0%B8%B2%E0%B8%B0%E0%B8%AB%E0%B9%8C%E0%B9%81%E0%B8%97%E0%B8%99.html" style="text-decoration: none; ">ครอบครัวข่าว 3</a><b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; คนร้ายกระหน่ำยิงรถ ส.ส.ชาดา ไทยเศรษฐ์ แต่ลูกชายรับเคราะห์ดับคาที่ ตำรวจมึน โยงการเมืองหรือไม่ ขณะที่ ชาดา บอกขอฝังศพลูกชายก่อนให้ปากคำ</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; เมื่อช่วงค่ำวานนี้ (20 สิงหาคม) ระหว่างที่นายชาดา ไทยเศรษฐ์ ส.ส.อุทัยธานี พรรคชาติไทยพัฒนา และประธานกรรมาธิการการเกษตรและสหกรณ์ สภาผู้แทนราษฏร พาครอบครัวเดินทางไปพักผ่อนที่เขาใหญ่ โดยพักที่ชาโต รีสอร์ท ในอ.ปากช่อง จ.นครราชสีมา&nbsp;<span style="color: rgb(128, 0, 0); ">ช่วงเกิดเหตุนายชาดาได้ขับรถออกมากับ นายฟารุต ไทยเศรษฐ์ บุตรชาย เพื่อไปซื้อของในร้านค้า โดยเป็นรถยนต์แลนด์ ครุยเซอร์ สีดำ ป้ายแดง หม่ายเลขทะเบียน อ 5726 กทม.&nbsp;</span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(0, 0, 128); "><b>แต่หลังจากซื้อของเสร็จ นายชาดา ได้ขับรถยนต์อีกคันหนึ่งซึ่งเป็นรถของเพื่อนที่นัดเจอกันเพื่อมุ่งหน้ากลับที่พัก ทำให้นายฟารุต จึงขับรถอีกคันตามหลังมา</b>&nbsp;</span>ระหว่างทาง จู่ๆ ก็มีคนร้ายขับรถยี่ห้อมิตซูบิชิ รุ่นสตาร์ด้า ไม่ติดป้ายทะเบียน พุ่งออกมาจากข้างทาง และพยายามขับแซงรถของนายฟารุต&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; เมื่อนายฟารุตเห็นผิดสังเกต จึงพยายามขับรถกันไม่ให้คนร้ายแซง แต่คนร้ายก็สามารถเร่งเครื่องแซงได้สำเร็จ พร้อมกับที่ชาย 2 คนซึ่งนั่งอยู่ในรถยนต์คันกล่าว ได้ใช้อาวุธปืนกระหน่ำยิงเข้าที่รถของนายฟารุต กระทั่งเจ้าตัวถูกกระสุนทะลุเข้าใต้หู ฝังในกะโหลก ฟุบคาพวงมาลัย จนรถยนต์เสียหลักพุ่งชนเสาไฟฟ้าข้างทาง<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(128, 0, 128); ">ส่วนนายชาดา ซึ่งขับรถนำหน้า เนื่องจากถนนเป็นทางโค้ง เมื่อไม่เห็นไฟรถยนต์ของนายฟารุตตามมา จึงย้อนกลับมาดู และพบรถยนต์ของคนร้ายกำลังขับหนีไปพอดี โดยมือปืนที่นั่งข้างหลัง 2 คน ได้ยิงใส่รถนายชาดา แต่ไม่โดน ก่อนจะหลบหนีไป อย่างไรก็ตามนายชาดาได้รีบนำตัวลูกชายส่งโรงพยาบาล ก่อนที่นายฟารุตจะเสียชีวิตในเวลาต่อมา</span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; และล่าสุด เวลา 07.00 น. ของวันนี้ (21 สิงหาคม) ญาติ ๆได้นำศพนายฟารุตกลับมาประกอบพิธีทางศาสนาอิสลามที่บ้านบริเวณหลังวัดทุ่งแก้ว เขตเทศบาลเมืองอุทัยธานี ท่ามกลางข้าราชการชั้นผู้ใหญ่ กลุ่มการเมืองท้องถิ่น และประชาชนเดินทางมาร่วมพิธีเป็นจำนวนมาก โดยพล.ต.ต.กรเอก เพชรไชยเวช ผบก.ภ.จว.อุทัยธานี ได้นำกำลังตำรวจพร้อมอาวุธครบมือมาคุ้มกัน และไม่อนุญาตให้สื่อมวลชนถ่ายภาพในงาน<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">อย่างไรก็ตาม สำหรับสาเหตุที่นายฟารุตถูกยิงเสียชีวิตครั้งนี้ คาดว่า คนร้ายอาจต้องการลงมือสังหาร นายชาดา โดยไม่ทราบว่า นายชาดาได้เปลี่ยนไปขับรถยนต์อีกคันหนึ่ง&nbsp;</span>ทั้งนี้ นายฟารุตถือเป็นทายาททางการเมืองของนายชาดา โดยมีแผนจะให้ลงเล่นการเมืองสนามใหญ่ในสมัยหน้า และได้เปิดตัวกับประชาชนบ้างแล้ว ขณะที่นายชาดา ถือเป็นนักการเมืองที่มีชื่อเสียงของจังหวัด และมีผู้ให้ความเคารพนับถือเป็นจำนวนมาก ซึ่งหลังเกิดเหตุได้มีการย้ายนายชาดาไปอยู่ในสถานที่ปลอดภัย ท่ามกลางการอารักขาของทีมคุ้มกันของเจ้าหน้าที่ตำรวจ<br><br><br><br><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/logo/inn_logo.jpg">&nbsp;&nbsp;<a target="_blank" href="http://www.krobkruakao.com/%E0%B8%82%E0%B9%88%E0%B8%B2%E0%B8%A7/60558/%E0%B8%94%E0%B8%B1%E0%B8%81%E0%B8%A2%E0%B8%B4%E0%B8%87-%E0%B8%AA-%E0%B8%AA-%E0%B8%AD%E0%B8%B8%E0%B8%97%E0%B8%B1%E0%B8%A2%E0%B8%AF-%E0%B8%9A%E0%B8%B8%E0%B8%95%E0%B8%A3%E0%B8%8A%E0%B8%B2%E0%B8%A2%E0%B8%A3%E0%B8%B1%E0%B8%9A%E0%B9%80%E0%B8%84%E0%B8%A3%E0%B8%B2%E0%B8%B0%E0%B8%AB%E0%B9%8C%E0%B9%81%E0%B8%97%E0%B8%99.html" style="text-decoration: none; "><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/logo/krobkruakao2.gif" style="border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; "></a></span>', '2012821202814-100.jpg', 1, '2012-08-21 20:28:14', 0);
INSERT INTO `post` VALUES (15, 11, 'ตัดพ้อ! น้องเก๋ ประภาวดี ประกาศอำลาทีมชาติถาวร', 'เก๋ ประภาวดี เจริญรัตนธารากูล ประกาศอำลาทีมชาติถาวร โดย น้องงเก๋ ประภาวดี  โพสต์เฟซบุ๊ก ลาออกจากทีมชาติ หลังไม่มีชื่อ เก๋ ประภาวดี เจริญรัตนธารากูล ในการแข่งขันโอลิมปิก เอเชี่ยนเกมส์', '<div style="text-align: center;"><img src="http://hilight.kapook.com/img_cms2/user/pattamaporn/Crime/1321848809.jpg"><span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); ">&nbsp;</span></div><div><span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); ">&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;<b>น้องเก๋ อดีตเหรียญทองโอลิมปิก ประกาศอำลาทีมชาติถาวร หลังไม่มีรายชื่อติดทีมชาติในการแข่งขันเอเชี่ยนเกมส์-โอลิมปิกเกมส์ครั้งต่อไป<br></b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">&nbsp;&nbsp; วานนี้ (20 สิงหาคม) ผู้สื่อข่าวรายงานว่า น้องเก๋ ประภาวดี เจริญรัตนธารากูล อดีตฮีโร่เหรียญทองโอลิมปิก 2008 ที่ประเทศจีน ได้ประกาศผ่านเฟซบุ๊กส่วนตัวว่า&nbsp;<b>ถึงเวลาแล้วที่เธอต้องอำลาวงการยกน้ำหนักไทย ทั้ง ๆ ที่อยากรับใช้ชาติต่อ แต่เมื่อไม่มีโอกาสทั้ง เอเชี่ยนเกมส์ และโอลิมปิกเกมส์ ในการแข่งขันครั้งหน้าที่จะถึง ก็ต้องหันไปทำอย่างอื่นแทน</b><br></span><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ทั้งนี้ สืบเนื่องมาจาก เสธ.ยอด หรือ พล.ต.อินทรัตน์ ยอดบางเตย นายกสมาคมยกน้ำหนักสมัครเล่นแห่งประเทศไทย ได้ประชุมหารือเกี่ยวกับการเตรียมนักกีฬายกน้ำหนักทีมชาติไทย เพื่อให้เข้าร่วมการแข่งขันมหกรรมกีฬารายการสำคัญ ๆ ต่าง ๆ อย่างเช่น เอเชี่ยนเกมส์ 2012 และโอลิมปิกเกมส์ 2016&nbsp;<b>โดยมีรายชื่อนักกีฬายกน้ำหนักรวมทั้งหมด 26 คน แบ่งเป็นชาย 12 คน และหญิง 14 คน และในส่วนของนักกีฬายกน้ำหนักหญิง มีรายชื่อดังต่อไปนี้...</b><br><br><br><b style="background-color: rgb(254, 220, 186); ">รุ่น 48 กิโลกรัม</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - อรอุมา วันฤกษ์<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - สิริวิมล ประมงคล<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - พนิดา คำศรี<br><br><b><span style="background-color: rgb(254, 220, 186); ">&nbsp;รุ่น 53 กิโลกรัม</span></b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - วิภาดา ศิริมงคล<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - โสภิตา ธนสาร<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - กิติมา สุธานันท์<br><br><b style="background-color: rgb(254, 220, 186); ">รุ่น 58 กิโลกรัม</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - พิมศิริ ศิริแก้ว เจ้าของเหรียญเงิน โอลิมปิกเกมส์ 2012 ที่กรุงลอนดอน ประเทศอังกฤษ<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - สุกัญญา ศรีสุราช<br><br><b style="background-color: rgb(254, 220, 186); ">รุ่น 58 - 63 กิโลกรัม</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - กมลชนก อินทมาตย์<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - รัตติกาล กุลน้อย<br><br><b style="background-color: rgb(254, 220, 186); ">&nbsp;รุ่น 63 - 69 กิโลกรัม</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - วิริยา สุวรรณรัตน์<br><br><b style="background-color: rgb(254, 220, 186); ">&nbsp;รุ่น 75 กิโลกรัม</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - กัญญลักษณ์ ชูมณี<br><br><b><span style="background-color: rgb(254, 220, 186); ">รุ่นเกิน 75 กิโลกรัม</span></b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - ชิดชนก พูลทรัพย์สกุล<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - แพรวนภา เคนจันทึก<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(128, 0, 128); ">&nbsp;&nbsp; อย่างไรก็ตาม เมื่อทาง สมาคมยกน้ำหนักสมัครเล่นแห่งประเทศไทย ได้ประกาศรายชื่อนักกีฬาทั้งหมด ก็ไม่พบชื่อของ น้องเก๋ ประภาวดี เจริญรัตนธารากูล อดีตนักกีฬายกเหล็กหมายเลข 1 ของโลก รุ่น 53 กิโลกรัม และเจ้าของเหรียญทอง โอลิมปิกเกมส์&nbsp; 2008 ที่ประเทศจีน แต่อย่างใด จึงทำให้น้องเก๋ออกมาตัดพ้อและประกาศเลิกยกน้ำหนักถาวรผ่านเฟซบุ๊กดังกล่าว</span><br>&nbsp;&nbsp;&nbsp;&nbsp;<br>อ่านรายละเอียดเพิ่มเติมจาก<br><a target="_blank" href="http://www.dailynews.co.th/sports/150532" style="text-decoration: none; "><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/logo/dailynews.jpg" style="border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; "></a></span></div>', '2012821203040-1321848809.jpg', 1, '2012-08-21 20:30:40', 18);
INSERT INTO `post` VALUES (16, 11, 'อุทยานแห่งชาติ ขึ้นค่าเข้า 33 แห่งทั่วไทย เริ่ม ต.ค.นี้', 'อุทยานแห่งชาติ เตรียม ขึ้นค่าธรรมเนียม ค่าเข้า อุทยานแห่งชาติ 33 แห่ง ทั้งทางบกและทางทะเล', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><b>อุทยานแห่งชาติจ่อขึ้นค่าเข้า33แห่งทั่วไทย (ไอเอ็นเอ็น)</b><a target="_blank" href="http://www.oceansmile.com/S/Phangnga/ForestSeaSurin.htm" style="text-decoration: none; "><br></a><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>อุทยานแห่งชาติเตรียมขึ้นค่าธรรมเนียมค่าเข้า 33 แห่ง ทั้งทางบกและทางทะเล โดยที่ ดอยอินทนนท์ ภูกระดึง เขาใหญ่ สิมิลัน และ หมู่เกาะสุรินทร์ ขึ้นค่าตั๋วอีกกว่าเท่าตัว<br></b><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; นายดำรงค์ พิเดช อธิบดีกรมอุทยานแห่งชาติสัตว์ป่าและพันธุ์พืช กระทรวงทรัพยากรธรรมชาติและสิ่งแวดล้อม ได้ลงนามในประกาศกรมอุทยานฯ เรื่องการกำหนดอัตราค่าบริการสำหรับบุคคลเข้าไปในอุทยานฯ จำนวน 33 แห่ง&nbsp;<span style="color: rgb(128, 0, 0); ">โดยกำหนดอัตราค่าบริการสำหรับบุคคลเข้าไปในอุทยานฯ คือ&nbsp;<b>ชาวไทย ผู้ใหญ่ 100 บาท เด็ก 50 บาท ชาวต่างชาติ ผู้ใหญ่ 500 บาท เด็ก 300 บาท โดยให้มีผลตั้งแต่วันที่ 1 ตุลาคม 2555 เป็นต้นไป</b>&nbsp;(อัตราปัจจุบันคือ ชาวไทย ผู้ใหญ่ ราคา 40 บาท เด็ก 20 บาท ผู้สูงอายุ 60 ปีขึ้นไปเข้าฟรี&nbsp; ชาวต่างชาติ ผู้ใหญ่ 400 บาท เด็ก 200 บาท)</span><br><br><br><b style="background-color: rgb(254, 220, 186); ">สำหรับอุทยานฯ จำนวน&nbsp; 33 แห่ง ที่มีการปรับอัตราค่าบริการใหม่ ประกอบด้วย ภาคเหนือ 8 แห่งได้แก่</b><br><blockquote><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;1. อุทยานฯ แจ้ซ้อน จ.ลำปาง<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;2. อุทยานฯ ผ้าห่มปก จ.เชียงใหม่<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;3. อุทยานฯ สุเทพ-ปุย จ.เชียงใหม่<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;4. อุทยานฯ อินทนนท์ จ.เชียงใหม่<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;5. อุทยานฯ ทุ่งแสลงหลวง จ.พิษณุโลก เพชรบูรณ์<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;6. อุทยานฯ น้ำหนาว จ.เพชรบูรณ์ ชัยภูมิ<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;7. อุทยานฯ ภูหินร่องกล้า จ.พิษณุโลก เลย<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;8. อุทยานฯ ห้วยน้ำดัง จ.เชียงใหม่<br></blockquote><br><b style="background-color: rgb(254, 220, 186); ">ภาคตะวันออกเฉียงเหนือ ได้แก่</b><br><blockquote><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;9. อุทยานฯ เขาใหญ่ จ.นครราชสีมา<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;10. อุทยานฯ ผาแต้ม จ.อุบลราชธานี<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;11. อุทยานฯ ภูกระดึง จ.เลย<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;12. อุทยานฯ ภูเรือ จ.เลย<br></blockquote><br><b><span style="background-color: rgb(254, 220, 186); ">ภาคตะวันออก&nbsp; ได้แก่</span></b><br><blockquote><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;13. อุทยานฯ เขาแหลมหญ้า-หมู่เกาะเสม็ด<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;14. อุทยานฯ น้ำตกพลิ้ว จ.จันทบุรี<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;15. อุทยานฯ หมู่เกาะช้างจ.ตราด ภาคตะวันตก<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;16. อุทยานฯ แก่งกระจาน จ.เพชรบุรี<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;17. อุทยานฯ เขื่อนศรีนครินทร์ จ.กาญจนบุรี<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;18. อุทยานฯ ไทรโยค จ.กาญจนบุรี<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;19. อุทยานฯ เอราวัณ จ.กาญจนบุรี&nbsp;<br></blockquote><br><b style="background-color: rgb(254, 220, 186); ">ภาคใต้ ได้แก่</b><br><blockquote><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;20. อุทยานฯ เขาสก จ.สุราษฎร์ธานี<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;21. อุทยานฯ ตะรุเตา จ.สตูล<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;22. อุทยานฯ ธารโบกขรณี จ.กระบี่<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;23. อุทยานฯ หมู่เกาะลันตา จ.กระบี่<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;24. อุทยานฯ หมู่เกาะสิมิลัน จ.พังงา<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;25. อุทยานฯ หมู่เกาะสุรินทร์ จ.พังงา<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;26. อุทยานฯ หมู่เกาะอ่างทอง จ.สุราษฎร์ธานี<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;27. อุทยานฯ หาดเจ้าไหม จ.ตรัง<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;28. อุทยานฯ หาดนพรัตน์ จ.กระบี่<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;29. อุทยานฯ อ่าวพังงา จ.พังงาใน<br></blockquote><br><b style="background-color: rgb(254, 220, 186); ">ส่วนอุทยานฯ 4 แห่งที่จัดตั้งขึ้นใหม่ คิดอัตราค่าบริการสำหรับผู้ใหญ่ 20 บาท เด็ก 10 บาท ชาวต่างชาติ ผู้ใหญ่ 100 บาท เด็ก 50 บาท คือ</b><br><blockquote><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;30. อุทยานฯ ขุนขาน จ.เชียงใหม่<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;31. อุทยานฯ เข้าค้อ จ.เพชรบูรณ์<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;32. อุทยานฯ ดอยภูนาง จ.พะเยา<br><img border="0" alt="" src="http://img.kapook.com/image/icon/ico-8349.gif">&nbsp;33. อุทยานฯ เฉลิมพระเกียรติไทยประจัน จ.ราชบุรี<br></blockquote><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">อย่างไรก็ตาม นายดำรงค์ ระบุว่า ค่าเข้าอุทยานฯ ที่ปรับใหม่นั้น จะดำเนินการเฉพาะในอุทยานฯ ขนาดใหญ่ และอุทยานทางทะเลเท่านั้น แต่หากมีเสียงคัดค้านมามากว่าราคาดังกล่าวสูงเกินไป ก็จะต้องมาพิจารณาถึงเหตุผลกันอีกครั้ง ทั้งนี้ยืนยันว่า<b>&nbsp;เงินที่จ่ายเข้าอุทยานฯ ส่วนหนึ่งก็เพื่อที่จะรักษาไว้ซึ่งทรัพยากรธรรมชาติ ถือเป็นการทำนุบำรุงทรัพยากรไว้วิธีการหนึ่ง</b></span></span>', '2012821203211-S73F0330.jpg', 1, '2012-08-21 20:32:11', 1);
INSERT INTO `post` VALUES (17, 11, 'ถอดบทเรียนวัดพระธรรมกาย สตีฟ จ็อบส์ ตายแล้วไปไหน?', 'ล่าสุด วัดพระธรรมกาย ก็ไม่พลาดที่จะนำเอาเรื่อราวของ Steve Jobs สตีฟ จ็อบส์ มาเสนอเป็นสารคดีให้ศึกษากัน', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img width="421" height="401" alt="สตีฟ จ็อบส์ ตายแล้วไปไหน" class="img-mobile" src="http://hilight.kapook.com/img_cms2/user/thitima/New/szgvd-eszgvwa.jpg" style="width: 421px; height: 401px; "><br></div><div style="text-align: center; "><br></div><div><div><a target="_blank" href="http://www.kapook.com/" style="text-decoration: none; ">เรียบเรียงข้อมูลโดยกระปุกดอทคอม</a></div><div>ขอขอบคุณภาพประกอบจาก &nbsp;<a target="_blank" href="http://www.macthai.com/2012/08/20/%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9F-%E0%B8%88%E0%B9%87%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99/" style="text-decoration: none; ">macthai.com</a>&nbsp;</div></div><div><br></div><div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">แม้ว่า<b>&nbsp;"สตีฟ จ็อบส์"&nbsp;</b>อดีตซีอีโอของค่ายแอปเปิ้ลจะเสียชีวิตไปเกือบ 1 ปีแล้ว แต่ทุกวันนี้ คนทั่วโลกก็ยังเอ่ยถึงชื่อของศาสดาแห่งโลกนวัตกรรมคนนี้อยู่ และยังนำเรื่องราวชีวิตของเขามาเป็นบทเรียนสอนคนรุ่นหลังในหลาย ๆ เรื่อง อย่างที่เราเห็นหนังสือมากมายหลายเล่มที่เกี่ยวกับ<b>&nbsp;"สตีฟ จ็อบส์"</b>เปิดตัวและวางขายอยู่ทั่วไป</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(128, 0, 0); ">&nbsp;เช่นเดียวกับการบรรยายธรรมของวัดพระธรรมกาย ที่ถ่ายทอดผ่านทางเว็บไซต์ DMC.TV เมื่อวันที่ 16 สิงหาคมที่ผ่านมา ก็ได้มีการหยิบยกชีวิตของ<b>&nbsp;"สตีฟ จ็อบส์"</b>&nbsp;ขึ้นมาบรรยายเป็นสารคดีบทเรียนกฎแห่งกรรมให้ผู้ที่สนใจธรรมะได้ศึกษา หลังจาก&nbsp;<b>"โทนี่ ซวง"&nbsp;</b>วิศวกรอาวุโสของบริษัทแอปเปิ้ล ในรัฐแคลิฟอร์เนีย ประเทศสหรัฐอเมริกา ได้เขียนจดหมายมาขอให้ท่านพระเทพญาณมหามุนี (ไชยบูลย์ ธมฺมชโย) เจ้าอาวาสวัดพระธรรมกาย ใช้ญาณตรวจสอบว่า<b>&nbsp;"สตีฟ จ็อบส์ ตายแล้วไปไหน ตอนนี้ตัวเขาเป็นอย่างไรบ้าง?"</b>&nbsp;เพราะ&nbsp;<b>"สตีฟ จ็อบส์"&nbsp;</b>ก็เป็นคนหนึ่งที่นับถือศาสนาพุทธจนถึงวาระสุดท้ายของชีวิต และเคยคิดจะออกบวชเป็นพระภิกษุในพระพุทธศาสนาด้วย</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ทั้งนี้ พระคุณเจ้าได้ตอบคำถามเรื่องนี้ในเชิงศาสนา พร้อมกับนำภาพ 3D ขึ้นมาประกอบการบรรยายธรรมะ ในรายการโรงเรียนอนุบาลฝันในฝันวิทยา โดยอธิบายก่อนว่า&nbsp;<b>ชีวิตหลังความตายมีอยู่จริง ไม่ว่าจะเป็นเชื้อชาติ ศาสนา หรือเผ่าพันธุ์ใด เมื่อจากโลกนี้ไปแล้วก็จะได้บังเกิดใหม่ในภพภูมิต่าง ๆ อาจจะอยู่ในภพที่ดี (สุคติ) หรือภพที่ไม่ดี (ทุคติ) แต่หมายความว่า ตายแล้วไม่สูญ&nbsp;</b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; อย่างไรก็ตาม การจะได้ไปอยู่ภพไหนหลังความตายนั้น ขึ้นอยู่กับว่า&nbsp;<span style="color: rgb(0, 0, 128); ">ตัวเองทำตัวอย่างไรไว้ก่อนจะเสียชีวิต หากมีจิตใจที่ผ่องใส ก็จะได้ไปอยู่ในสุคติภูมิ&nbsp;<b>แต่หากมีจิตใจเศร้าหมองก็ต้องไปบังเกิดอยู่ในทุคติภูมิ ความผ่องใส หรือเศร้าหมองขึ้นอยู่กับกำลังของบุญและบาปที่บุคคลนั้นกระทำเอาไว้</b></span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; นอกจากนี้ ในภพสุคติและทุคติก็ยังแบ่งเป็นชั้น ๆ หากเป็นสุคติภูมิก็จะแบ่งตามความผ่องใสของใจว่า<b>&nbsp;มีความผ่องใสมากหรือน้อย ถ้าทำทั้งบุญและบาปเอาไว้ในสมัยที่ยังมีชีวิตอยู่ ก็จะได้ไปบังเกิดในภพภูมิที่มีความสุขสบายในระดับเบื้องต้น&nbsp;</b>แล้วไล่ขึ้นไปจนถึงสวรรค์ชั้นต่าง ๆ ส่วนทุคติภูมิ ผู้ที่ตกในภพภูมินี้จะอยู่ในความมืดมิด ทรมานแสนสาหัสอยู่ในภพภูมิของสัตว์นรก เปรต อสูรกาย เป็นต้น</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; มาถึงเรื่อง&nbsp;<b>"สตีฟ จ็อบส์"</b>&nbsp;ที่มีผู้ถามมาบ้าง....พระคุณเจ้า ได้อธิบายว่า ก่อนที่&nbsp;<b>"สตีฟ จ็อบส์"</b>จะเสียชีวิต เขายังเป็นห่วงครอบครัว ยังห่วงแผนงานต่าง ๆ ที่คิดเอาไว้มากมาย แต่ยังไม่ได้ทำ ซ้ำยังห่วงอนาคตของบริษัทที่ตัวเองทุ่มเทแรงกายแรงใจสร้างมาจนยิ่งใหญ่อย่างทุกวันนี้ สิ่งที่เขาคิดเหล่านี้ผุดเป็นภาพที่ทำให้เขาไม่สบายใจ และทำให้จิตใจของเขาเศร้าหมองไปด้วย</div><div><br></div><div style="text-align: center; "><img width="552" height="414" alt="ถอดบทเรียนวัดพระธรรมกาย สตีฟ จ็อบส์ ตายแล้วไปไหน?" class="img-mobile" src="http://hilight.kapook.com/img_cms2/user/thitima/New/where-is-steve-jobs_06.JPG" style="width: 552px; height: 414px; "><br></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; และที่สำคัญมาก ๆ คุณสตีฟ จ็อบส์ เองก็รู้สึกเป็นกังวลลึก ๆ ว่า&nbsp;<span style="color: rgb(128, 0, 0); ">"เ<b>มื่อตัวเขาตายไปแล้ว ชีวิตหลังความตายของเขาจะเป็นอย่างไร ตัวเขาจะได้ไปอยู่ที่ไหน และที่แห่งนั้นจะเป็นอย่างไร</b>หรือว่าจะเป็นอย่างที่อาจารย์ของเขา ซึ่งเป็นชาวญี่ปุ่นและเป็นนักบวชในพระพุทธศาสนา ได้เคยสอนเอาไว้หรือเปล่า หรือว่าจะเป็นอย่างอื่น เรียกได้ว่า&nbsp;<b>ทั้งความไม่แน่ใจ, ความวิตกกังวล, และความผูกพันในเรื่องคน งาน เงิน &nbsp;และโปรเจกต์ต่าง ๆ มันได้กลายเป็นความคิดที่วนเวียนอยู่ในใจของคุณสตีฟ จ็อบส์ ณ ช่วงเวลานั้นอยู่ตลอดเวลา</b>"</span>&nbsp;เจ้าอาวาสวัดพระธรรมกาย อธิบาย</div><div><br></div><div style="text-align: center; "><img width="552" height="414" alt="สตีฟ จ็อบส์ ตายแล้วไปไหน" class="img-mobile" src="http://hilight.kapook.com/img_cms2/user/thitima/New/where-is-steve-jobs_09.jpg" style="width: 552px; height: 414px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เมื่อเป็นเช่นนี้ แสดงว่า<span style="color: rgb(0, 0, 128); ">&nbsp;ก่อน<b>&nbsp;"สตีฟ จ็อบส์"&nbsp;</b>จะจากโลกนี้ไป ได้เกิดภาพความทรงจำต่าง ๆ มากมายขึ้นมาในใจ ทั้งสุข ทุกข์ ปลื้มใจ ไม่ปลื้มใจ สลับกันไป ซึ่งการที่ภาพความทรงจำที่ทำให้จิตใจผ่องใส และเศร้าหมองปนเปกันไป มาส่งผลรวมกับนิสัยพื้นฐานของ&nbsp;<b>"สตีฟ จ็อบส์"</b>&nbsp;ที่เป็นคนมีความรู้ความสามารถทางด้านวิทยาศาสตร์ และมีสุนทรียภาพทางศิลปะสูงมาก จึงส่งผลให้&nbsp;<b>"สตีฟ จ็อบส์"</b>&nbsp;ได้ไปบังเกิดใหม่เป็น<b>&nbsp;"เทพบุตรภุมมะเทวา ระดับกลางสายวิทยาธรกึ่งยักษ์"&nbsp;</b></span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; มาถึงตรงนี้ หลายคนอาจจะสงสัยว่า&nbsp;<b>"ภุมมะเทวาสายวิทยาธรกึ่งยักษ์"</b>&nbsp;มีลักษณะเป็นเช่นไร เจ้าอาวาสวัดพระธรรมกายได้ให้ความรู้ว่า เป็นภุมมะเทวาที่มีอัธยาศัย 2 อย่างมาผสมผสานกัน คือ อัธยาศัยของวิทยาธรที่รักในการเรียนรู้ศาสตร์ และความรู้ต่าง ๆ กับ อัธยาศัยของยักษ์ที่มักโกรธ ขี้โมโห และหงุดหงิดง่าย จึงทำให้ภุมมะเทวาสายนี้มีลักษณะเป็นวิทยาธรกึ่งยักษ์</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; รูปลักษณ์ของ<b>&nbsp;"ภุมมะเทวาสายวิทยาธรกึ่งยักษ์"</b>&nbsp;ของ&nbsp;<b>"สตีฟ จ็อบส์"&nbsp;</b>นั้น จะมีลักษณะกายเหมือนเทพบุตรภุมมะเทวาระดับกลาง แต่จะมีผิวสีดำอมแดงและหยาบกว่าเทพบุตรภุมมะเทวาระดับกลางรูปอื่น ๆ เพราะเป็นกึ่งยักษ์ เมื่อโกรธ จะมีเขี้ยวงอกออกมา ซึ่งเขี้ยวก็ยังมีรายละเอียดอีกมากมาย ไม่เหมือนกับเขี้ยวของแวมไพร์ หรือแดร็กคิวล่า ที่เห็นกันในภาพยนตร์</div><div><br></div><div style="text-align: center; "><img width="552" height="414" alt="ถอดบทเรียนวัดพระธรรมกาย สตีฟ จ็อบส์ ตายแล้วไปไหน?" class="img-mobile" src="http://hilight.kapook.com/img_cms2/user/thitima/New/where-is-steve-jobs_13.JPG" style="width: 552px; height: 414px; "><br></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เทพบุตรองค์นี้จะอยู่อาศัยในวิมานขนาดปานกลาง เป็นโลหะสีเงิน สีขาว และแก้วผลึกขนาดใหญ่ที่มีขอบเขตกว้างขวาง สูงประมาณตึก 6 ชั้น อยู่อาศัยซ้อนอยู่บนโลกมนุษย์ ใกล้ ๆ กับที่ทำงานในสมัยที่ยังเป็นมนุษย์ และรอบ ๆ บริเวณ ก็ยังมีวิมานของเหล่าภุมมะเทวาที่มีความชอบคล้าย ๆ กันอาศัยอยู่ด้วย&nbsp;<b>ขณะเดียวกัน เทพบุตรก็ยังมีบริวารรับใช้อีก 20 ตน ซึ่งเป็นผลแห่งบุญมาจากการที่เขาได้บริจาคสิ่งของ เงินทอง และให้ความรู้แก่คนอื่นในสังคมนั่นเอง</b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ส่วนชีวิตเทพบุตรของ<b>&nbsp;"สตีฟ จ็อบส์"</b>&nbsp;นั้น พระคุณเจ้า อธิบายว่า&nbsp;<span style="color: rgb(128, 0, 0); ">หลังจากศาสดาโลกไอทีเสียชีวิตไปแล้ว เขาได้ลืมตาตื่นขึ้นมาบนเตียงที่ตั้งอยู่กลางวิมาน และรู้สึกตื่นตา ตื่นใจ กับสิ่งที่อยู่รอบตัวเป็นอย่างมาก พร้อมกับแสดงความอัศจรรย์ใจที่เขาหายจากโรคมะเร็งเป็นปลิดทิ้ง แถมยังดูเป็นหนุ่มหล่อกว่าเดิมอีกต่างหาก จากนั้น ก็ได้มีหัวหน้าเหล่าบริวารมาต้อนรับเขา พร้อมกับแนะนำสังคมใหม่ให้เขาได้รู้จัก น<b>อกจากนี้ เขายังได้ใช้ชีวิตอย่างสุขสบายในวิมานไฮเทคที่ทุกสิ่งทุกอย่างแทบจะทำได้โดยอัตโนมัติ เพียงแค่คิด</b></span></div><div><br></div><div style="text-align: center; "><img width="552" height="414" alt="สตีฟ จ็อบส์ ตายแล้วไปไหน" class="img-mobile" src="http://hilight.kapook.com/img_cms2/user/thitima/New/where-is-steve-jobs_11.jpg" style="width: 552px; height: 414px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ท่านพระเทพญาณมหามุนี อธิบายต่ออีกว่า เทพบุตร&nbsp;<b>"สตีฟ จ็อบส์"</b>&nbsp;ได้รับเชิญให้ไปประชุมหมู่บ้าน ซึ่งหัวหน้าหมู่บ้านภุมมะเทวา ก็ได้แนะนำกฎระเบียบต่าง ๆ ให้เขาได้ปฏิบัติ สิ่งเหล่านี้ทำให้<b>"สตีฟ จ็อบส์"&nbsp;</b>ตื่นเต้นเป็นอย่างมาก ตลอดเวลาที่เขาอยู่ในวิมานแห่งนั้น มีเพื่อนบ้านแวะเวียนมาพบปะพูดคุยถึงความสามารถของเขา ที่ได้สร้างสรรค์นวัตกรรมใหม่ ๆ ให้เกิดขึ้นในเมืองมนุษย์&nbsp;<b>นั่นก็เพราะภุมมะเทวาที่มาอยู่ร่วมกันในที่แห่งนี้ ล้วนแล้วแต่เคยสร้างสรรค์ผลงานดี ๆ เอาไว้สมัยที่ยังมีชีวิตอยู่เช่นกัน จึงได้ติดตามผลงานนวัตกรรมอยู่เรื่อย ๆ&nbsp;</b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ทั้งนี้ ในช่วง 10 เดือนที่ผ่านมา<b>&nbsp;"สตีฟ จ็อบส์"&nbsp;</b>ไม่รู้สึกโกรธเลย เพราะได้อยู่ในสังคมที่ตอบสนองความชอบของเขาทั้งหมด ดังนั้น จึงไม่มีเขี้ยวที่จะงอกต่อเมื่อมีอารมณ์โกรธปรากฏให้เห็น</div><div><br></div><div>&nbsp; &nbsp; &nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">&nbsp; &nbsp; "ในระหว่างที่ท่านเทพบุตรสตีฟ จ็อบส์ กำลังเพลิดเพลินกับการใช้ชีวิตอยู่ในสังคมภุมมะเทวาแห่งนั้น ก็ได้มีกระแสธารแห่งบุญจากที่แห่งหนึ่ง ไปเชื่อมจรดที่ศูนย์กลางกายของท่านเทพบุตรใหม่สตีฟ จ็อบส์ ซึ่งทันทีที่กระแสบุญดังกล่าวได้ไปจรดเชื่อมที่ศูนย์กลางกายของเขา ก็เป็นผลทำให้ใจของเขาบังเกิดความสว่างไสวขึ้นมาในทันที แล้วภาพของแหล่งกำเนิดแสงสว่าง ที่มาจากคนกลุ่มหนึ่งที่ได้สร้างองค์พระธรรมกายประจำตัวให้กับตัวเขา และก็นึกถึงเขาก็ได้ไปปรากฏฉายขึ้นภายในใจของเขา"</span>พระคุณเจ้า กล่าว</div><div><br></div><div style="text-align: center; "><img width="552" height="414" alt="ถอดบทเรียนวัดพระธรรมกาย สตีฟ จ็อบส์ ตายแล้วไปไหน?" class="img-mobile" src="http://hilight.kapook.com/img_cms2/user/thitima/New/where-is-steve-jobs_22.JPG" style="width: 552px; height: 414px; "><br></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ในตอนท้าย เจ้าอาวาสวัดพระธรรมกาย ระบุด้วยว่า แม้ว่า&nbsp;<b>"สตีฟ จ็อบส์"</b>&nbsp;จะได้เห็นภาพของการทำบุญดังกล่าวแล้ว แต่เขายังไม่ทราบว่าจะอนุโมทนาอย่างไร สิ่งที่เขารับรู้ได้ก็คือ เขารู้สึกดีใจ ชอบใจ และสุขใจมาก</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; อย่างไรก็ตาม หลังจากทางสถานีโทรทัศน์ DMC ได้นำเสนอเรื่องนี้ไป ก็ได้มีผู้ตั้งข้อสงสัยจำนวนหนึ่ง ทางสถานีโทรทัศน์ DMC จึงได้ชี้แจงไว้ในเว็บไซต์&nbsp;<a target="_blank" href="http://www.dmc.tv/pages/%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9F-%E0%B8%88%E0%B9%87%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-Steve-Jobs-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99/20120816-%E0%B8%9B%E0%B8%A3%E0%B9%82%E0%B8%A5%E0%B8%81%E0%B8%99%E0%B8%B4%E0%B8%A7%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%AD%E0%B8%99-%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9B-%E0%B8%88%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99-%E0%B8%95%E0%B8%AD%E0%B8%99%E0%B8%97%E0%B8%B5%E0%B9%88-1_LEFT.html" style="text-decoration: none; ">dmc.tv</a>&nbsp;มีเนื้อหาดังนี้</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>"ชี้แจงเรื่อง การนำเสนอเนื้อหา สตีฟ จ็อบส์ ตายแล้วไปไหน ของเว็บไซต์ต่าง ๆ</b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(128, 0, 0); ">&nbsp;เนื่องจากทางสถานีโทรทัศน์ DMC ได้มีการนำเสนอเนื้อหาเรื่อง Where is Steve Jobs และได้มีเว็บไซต์ต่าง ๆ สรุปเนื้อหา Where is Steve Jobs ไปไว้ในเว็บไซต์ของตน ซึ่งเป็นการสรุปเนื้อหาดังกล่าวนั้น<b>&nbsp;เป็นการสรุปโดยความเห็นส่วนตัว ซึ่งอาจมีการเพิ่มตัดแต่งประเด็น หรือเสริมความเห็นส่วนตัวเข้าไป ทำให้เนื้อหาในบางเว็บไซต์ผิดพลาดและไม่สมบูรณ์&nbsp;</b></span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; จึงขอให้ผู้อ่านใช้วิจารณญาณและเทียบเคียงกับต้นฉบับจากทาง&nbsp;<a target="_blank" href="http://www.dmc.tv/pages/%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9F-%E0%B8%88%E0%B9%87%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-Steve-Jobs-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99/20120816-%E0%B8%9B%E0%B8%A3%E0%B9%82%E0%B8%A5%E0%B8%81%E0%B8%99%E0%B8%B4%E0%B8%A7%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%AD%E0%B8%99-%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9B-%E0%B8%88%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99-%E0%B8%95%E0%B8%AD%E0%B8%99%E0%B8%97%E0%B8%B5%E0%B9%88-1_LEFT.html" style="text-decoration: none; ">dmc.tv</a>&nbsp;ทั้งนี้ ทางเว็บ&nbsp;<a target="_blank" href="http://www.dmc.tv/pages/%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9F-%E0%B8%88%E0%B9%87%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-Steve-Jobs-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99/20120816-%E0%B8%9B%E0%B8%A3%E0%B9%82%E0%B8%A5%E0%B8%81%E0%B8%99%E0%B8%B4%E0%B8%A7%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%AD%E0%B8%99-%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9B-%E0%B8%88%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99-%E0%B8%95%E0%B8%AD%E0%B8%99%E0%B8%97%E0%B8%B5%E0%B9%88-1_LEFT.html" style="text-decoration: none; ">dmc.tv</a>ได้นำเสนอเนื้อหาต้นฉบับที่ถูกต้องไว้แล้ว ผู้อ่านสามารถอ้างอิงได้จากลิงค์ บทความนี้&nbsp;</div><div><span style="color: rgb(0, 0, 128); "><br></span></div><div><span style="color: rgb(0, 0, 128); ">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>เรื่องราว Where is Steve Jobs เป็นความรู้ที่ได้จากการปฏิบัติสมาธิ (Meditation) ในพระพุทธศาสนา วิชชาธรรมกาย ซึ่งเมื่อฝึกปฏิบัติจนเชี่ยวชาญชำนาญแล้ว ผู้ปฏิบัติจะสามารถรู้เห็นเรื่องราวกฎแห่งกรรม, การเวียนว่ายตายเกิดของสรรพสัตว์ทั้งหลาย&nbsp;</b>ซึ่งได้นำมาถ่ายทอดให้เข้าใจง่าย ๆ ในรายการโรงเรียนอนุบาลฝันในฝันวิทยา โดยพระเทพญาณมหามุนี (เจ้าอาวาสวัดพระธรรมกาย) เพื่อให้เข้าใจถึงเรื่องราวความจริงของชีวิตว่า เกิดมาทำไม ตายแล้วไปไหน อะไรเป็นเป้าหมายของชีวิต นำไปสู่ผลการปฏิบัติของผู้ฟัง ทำให้ผู้ฟังเกิดความเกรงกลัวต่อบาป รักในการทำบุญกุศล รักการปฏิบัติธรรมสืบไป</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<b>&nbsp;เรื่องราว Where is Steve Jobs นี้เป็นเพียงทรรศนะหนึ่งเท่านั้น ผู้ฟังมีสิทธิ์ที่จะเชื่อหรือไม่ก็ได้"&nbsp;</b>&nbsp;</div><div>&nbsp;</div><div><div style="text-align: center; "><br><iframe width="560" height="420" frameborder="0" allowfullscreen="" src="http://www.youtube.com/embed/SlrSLlhsmV8"></iframe></div><div style="text-align: center; "><br></div><div style="text-align: center; "><a target="_blank" href="http://www.youtube.com/watch?v=SlrSLlhsmV8" style="text-decoration: none; ">คลิป Case Study สตีฟ จ็อบส์ ตอนที่ 1-1 โพสต์โดยคุณ dmcupdate สมาชิกเว็บไซต์ยูทูบ</a></div><div style="text-align: center; "><br></div><div style="text-align: center; "><br><iframe width="560" height="420" frameborder="0" allowfullscreen="" src="http://www.youtube.com/embed/abMQPXSng2E"></iframe></div><div style="text-align: center; "><br></div><div style="text-align: center; "><a target="_blank" href="http://www.youtube.com/watch?v=abMQPXSng2E" style="text-decoration: none; ">คลิป Case Study สตีฟ จ็อบส์ ตอนที่ 1-2 โพสต์โดยคุณ dmcupdate สมาชิกเว็บไซต์ยูทูบ</a></div></div><div><br></div><div><br></div><div>อ่านรายละเอียดเพิ่มเติมจาก</div><div><a target="_blank" href="http://www.dmc.tv/pages/%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9F-%E0%B8%88%E0%B9%87%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-Steve-Jobs-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99/20120816-%E0%B8%9B%E0%B8%A3%E0%B9%82%E0%B8%A5%E0%B8%81%E0%B8%99%E0%B8%B4%E0%B8%A7%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%AD%E0%B8%99-%E0%B8%AA%E0%B8%95%E0%B8%B5%E0%B8%9B-%E0%B8%88%E0%B8%AD%E0%B8%9A%E0%B8%AA%E0%B9%8C-%E0%B8%95%E0%B8%B2%E0%B8%A2%E0%B9%81%E0%B8%A5%E0%B9%89%E0%B8%A7%E0%B9%84%E0%B8%9B%E0%B9%84%E0%B8%AB%E0%B8%99-%E0%B8%95%E0%B8%AD%E0%B8%99%E0%B8%97%E0%B8%B5%E0%B9%88-1_LEFT.html" style="text-decoration: none; ">dmc.tv</a></div></div></span>', '2012821203400-szgvd-eszgvwa.jpg', 1, '2012-08-21 20:34:00', 0);
INSERT INTO `post` VALUES (18, 11, 'เสนอร่างกฎหมาย ห้ามขายเหล้าปั่นทุกพื้นที่!', 'เสนอร่างกฎหมาย อีก 4 ฉบับ ห้ามขายเหล้าปั่นทุกพื้นที่ และห้ามขายเครื่องดื่มแอลกอฮอล์ริมทางเท้า', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><div style="text-align: center;"><span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><img src="http://hilight.kapook.com/img_cms2/user/fontip/etc/shutterstock_80363782.jpg"></span></div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>เสนอร่างกฎหมาย อีก 4 ฉบับ ห้ามขายเหล้าปั่นทุกพื้นที่ และห้ามขายเครื่องดื่มแอลกอฮอล์ริมทางเท้า ย้ำประกาศเวลาขายชัดเจน ทั้งมีใบอนุญาต และไม่มีใบอนุญาต เอาผิดเหมือนกันหมด</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; วานนี้ (20 สิงหาคม) นพ.สมาน ฟูตระกูล ผู้อำนวยการสำนักงานคณะกรรมการควบคุมเครื่องดื่มแอลกอฮอล์ กรมควบคุมโรค เปิดเผยว่า หลังจากที่มีการบังคับใช้ประกาศ พ.ร.บ ควบคุมเครื่องดื่มแอลกอฮอล์ พ.ศ. 2551 เรื่องการห้ามดื่มเครื่องดื่มแอลกอฮอล์ในทางสาธารณะขณะขับขี่ หรือขณะโดยสารอยู่ในรถ หรือบนรถทุกประเภท ซึ่งมีผลบังคับใช้ตั้งแต่วันที่ 8 สิงหาคม เป็นต้นมา หากใครฝ่าฝืนต้องระวางโทษจำคุกไม่เกิน 6 เดือน และปรับไม่เกิน 1 หมื่นบาท หรือทั้งจำทั้งปรับ ทั้งนี้ จาก พ.ร.บ.ดังกล่าว ทางสำนักงานฯ ได้เตรียมเสนอร่างประกาศอีก 4 ฉบับ เพื่อเสนอคณะกรรมการควบคุมเครื่องดื่มแอลกอฮอล์ในการประชุมเดือนกันยายนนี้ ดังนี้<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b><span style="color: rgb(0, 0, 128); ">1. ร่างประกาศห้ามขายเหล้าปั่นทุกพื้นที่<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2. ร่างประกาศห้ามขายเครื่องดื่มแอลกอฮอล์บริเวณรอบสถานศึกษา 300 เมตร<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3. ร่างประกาศกำหนดเวลาห้ามขายเครื่องดื่มแอลกอฮอล์ทุกประเภท รวมทั้งร้านที่มีใบอนุญาตและไม่มีใบอนุญาต<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4. ร่างประกาศห้ามขายห้ามดื่มเครื่องดื่มแอลกอฮอล์ในทางสาธารณะเพื่อการจราจรทางบก ได้แก่ ถนนสาธารณะ ไหล่ทาง ทางเท้า</span></b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; นพ.สมาน กล่าวต่อว่า สำหรับร่างประกาศทั้ง 4 ฉบับ ทางคณะกรรมการควบคุมเครื่องดื่มแอลกอฮอล์เคยตีกลับ เพื่อให้ไปสำรวจความคิดเห็นประชาชนก่อน โดยล่าสุดตนได้รวบรวมผลสำรวจ และพบว่าร้อยละ 80 ในกลุ่มประชาชน 5,000 ตัวอย่าง เห็นด้วยกับร่างนี้<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(128, 0, 0); ">ผอ.สำนักงานควบคุมเครื่องดื่มแอลกอฮอล์ กล่าวต่อว่า ส่วนร่างประกาศกำหนดเวลาห้ามขายสุรา&nbsp;<b>ซึ่งปัจจุบันได้ใช้กฎหมายตามประกาศคณะปฏิวัติ ฉบับ 2553 กำหนดให้ผู้ได้รับอนุญาตขายสุราได้ 2 ช่วง คือ 11.00 - 14.00 น. และ 17.00 - 24.00 น.&nbsp;</b>หากฝ่าฝืนมีโทษจำคุก 2 ปี ปรับ 4,000 บาท หรือทั้งจำทั้งปรับ แต่ทั้งนี้ ปัญหาที่เกิดขึ้นก็คือ ไม่สามารถเอาผิดกับพวกขายเหล้าเถื่อนได้ จะเอาผิดได้เฉพาะไม่มีใบอนุญาตขายเท่านั้น โดยมีโทษปรับสำหรับสุราไทย 500 บาท&nbsp; และสุราต่างประเทศ 2,000 บาท&nbsp;</span><br><br><br><br>อ่านรายละเอียดเพิ่มเติมจาก&nbsp;<br><a target="_blank" href="http://www.khaosod.co.th/view_newsonline.php?newsid=TVRNME5UUTFOelF4T0E9PQ==&amp;catid=01" style="text-decoration: none; "><img border="0" alt="" src="http://hilight.kapook.com/img_cms2/logo/khaosod.jpg" style="border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; "></a></span>', '2012821203607-shutterstock_80363782.jpg', 1, '2012-08-21 20:36:07', 1);
INSERT INTO `post` VALUES (19, 10, 'สถานที่ท่องเที่ยว 7 ประเทศน่าเที่ยวในเอเชีย ที่ไม่ควรพลาด', 'เชื่อเถอะว่าประเทศในแถบเอเชียมีสถานที่ท่องเที่ยวดี ๆ มากมายให้คุณเลือก', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><img width="601" height="601" border="0" alt="สถานที่ท่องเที่ยว" title="สถานที่ท่องเที่ยว" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/tt.jpg" style="width: 601px; height: 601px; "></span><div><span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><span style="color: rgb(0, 0, 204); ">คุณไม่จำเป็นต้องนั่งเครื่องบินไกล ๆ ไปเที่ยวฝั่งยุโรป เพื่อให้ได้วันหยุดสุดแสนประทับใจ เชื่อเถอะว่าประเทศในแถบเอเชียก็มีสถานที่น่าเที่ยวดี ๆ มากมายให้คุณเลือกเที่ยวได้ทุกรูปแบบไม่แพ้กันเลย แถมยังมีให้เลือกหลากหลายเหมาะกับทุกไลฟ์สไตล์อีกด้วย&nbsp;<b>ดังนั้น กระปุกท่องเที่ยวจึงได้รวบรวม 7 ประเทศ น่าสนใจจากทั่วเอเชียมาฝาก ให้คนที่รักการท่องเที่ยวได้เข้ามาลองอ่านกันดู ส่วนจะมีอะไรบ้างนั้น ก็ลองไปดูกันเลยค่ะ</b></span><br><br><div align="center"><img width="601" height="801" border="0" alt="สิงคโปร์" title="สิงคโปร์" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/u3.jpg" style="width: 601px; height: 801px; "><br></div><br><img width="30" height="33" border="0" alt="" src="http://img.kapook.com/image/icon/96631_1.gif" style="width: 30px; height: 33px; "><b style="background-color: rgb(255, 161, 180); ">1. ประเทศสิงคโปร์</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ถ้าคุณเป็นคนรักความสะอาดและชอบวิวในเมืองสวย ๆ ล่ะก็ สิงคโปร์ก็คงจะเป็นประเทศน่าเที่ยวสำหรับคุณมากทีเดียว เพราะความสะอาดของที่นี่เรียกว่าไม่มีที่ติเลย โดยคุณจะไม่เห็นเศษขยะทิ้งเกะกะตามข้างถนนเลยแม้แต่ชิ้นเดียว แถมปัญหาเรื่องรถติดยังเป็นเรื่องไกลตัวของที่นี่อีกด้วย จึงเหมาะกับคนที่ชอบความมีระเบียบวินัยมาก ยิ่งไปกว่านั้น วิวแสงสีจากสิ่งก่อสร้างสวย ๆ ในช่วงกลางคืน รวมไปถึงแหล่งช้อปปิ้งและอาหารของที่นี่ยังขึ้นชื่อมากอีกด้วยนะ<br><br><div align="center"><img width="600" height="374" border="0" alt="มองโกเลีย" title="มองโกเลีย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/f_1.jpg" style="width: 600px; height: 374px; "><br></div><br><img width="30" height="33" border="0" alt="" src="http://img.kapook.com/image/icon/96631_1.gif" style="width: 30px; height: 33px; "><b style="background-color: rgb(179, 255, 184); ">2. ประเทศมองโกเลีย</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ประเทศมองโกเลีย ดินแดนแห่งตำนานที่มีเรื่องราวเล่าขานมาช้านาน อีกทั้งยังเป็นประเทศที่มีวิถีชีวิตและวัฒนธรรม รวมถึงธรรมชาติที่งดงาม โดยเฉพาะความสวยของอุทยานแห่งชาติ Altai Tavan Bogd ที่เรียกได้ว่าสวยไม่แพ้ที่ไหน ๆ เลย โดยอุทยานแห่งนี้ไม่จำเป็นต้องตกแต่งใด ๆ เพิ่ม ก็สวยงามด้วยวิวแม่น้ำและภูเขาตามธรรมชาติอยู่แล้ว จึงทำให้เป็นแหล่งพักผ่อนชมวิวสุดโปรดของนักท่องเที่ยวมากมาย นอกจากนี้ ยังเป็นที่ซึ่งผู้คนนิยมมาพายเรือคายัคและขี่ม้าแข่งด้วยเช่นกัน<br><br><div align="center"><div align="center"><img width="601" height="601" border="0" alt="ประเทศไทย" title="ประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/k.jpg" style="width: 601px; height: 601px; "><br></div></div><br><img width="30" height="33" border="0" alt="" src="http://img.kapook.com/image/icon/96631_1.gif" style="width: 30px; height: 33px; "><b style="background-color: rgb(251, 255, 122); ">3. ประเทศไทย</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; แน่นอนว่าแหล่งท่องเที่ยวที่มีครบทุกอย่างแบบเมืองไทย จะต้องอยู่ในลิสต์ของเราอย่างแน่นอน ซึ่งเมืองไทยมีทั้งอาหารหลากหลายเมนูที่โด่งดังไปทั่วโลก ไม่ว่าจะเป็น ผัดไทย ต้มยำกุ้ง หรือต้มข่าไก่ ฯลฯ วัดเก่าแก่สวยงาม แหล่งช้อปปิ้งหลากสไตล์ ไปจนถึงทิวทรรศน์งดงามตามธรรมชาติ แต่ไฮไลท์ที่ชาวต่างชาติชอบกันเป็นพิเศษก็คือ “เกาะพีพี” ซึ่งเป็นหนึ่งในความภูมิใจของไทยเรานี่เอง โดยเกาะนี้สวยถึงขนาดที่ชาวต่างชาติยกย่องให้เป็นหนึ่งในเกาะที่สวยที่สุดในเอเชียเลยทีเดียว&nbsp;<br><br><div align="center"><img width="601" height="601" border="0" alt="กัมพูชา" title="กัมพูชา" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/u5.jpg" style="width: 601px; height: 601px; "><br></div><br><img width="30" height="33" border="0" alt="" src="http://img.kapook.com/image/icon/96631_1.gif" style="width: 30px; height: 33px; "><b style="background-color: rgb(156, 255, 222); ">4. ประเทศกัมพูชา</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; สำหรับคนที่ต้องการสัมผัสศิลปะแบบโบราณที่ยังคงงดงามไม่แพ้สมัยก่อน คุณก็ควรลองไปเยี่ยมชม "นครวัด" ที่ประเทศกัมพูชาดูสักครั้ง เพราะที่นี่ถือเป็นศาสนสถานของชาวพุทธที่ใหญ่ที่สุดในโลก และเต็มไปด้วยศิลปะที่ปราณีตสวยงามมากมายทั้งสถาปัตยกรรมที่งดงาม รวมไปถึงภาพสลักสวย ๆ จากวรรณคดีเก่าแก่<br><br><div align="center"><img width="601" height="801" border="0" alt="ญี่ปุ่น" title="ญี่ปุ่น" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/u1.jpg" style="width: 601px; height: 801px; "><br></div><br><img width="30" height="33" border="0" alt="" src="http://img.kapook.com/image/icon/96631_1.gif" style="width: 30px; height: 33px; "><b style="background-color: rgb(254, 220, 186); ">5. ประเทศญี่ปุ่น</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ญี่ปุ่นถือเป็นหนึ่งในประเทศที่นักท่องเที่ยวทั่วโลกนิยมกันมากทีเดียว เพราะมีครบไปซะทุกอย่าง ทั้งอาหารอร่อย ๆ แหล่งช้อปปิ้ง รวมไปถึงสถานที่เที่ยวทางวัฒนธรรม อย่างไรก็ตาม บางทีคุณอาจไม่จำเป็นต้องออกไปเที่ยวใช้จ่ายให้มากมาย แต่ก็สามารถสัมผัสความสวยของที่นี่ได้เหมือนกัน ซึ่งก็คือการชมซากุระที่มีเฉพาะในญี่ปุ่นนั่นเอง โดยที่ความงามของดอกไม้ชนิดนี้จะทำให้คุณประทับใจจนลืมไม่ลงเลยล่ะ<br><br><div align="center"><img width="601" height="601" border="0" alt="อินเดีย" title="อินเดีย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/u2.jpg" style="width: 601px; height: 601px; "><br></div><br><img width="30" height="33" border="0" alt="" src="http://img.kapook.com/image/icon/96631_1.gif" style="width: 30px; height: 33px; "><b style="background-color: rgb(229, 176, 255); ">6. ประเทศอินเดีย</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; อินเดียเป็นอีกประเทศที่ร่ำรวยทางวัฒนธรรม รวมถึงมีความหลากหลายแทรกตัวอยู่ทุกพื้นที่ ในแต่ละเมืองก็มีวิถีชีวิตที่ค่อนข้างแตกต่างกัน แต่ถ้าคุณเป็นคนรักธรรมชาติอย่างแท้จริง คุณก็จะต้องหลงรักที่นี่อย่างแน่นอน เพราะอุทยานแห่งชาติ Kaziranga ถือเป็นแหล่งอาศัยของสัตว์ป่ามากมาย โดยมีทั้งแรดและช้างป่าจำนวนมากให้เหล่าคนรักสัตว์ได้สัมผัส พร้อมทิวทัศน์ตามธรรมชาติที่งดงาม นอกจากนี้ อาหารอินเดียยังมีเมนูขึ้นชื่อมากมายที่น่าลิ้มลองอีกด้วย<br><br><div align="center"><img width="601" height="601" border="0" alt="เวียดนาม" title="เวียดนาม" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/u4.jpg" style="width: 601px; height: 601px; "><br></div><br><img width="30" height="33" border="0" alt="" src="http://img.kapook.com/image/icon/96631_1.gif" style="width: 30px; height: 33px; "><b style="background-color: rgb(207, 207, 207); ">7. ประเทศเวียดนาม</b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; และแล้วก็มาถึงประเทศสุดท้ายซึ่งก็คือประเทศเวียดนามที่เรารู้จักกันดีนั่นเอง โดยที่นี่เหมาะกับคนที่ชอบใช้วันพักผ่อนแบบเงียบสงบเป็นที่สุด เพราะไม่มีรถติดให้วุ่นวายจากการที่คนส่วนใหญ่เลือกใช้จักรยานหรือมอเตอร์ไซค์ แถมโคมผ้าน่ารัก ๆ ที่ชาวบ้านนิยมใช้กัน พร้อมทั้งชุดพื้นเมืองสวย ๆ ยังช่วยให้เมืองแห่งนี้ยังมีบรรยากาศอ่อนหวานน่ารักมาก ๆ อีกด้วย จึงไม่ใช่เรื่องแปลกเลยที่นักท่องเที่ยวหลาย ๆ คนจะประทับใจจนซื้อชุดพื้นเมืองของที่นี่กลับไปเป็นที่ระลึกกันมาแล้ว<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(128, 0, 128); ">อย่างไรก็ตาม นอกจากที่เรานำมาฝากกันนี้ ทวีปเอเชียก็ยังมีสถานที่น่าเที่ยวอีกมากทั้งประเทศจีน ภูฏาน มาเลเซีย และประเทศอื่น ๆ ซึ่งแต่ละที่ก็สวยไม่แพ้กันเลย เพราะฉะนั้น หยุดยาวคราวหน้าก็อย่าลืมเก็บประเทศสวย ๆ ในเอเชียนี้ไว้เป็นตัวเลือกของคุณด้วยนะคะ</span></span></div>', '2012821204014-3_65_1344945181.jpg', 1, '2012-08-21 20:40:14', 0);
INSERT INTO `post` VALUES (20, 10, 'สถานที่ท่องเที่ยว ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน', 'ท่องเที่ยว ขุนเขาและดอกไม้ บนเส้นทางเชียงราย ยูนนาน สถานที่ท่องเที่ยว บนเส้นทางเชียงราย ยูนนาน ที่เต็มไปด้วยขุนเขา และดอกไม้อันแสนงดงาม', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><b><img width="400" height="574" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/1_1.jpg" style="width: 400px; height: 574px; "><br></b></div><div style="text-align: center; "><b><br></b></div><div style="text-align: center; "><b><img width="400" height="580" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/2.jpg" style="width: 400px; height: 580px; "><br></b></div><div style="text-align: center; "><b><br></b></div><div style="text-align: center; "><b><img width="500" height="331" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/3_1.jpg" style="width: 500px; height: 331px; "><br></b></div><div style="text-align: center; "><b><br></b></div><div style="text-align: center; "><img width="500" height="363" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/5.jpg" style="width: 500px; height: 363px; "></div><div><b><br></b></div><div><b>ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน&nbsp;(อสท.)</b></div><div><br></div><div><span style="color: rgb(128, 0, 0); ">ธเนศ งามสม...เรื่อง&nbsp;</span></div><div><span style="color: rgb(128, 0, 0); ">ธเนศ งามสม, ไพศาล เจริญจรัสกุล และโสกณ บูรณประพฤกษ์...ภาพ</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(0, 0, 128); "><b>&nbsp;"เรามาไกลมาแล้ว"&nbsp;</b>จู่ ๆ ความรู้สึกนี้ก็แวบผ่านเข้ามา ขณะลมหนาวเหน็บกระโชกอาบเนื้อตัวปรากฏขุนเขาห่มคลุมหิมะอยู่เบื้องหน้า ทิวสนเอนไหว ดอกไม้ป่าเอนลู่ราวกับจะปลิดปลิว อารมณ์รับรู้ดังกล่าวยิ่งเข้าจู่จับ เมื่อพบว่าตนเองกำลังอยู่บนภูเขาห่างไกลในมณฑลยูนนาน จีนแผ่นดินใหญ่...</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 10 วันที่แล้ว เรามาพบกันที่ เชียงของ อำเภอเล็ก ๆ ในจังหวัดเชียงราย เย็นวันนั้นแม่น้ำโขงซึ่งทอดผ่านหน้าเรือนพักทาบทาด้วยแสงสีชมพูเย็นตา แผ่นดินลาวฝั่งตรงข้ามมองเห็นเป็นรูปเงารางเลือน<b>"เรา"&nbsp;</b>หมายถึงคน 6 คน ซึ่งเดินทางมาจากต่างที่ต่างถิ่น ทว่ามีจุดหมายร่วมกัน คือ มณฑลยูนนาน จีนตอนใต้</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="color: rgb(128, 0, 128); ">&nbsp;&nbsp;<b>"ไม่มีอะไรคาดเดาได้ชัดเจน เพราะเราไม่เคยใช้เส้นทางนี้กันมาก่อน"&nbsp;</b>ใครคนหนึ่งในคณะเอ่ย น้ำเสียงราบเรียบทว่าดวงตาเป็นประกาย สำหรับผู้ชื่นชอบพานพบโลกกว้าง พูดได้ว่า&nbsp;<b>จากเชียงของ ผ่านลาวเหนือ เวียดนาม ซาปา ไปยังจีนแผ่นดินใหญ่ คือเส้นทางที่ใครหลายคนกระหายใคร่ออกเดินทาง</b>&nbsp;สำหรับคณะของเรา กล่าวได้ว่า&nbsp;<b>นี่คือหนึ่งในความฝัน มันจะหอมหวานและเย้ายวนใจเพียงใด หากได้รอนแรมไปบนทางสายนี้</b></span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; และสำหรับผม ในยามที่ชีวิตมีแง่มุมให้ครุ่นคิด การเดินทางไกลอาจเป็นดั่ง&nbsp;<b>"แว่น"&nbsp;</b>ที่ช่วยกรองภาพบางภาพให้กระจ่างชัด ปราศจากมายาคติ นิยาม หรือความคาดหวังใด ๆ</div><div style="text-align: center; "><br></div><div style="text-align: center; "><div><img width="500" height="335" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/6.jpg_2.jpg" style="width: 500px; height: 335px; "></div></div><div><br></div><div><b><img border="0" alt="" src="http://img.kapook.com/image/icon/icon_1/48.gif">&nbsp;</b><b style="background-color: rgb(255, 255, 0); ">แม่น้ำบางสาย</b></div><div><b><br></b></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ใครบางคนกล่าวไว้ว่า&nbsp;<b><span style="color: rgb(128, 0, 0); ">การจะข้ามพ้นบางสิ่ง ก็คล้ายการข้ามแม่น้ำสักสาย...</span></b>คงเป็นความจริง ผมทบทวนนิยามนี้ในใจ ขณะเรือลำน้อยพาข้ามสายน้ำโขงอันกว้างใหญ่ ท้องฟ้ายามเช้าห่มเมฆสีเทา แดดต้นฤดูฝนส่องลอดลงมารำไร อาบผิวน้ำโขงเป็นประกาย เรือน้อยพาเรามาขึ้นฝั่งเมืองห้วยทราย สาธารณรัฐประชาธิปไตยประชาชนลาว ผ่านขั้นตอนตรวจคนเข้าเมือง เราก็แบกสัมภาระกลืนไปกับผู้คนหลากเชื้อชาติ ที่มุมหนึ่งของท่าเรือ รถตู้ป้ายทะเบียนลาวจอดรอเราอยู่ เก็บสัมภาระใบโตเรียบร้อย รถตู้สีขาวสภาพจวนเก่าก็วิ่งชิดเลนขวา บ่ายหน้าออกจากท่าเรือ</div><div><br></div><div>&nbsp; &nbsp; &nbsp;<span style="color: rgb(0, 0, 128); ">&nbsp;&nbsp; &nbsp;&nbsp;<b>"สบายดีทุก ๆ ท่าน มื้อนี้เราจะอยู่บนรถกันตลอดทางจะโค้งขึ้นภูไปถึงอุดมไชยโพ้น"</b>ท้าวทอง ผู้รับหน้าที่สารถีกล่าวต้อนรับด้วยภาษาลาวและเสียงหัวเราะอารมณ์ดี หลายคนหัวเราะตาม สองข้างทางปรากฏทิวเขาห่มหมอกขาว บรรยากาศต่างที่ต่างถิ่นทำให้ใจของเราตื่นเพริด ทางภูเขาเบื้องหน้ากลายเป็นความรื่นรมย์รอให้พอเผชิญ&nbsp;</span></div><div><br></div><div style="text-align: center; "><img width="500" height="333" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/9.jpg" style="width: 500px; height: 333px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ผมก็รู้สึกเช่นเดียวกับมิตรร่วมทาง การรอนแรมกำลังเริ่มต้น หลายสิ่งที่ทับถมคล้ายค่อย ๆ ถูกชะล้างด้วยภาพแปลกใหม่</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เราบ่ายหน้าไปตามทางลาดยางเก่ากร่อน ทว่าไม่มีใครใส่ใจ ทิวทัศน์ข้างทางและท้องทุ่งดึงดูดทั้งดวงตาและอารมณ์ ล่วงผ่านชั่วโมงแรก เรามาถึงตัวเมืองห้วยทราย ชุมชนเล็ก ๆ ระหว่างทาง ท้าวทอง จอดรถให้เราลงไปชมตลาด ซึ่งวางเรียงด้วยพืชผักนานา</div><div><br></div><div style="text-align: center; "><img width="502" height="377" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/55.jpg" style="width: 502px; height: 377px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(128, 0, 128); ">&nbsp;&nbsp;&nbsp;<b>"หน่อขม แซบหลาย"</b>&nbsp;ท้าวทองชี้ให้ดูหน่อไม้ชมพลางไต่ถามราคา หากหนทางไม่ยาวไกลชายหนุ่มคงซื้อกลับไปฝากคนที่บ้าน จากตลาดห้วยทราย เราพบแผงขายหน่อขมอีกแห่งริมทาง หญิงสาวชาวม้งบอกกับผมว่า ฤดูฝนเช่นนี้หน่อขมจะมีให้เสาะหาในป่าเพียง 2 เดือนแรก และเมื่อรวมกับรสชาตินุ่มหวานต่างจากชื่อ จึงเป็นที่นิยมทั่วทั้งลาวเหนือ</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ม่านหมอกล่องลอย ถนนทอดผ่านทุ่งกว้าง ท้าวทองชี้ให้ดูจุดสร้างสะพานข้ามแม่น้ำโขงแห่งใหม่ ซึ่งจะเชื่อมเชียงของกับห้วยทรายเข้าด้วยกัน ถึงวันนั้นถนนเก่ากร่อนคงหายไป หลายสิ่งคงมาเยือนห้วยทราย</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ท้องฟ้าเริ่มกระจ่าง เส้นทางทอดขึ้นลงภูผ่าน บ้านลาวห้วย หมู่บ้านชนเผ่าซึ่งสร้างเรือนอยู่ในหุบดอย ถึง บ้านน้ำฟ้า เราแวะพักคนและรถ ไปต่อไม่ไกลก็เข้า แขวงหลวงน้ำทา ช่วงต่อมาสองข้างทางร่มรื่นด้วยไม้ใหญ่ ที่โค้งภูช่วงหนึ่งมีแผงขายของป่า มีผักแปลก ๆ กล้วยน้ำว้า มันเพลา (มันแกว) นกปรอดหัวสีเขม่า รวมถึงกล้วยไม้ป่าดอกสีสดใส</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp;<b>&nbsp;&nbsp; ขณะข้ามแม่น้ำเพื่อไปต่อ ภาพดอกไม้ช่อหนึ่งและถ้อยคำบางคำก็แวบผ่านเข้ามาความรู้สึกบางอย่างค่อย ๆ ก่อรูปเค้าโครงหน้า มันเป็นอารมณ์ที่ยากจะควบคุม เมื่อพบว่าตนเองกำลังจากบางสิ่งมา ขณะหนทางข้างหน้าทอดยาวไกลออกไป</b></div><div><br></div><div style="text-align: center; "><img width="400" height="568" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/4.jpg_1.jpg" style="width: 400px; height: 568px; "><br></div><div style="text-align: center; "><br></div><div style="text-align: center; "><img width="500" height="394" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/6.jpg" style="width: 500px; height: 394px; "><br></div><div><br></div><div><b><img border="0" alt="" src="http://img.kapook.com/image/icon/icon_1/48.gif">&nbsp;</b><b style="background-color: rgb(255, 255, 0); ">สะพาน</b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ข้ามแม่น้ำสายนั้นมาตลอดครึ่งวัน เราใช้เวลาอยู่บนถนนทอดขึ้นลงภูจนตะวันเริ่มคล้อย เราจึงมาถึง เมืองไชย</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; แขวงอุดมไชย ท้าวทองพาขึ้นไปชม วัดพูทาด ซึ่งประดิษฐานบนยอดภู มองจากบันไดนาค เมืองไชยแทรกตัวอยู่ในที่ราบแคบ ๆ มองเห็นป้ายอักษรลาวและจีนเปิดไฟสว่างเรืองท้าวทองบอกว่า ชายแดนจีนอยู่ห่างไปไม่ไกลนัก ยามค่ำคืนเมืองไชยเงียบสงบไร้เสียงรบกวน ขณะออกไปกินมื้อค่ำ เราพบนักเดินทางต่างชาติน้อยราย ส่วนใหญ่เป็นชาวจีนที่เข้ามาค้าขาย</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(0, 0, 128); ">&nbsp;<b>รุ่งเช้า...เมืองไชยห่มหมอกหนาวเย็น เราจากที่นั่นมาขณะพระออกบิณฑบาต ถนนคดโค้งไต่เลาะขึ้นไป ขณะเบื้องล่างแม่น้ำพาดทอดสายราวภาพลวงตา บ้านเรือนชาวลาวสูงปรากฏเป็นระยะ&nbsp;</b>ตามกระท่อมหลังน้อยมีผู้คนรายล้อมกองไฟ คล้ายจุดหิ่งห้อยเล็ก ๆ วับวามในป่าภูกว้างใหญ่ ข้ามสะพานอีกครั้ง&nbsp;<b>เราก็เข้าสู่ แขวงพงสาลี ที่ เมืองขวา เมืองชุมทางริมแม่น้ำอู เราต้องรอแพขนานยนต์เพื่อข้ามไปยังชายแดนเวียดนาม</b></span></div><div style="text-align: center; "><br><b><img width="500" height="350" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/4.jpg" style="width: 500px; height: 350px; "></b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ขณะเดินสำรวจริมฝั่ง นอกจากโรงแรมขนาดย่อม ห้องการไปรษณีย์ และนักเดินทางชาวยุโรป ผมพบเรือโดยสารไปหลวงพระบาง&nbsp;<b>ระหว่างไต่ถามเที่ยวเวลา ภาพเมืองสงบงามแห่งนั้นก็ค่อย ๆ ผุดเข้ามาในความทรงจำ&nbsp;</b>วันเวลาล่วงผ่าน สี่เดือนก่อนผมไปเยือนหลวงพระบางด้วยเหตุผลบางอย่าง ระหว่างรอนแรม เวลาครึ่งเดือนนั้นก็มอบคำตอบเปี่ยมความหมาย...</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เราข้ามแม่น้ำอูมาพร้อม ๆ กับเริ่มบอกลาแผ่นดินลาว ทางลูกรังคลุ้งฝุ่นทอดไปบนภูเขา นาน ๆ จึงจะพบหมู่บ้านชาวลาวสูง รอบ ๆ ตัวเรายามนี้คือทิวเขาซับซ้อนและป่าไม้ หลายครั้งที่รถตู้จวนเก่าต้องลงไปลุยบนทางเกวียน ในแม่น้ำลำห้วยมากสาย เพราะถนนและสะพานกำลังก่อสร้าง ท้าวทองบอกว่าเป็นความช่วยเหลือของเวียดนาม เพื่อจะเชื่อสองประเทศด้วยถนนแอสฟัลต์สายใหม่<b>&nbsp;มันเป็นความรู้สึกที่ทั้งเหนื่อยล้า ตื่นใจ และอาลัย เมื่อต้องคอยระวังก้อนหินหล่นลงมา หลีกต้นไม้ล้มขวาง รถยางแตก แล้วพบว่าภาพเหล่านั้นกำลังเลือนหาย...</b></div><div style="text-align: center; "><br></div><div style="text-align: center; "><img width="500" height="373" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/12.jpg_2.jpg" style="width: 500px; height: 373px; "><br></div><div style="text-align: center; "><br></div><div><b><img border="0" alt="" src="http://img.kapook.com/image/icon/icon_1/48.gif">&nbsp;</b><b style="background-color: rgb(255, 255, 0); ">บ่ายคล้อย</b></div><div><b><br></b></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ทางภูเขาพาเรามาถึงด่านปากหง ชำระค่าธรรมเนียมคนละ 4,000 กีบ หรือ 16 บาท เราก็ข้ามเส้นพรมแดนสู่ด่านไตตะราง สาธารณรัฐสังคมนิยมเวียดนาม ที่นี่เราแลกเงินกีบของลาวเป็นเงินด่อง เปลี่ยนจากรถตู้จวนเก่ามาเป็นมินิแวนคันใหม่</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="color: rgb(128, 0, 128); ">&nbsp;&nbsp;<b>"สวัสดีทุก ๆ ท่าน เรากำลังเข้าสู่อีกหนึ่งดินแดนไทยแล้วนะครับ"&nbsp;</b>ชายวัยล่วงชราซึ่งแทนตัวเองว่า&nbsp;<b>"อุดม"</b>&nbsp;กล่าวต้อนรับด้วยภาษาไทยชัดคำ ทั้งสำเนียงและใบหน้า ชวนให้เชื่อได้ว่าเขาเป็นคนไทย&nbsp;</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="color: rgb(0, 0, 128); "><b>"ผมเรียนภาษาไทยที่วิทยาลัยแห่งชาติ พอขึ้นปี 3 ต้องหยุดเรียน ไปรบกับอเมริกา"</b>คุณอุดมเล่าน้ำเสียงดังชัดประโยคท้ายทำให้หลายคนหันมาฟังด้วยความสนใจ จากด่านไตตะราง ถนนเปลี่ยนเป็นลาดยางค่อนข้างเรียบ ช่วงเขตปลอดทหาร (Demilitarized Zone) 6 กิโลเมตรระหว่างประเทศ&nbsp;<b>ผืนป่าร่มรื่นสมบูรณ์ แต้มแต่งด้วยเสี้ยวป่า ซึ่งยามนี้ผลิดอกสีน้ำนมไปทั่วทิวเขา พ้นจากเขตปลอดทหาร ถนนทอดลงภูเขามาพบนาข้าวลดหลั่น ครู่ต่อมาเราก็อยู่บนสะพานซึ่งทอดข้ามแม่น้ำป่าหนาม แวบหนึ่งผมมองเห็นซากรถจิ๊ปในสายน้ำสีชา</b></span></div><div><br></div><div style="text-align: center; "><img width="500" height="349" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/10.jpg" style="width: 500px; height: 349px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ชั่วโมงต่อมาเราก็เข้าเขตเมือง มอเตอร์ไซค์และรถยนต์สวนกันขวักไขว่ จักรยานพบเห็นได้ทุกท้องถนน ผ่านอนุสรณ์สถานทหารผู้เสียชีวิตจากสงคราม เราก็อยู่ใจกลางเดียนเบียนฟู ในยามนั้นไม่แน่ใจว่ามิตรร่วมทางรู้สึกอย่างไร สำหรับผมภาพแห่งสงครามได้จู่จับเข้ามา ตั้งแต่ข้ามสะพานแม่น้ำป่าหนามนั่นมาแล้ว&nbsp;</div><div><br></div><div style="text-align: center; "><div><img width="500" height="333" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/8_1.jpg" style="width: 500px; height: 333px; "></div><div><br></div><div><img width="500" height="329" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/8.jpg_3.jpg" style="width: 500px; height: 329px; "><br></div><div><br></div></div><div><b><img border="0" alt="" src="http://img.kapook.com/image/icon/icon_1/48.gif">&nbsp;</b><b style="background-color: rgb(255, 255, 0); ">ไท</b></div><div><b><br></b></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เดียนเบียนฟูพาผมไปพบหลากหลายอารมณ์ ทั้งเศร้าโศกและอบอุ่นในหัวใจ บ่ายวันนั้นเราออกนอกเมืองไปเยือน ศาลเจ้าหว่างกงเจิ้ต หรือ&nbsp;<b>"องเชียงสือ"&nbsp;</b>ที่ชาวไทยดำเคารพศรัทธา ย้อนเวลากลับไป ประวัติศาสตร์ของเวียดนามล้วนเต็มไปด้วยบาดแผลแห่งสงครามกว่า 900 ปี ที่อยู่ใต้อิทธิพลของจีน และความขัดแย้งกันเองระหว่างราชวงศ์ต่าง ๆ กว่าสิบสายตระกูล</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ช่วง พ.ศ. 2345 องค์ชายเหวียนแอ๋ง ทายาทตระกูลเหวียน หรือที่คนไทยรู้จักดีในนามของ เชียงสือ ผู้เคยหลบหนีมาขอความช่วยเหลือจากพระบาทสมเด็จพระพุทธยอดฟ้าจุฬาโลก รัชกาลที่ 1 สามารถรวมแคว้นทั้งเหนือและใต้ให้เป็นปีกแผ่นได้ พระองค์สถาปนาราชวงศ์เหวียนขึ้นปกครองประเทศ และเรียกดินแดนที่มีอาณาเขตตั้งแต่จีนตอนเหนือจรดคาบสมุทรก่าเมาทางตอนใต้ว่า<b>&nbsp;"เวียดนาม"</b>&nbsp;เป็นครั้งแรกในประวัติศาสตร์</div><div><span style="color: rgb(128, 0, 128); "><br></span></div><div><span style="color: rgb(128, 0, 128); ">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>ระหว่างรวบรวมแคว้นต่าง ๆ นั้น องเชียงสือ ขับไล่โจรก๊กอื่น ๆ ในเดียนเบียนฟูจนแตกพ่าย ชาวไทยดำซึ่งอาศัยอยู่ที่นี่สำนึกในบุญคุณ จึงสร้างศาลเจ้านี้เพื่อเป็นที่สักการะและอนุสรณ์สถาน</b>&nbsp;ผ่านกำแพงสูงเข้าไปอาณาบริเวณร่มรื่นด้วยหมูไม้สระบัว และไทรสูงใหญ่ ตัวศาลเจ้าอายุ 200 ปี ดูขรึมขลังสร้างจากไม้ลิม ซึ่งมีเนื้อไม้ทนงามคล้ายไม้สักของไทย ก้าวเข้าไปด้านในกลิ่นธูปควันเทียนล่องลอย&nbsp;<b>ตามบานประตูและหน้าต่างแกะสลักลวดลายไม้ดอก นกกระเรียน แลงดงามอ่อนช้อย สะท้อนให้เห็นว่าชาวไทยดำศรัทธาองเชียงลือมากเพียงใด</b>&nbsp;ตามประวัติบันทึกไว้ว่า&nbsp;<b>ราชวงศ์เหวียนรุ่งเรืองยาวนานร่วมร้อยปี กระทั่งเกิดความขัดแย้งทางศาสนาคริสต์กับฝรั่งเศส และตามมาด้วยความพ่ายแพ้แก่กองเรือฝรั่งเศสในปี พ.ศ. 2401 ส่งผลให้เวียดนามสูญเสียเอกราชนับแต่บัดนั้น</b></span></div><div><br></div><div style="text-align: center; "><img width="500" height="377" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/10.jpg_3.jpg" style="width: 500px; height: 377px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>จากศาลเจ้าองเชียงสือ เราไปเยือนฐาน A1 ลักษณะเป็นเนินสูงใจกลางเมือง&nbsp;</b>มองเห็นเดียนเบียนฟูได้รอบทิศทาง หลังตกเป็นอาณานิคม ฝรั่งเศสก็เข้ามาแสวงหาประโยชน์บนแผ่นดินเวียดนามในทุก ๆ ด้าน สร้างความอัดอั้นตันใจแก่หนุ่มสาวชาวเวียดที่ได้รับการศึกษาในต่างประเทศ นำมาสู่การก่อตั้งพรรคคอมมิวนิสต์อินโดจีนโดย&nbsp;<b>โฮจิมินห์ ชายหนุ่มนักหนังสือพิมพ์ซึ่งพำนักอยู่ที่ปารีส หลังสงครามโลกครั้งที่ 2 เวียดนามประกาศตนเป็นอิสระ ทว่าฝรั่งเศสไม่ยอมรับ จึงนำมาสู่สงครามอันดุเดือดยาวนานทั่วอินโดจีน&nbsp;</b></div><div><br></div><div style="text-align: center; "><img width="500" height="318" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/8.jpg_2.jpg" style="width: 500px; height: 318px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>ที่ฐาน A1 นี้ ฝรั่งเศสติดตั้งปืนใหญ่</b>&nbsp;ทั้งยังเตรียมพลร่มและเครื่องบินทิ้งระเบิด หวังจะให้เป็นหลุมพรางล่อทหารเวียดมินห์มาติดกับ ทว่าการณ์กลับตรงข้าม ฐานถูกปืนใหญ่ฝ่ายเวียดมินห์รายล้อมระดมยิงใส่ สนามบินและการสนับสนุนทางอากาศไม่สามารถใช้การได้ ทั้งเสบียงอาหาร อาวุธ หรือแม้แต่เครื่องหมายยศนายพลซึ่งทิ้งร่มลงมาก็ถูกยึดไป</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(0, 0, 128); ">&nbsp;<b>วันที่ 7 พฤษภาคม พ.ศ. 2497 ล่วงวันที่ 53 ทหารฝรั่งเศสก็ยอมจำนน นำไปสู่สนธิสัญญาเจนีวา</b>&nbsp;โดยสหรัฐอเมริกาเข้ามามีบทบาท บีบให้แบ่งเวียดนามออกเป็นเหนือ-ใต้ 3 ปีให้หลัง<b>ด้วยข้ออ้างในการสกัดกั้นลัทธิคอมมิวนิสต์ สหรัฐอเมริกาก็กระโจนเข้าสู่สงครามเวียดนาม และนำไปสู่ความพ่ายแพ้ในเวลาต่อมา...</b></span></div><div><span style="color: rgb(128, 0, 128); "><br></span></div><div><span style="color: rgb(128, 0, 128); ">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; วันถัดมา...เราแวะมาที่&nbsp;<b>ฐานเดอ คาสตรีส์ ฐานบัญชาการของฝรั่งเศสที่กล่าวได้ว่าสำคัญสุดในเวียดนาม ที่นี่เองที่ นายพลหวอ เหวียน ย้าป นายทหารคู่ใจโฮจิมินห์ ทำให้โลกต้องตกตะลึกด้วยการเอาชนะเจ้าอาณานิคม ส่งผลให้ภูมิภาคอินโดจีนเป็นอิสระจากฝรั่งเศส&nbsp;</b>จากฐานเดอ คาสตรีส์ เราไปเยือน กองบัญชาการเวียดมินห์ ซึ่งอยู่ห่างไปทางทิศตะวันออกราว 20 กิโลเมตร ถิ่นอาศัยของชาวไทยดำ&nbsp;</span></div><div><br></div><div style="text-align: center; "><img width="500" height="322" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/8.jpg_1.jpg" style="width: 500px; height: 322px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; หากอธิบายให้เห็นภาพกองบัญชาการเวียดมินห์คล้ายคลึงไม่น้อยกับฐานที่มั่นพรรคคอมมิวนิสต์แห่งประเทศไทย (พคท.) ที่ภูหินร่องกล้า ใต้ร่มเงาไม้ใหญ่ ทั้งโขดหิน ซอกหลีบ ภูเขา ล้วนเป็นชัยภูมิอันเหมาะสมในการวางแผนและเคลื่อนไหว อาณาบริเวณถูกจัดแบ่งเป็นสัดส่วน ทั้งกองบัญชาการ ห้องปฐมพยาบาล โรงนอน โรงอาหาร และอุโมงค์หลบภัยทางอากาศ ไม่ไกลจากกองบัญชาการ&nbsp;<b>มีแผ่นหินจารึกวันที่ 31 มกราคม ค.ศ. 1954 ถึงวันที่ 15 พฤษภาคมในปีเดียวกัน ว่า&nbsp;<span style="color: rgb(128, 0, 0); ">ณ ที่นี้ เป็นเวลานานถึง 4 เดือน 15 วัน ที่นายทหารเวียดมินห์ได้ทุ่มเทหยาดเหงื่อและเลือดเนื้อเพื่อขับไล่ฝรั่งเศส กว่าจะได้อิสรภาพและความเป็น "ไท" คืนกลับมา...</span></b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ตะวันบ่ายคล้อย เราเดินจากฐานบัญชาการเวียดมินห์มาทางหมู่บ้านไทยดำ ลมพัดเย็นชื่น ตะวันกำลังลอยต่ำ ฉายแสงทองอาบท้องนาและบ้านเรือนซึ่งสร้างอย่างน่าชมด้วยไม้ เด็ก ๆ วิ่งเล่นอยู่ริมลำห้วย หญิงสาวนุ่งผ้าถุงสีดำหาบพืชผักไปตามคันนา เย็นวันนั้นเรามีโอกาสไปเยือนเรือนไทยดำที่บ้านเพียงเลย ในช่วงแรกคุณอุดมทำหน้าที่ล่ามถ่ายทอดความ ทว่าเพียงครู่ก็นั่งยิ้มสบายใจ เพราะถ้อยคำหลายคำนั้นเหมือนภาษาไทย</div><div><br></div><div style="text-align: center; "><img width="500" height="387" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/10.jpg_1.jpg" style="width: 500px; height: 387px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(0, 0, 128); ">&nbsp;<b>"ไปซังมา"</b>&nbsp;ท่าว หญิงสาวชาวไทยดำเอ่ยพร้อมกับผายมือเชิญเรือนใหญ่ ตรงกลางเรือนสำรับกับข้าววางรอพร้อมแล้ว ท่าวแนะนำอาหารแต่ละจาน นั่น<b>&nbsp;"เหยือฟอง"</b>&nbsp;เนื้อหมูสับห่อใบตองแล้วเผา ปลาปิ้งหอมควันไฟ และ<b>&nbsp;"เบ๊าะบาน"</b>&nbsp;ยำดอกเสี้ยวขาว ดอกไม้ที่เราเห็นบานสะพรั่งระหว่าง เธอแนะนำว่ากินกับข้าวเหนียวดำ ขาว และเหลืองอร่อยนัก ระหว่างนั้นท่าวเพียรรินเหล้าชาวซึ่งหมักเอง&nbsp;<b>"บ่เมาบ่ม่าน"</b>&nbsp;เธอว่าพลางหัวเราะร่าเริง&nbsp;</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เนิ่นนานกว่า 2,000 ปีแล้วที่ชาวไทยดำอาศัยอยู่ที่นี่ เดียนเบียนฟูคือหนึ่งในบ้านของพวกเขาและเราชาว<b>&nbsp;"ไท"</b>&nbsp;ทั้งหลาย แม้ไม่ได้ถือพุทธ ทว่าชาวไทยดำบูชาบรรพบุรุษ ด้วยศรัทธาอันบริสุทธิ์ พวกเขายังคงสอนลูกหลานให้รำลึกถึงคุณของน้ำและนา ดั่งภาษิตที่ว่า<b>&nbsp;<span style="color: rgb(128, 0, 0); ">"ได้กินข้าวอย่าลืมเสื้อนา ได้กินปลาอย่าลืมเสื้อน้ำ"</span></b></div><div><br></div><div>&nbsp; &nbsp;<b>&nbsp;&nbsp; &nbsp; &nbsp; ค่ำคืนนั้น หลังห่างถิ่นที่อันคุ้นเคยมาหลายวัน ในแวดล้อมชาวไทยดำ ถ้อยคำ รอยยิ้ม และเสียงหัวเราะทำให้ผมรู้สึกเหมือนอยู่ท่ามกลางญาติมิตรเมืองไกล...ญาติมิตรชาวไท</b></div><div><br></div><div style="text-align: center; "><div><img width="400" height="491" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/11.jpg" style="width: 400px; height: 491px; "></div></div><div><br></div><div><b><img border="0" alt="" src="http://img.kapook.com/image/icon/icon_1/48.gif">&nbsp;</b><b style="background-color: rgb(255, 255, 0); ">เมืองในหมอก</b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เราจากเดียนเบียนฟูมาขณะตะวันฉาย ทะเลสาบห่มไอหมอก ทิวเขาสีครามกระจ่างตา สองข้างทางเรียงรายไปด้วยต้นไม้ ถนนทอดไปในท้องทุ่งกว้าง แม้จะเป็นปลายฤดูแล้ง ทว่าท้องทุ่งยังปูลาดด้วยข้าวกล้าเขียวขจี คุณอุดมบอกว่าเดียนเบียนฟูทำนาได้ปีละ 4 ครั้ง</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>"ดินแทนไม่ได้พักหรอกครับ"&nbsp;</b>ชายวัยล่วงชราเอ่ยแล้วก็ยิ้ม คงเป็นยิ้มภูมิใจในความขยันของชนชาติตน เราเพลินตากับท้องทุ่งข้าว มารู้ตัวอีกครั้งเมื่อถนนวกลงภูเขา นาข้าวกลายเป็นขั้นบันได เรือนชาวไทยดำสร้างกระจายตรงโน้นตรงนี้มินิแวนค่อย ๆ บ่ายหน้าลงไป มีแผงขายของป่าเป็นระยะ ม้าต่างถูกผูกไว้ตามร่มไม้ ช่วงต่อมาถนนเลียบแม่น้ำดำ มองเห็นกรวดหินเกลื่อนท้องน้ำ หมู่บ้านไทยดำ ปรากฏอีกครั้ง เห็นได้ชัดว่าเพิ่งสร้างขึ้นใหม่ เรียงรายไปตามถนนและแม่น้ำ คุณอุดมบอกว่าข้างหน้ามีโครงการสร้างเขื่อนชงค่า ตัวเมืองเก่าและหมู่บ้านจึงต้องย้ายมายังที่ใหม่</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="color: rgb(128, 0, 128); ">&nbsp;&nbsp;<b>"เราเป็นประเทศสังคมนิยม มีพรรคเดียว ถ้าตกลงกันได้ก็ลงมือทำ ไม่มีความขัดแย้งตามมา"</b>&nbsp;คุณอุดมเอ่ยแล้วก็ยิ้ม แกคงเห็นคำถามบนใบหน้าใครหลายคน ร่วม 30 กิโลเมตร ที่ถนนเลียบทะเลสาบ เมื่อลาดลงหุบเขาไลโจวเมืองใหม่ก็ปรากฏ เราแวะกินมื้อเที่ยงที่นี่ ไม่มีใครเอ่ยถึงอาหาร ถนน 8 เลนกับผังเมืองใหม่เอี่ยมนั้นให้รสชาติมากกว่า</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 15.32 น. เราบอกลาไลโจว บ่ายหน้าขึ้นเขาสูงอีกครั้ง ป้ายริมทางเขียนว่า<b>&nbsp;"ซาปา 70 กิโลเมตร"</b>&nbsp;ต้องใช้กำลังคนมากเพียงใด จึงจะสร้างและเดินทางได้สำเร็จเช่นนี้ ผมครุ่นคิดในใจ เมื่อถนนโค้งขดขึ้นไปบนไหล่เขาสูง หุบเหวยิ่งลึกลิ่ว อากาศยิ่งหนาวเย็น เมฆคล้ายลอยลงมาใกล้ถึงความสูงราว 2,000 เมตร จากระดับทะเลปานกลาง รอบตัวก็เลือนราง คล้ายกำลังเดนิทางอย่างไร้จุดหมาย...</div><div><br></div><div style="text-align: center; "><img width="500" height="340" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/12.jpg_1.jpg" style="width: 500px; height: 340px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp;&nbsp;<b>&nbsp; &nbsp; 17.43 น. เหมือนจู่ ๆ เมืองก็ปรากฏกลางม่านหมอก ดวงไฟหลากสีสลัวราง นักเดินทางหลากสัญชาติแบกเป้ดุ่มเดิน</b>&nbsp;อาคารปูนห้าหกชั้นเบียดเสียดสองฟากไหล่เขา แต่ละดูหาแขวนป้ายภาษาอังกฤษ ระบุโรงแรม เกสต์เฮาส์ ร้านขายอุปกรณ์กลางแจ้ง ร้านเช่าสูท ร้านไวน์ ร้านอาหารวิวภูเขา</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b>"ยินดีต้อนรับสู่ซาปาครับ"</b>&nbsp;ลุงหยุง เจ้าถิ่นซึ่งรอพวกเราอยู่ เอ่ยเบา ๆ ราวกับเกรงว่าม่านหมอกจะเลือนหาย เย็นวันดังกล่าวซาปาต้อนรับเราด้วยละอองฝนหมอกขาวและอากาศเหน็บหนาว&nbsp;</div><div><br></div><div style="text-align: center; "><img width="550" height="263" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/12.jpg" style="width: 550px; height: 263px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; รุ่งเช้าอุณหภูมิลดลงมาที่ 5.5 องศาเซลเซียส ละอองฝนยังโปรยปราย ผมสวมเชิ้ตตัวหนา ฝ่าสายฝนเย็นเยือกไปที่ตลาดเข้าอากาศคล้ายอุ่นขึ้นเมื่ออยู่ท่ามกลางผู้คนขวักไขว่ เหมือนเช่นทุก ๆ ชาติ ตลาดเช้ามากด้วยเรื่องราว พืชผักท้องถิ่นผลไม้นานา ดอกไม้ รอยยิ้ม และถ้อยโอภาปราศรัย แม้จะสื่อสารต่างภาษา</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">จากตลาดเช้าผมเดินตามทางซึ่งทอดลดหลั่นขึ้นไปผ่านโบสถ์คริสต์ สัญลักษณ์ประจำเมือง และบ้านพักสไตล์ฝรั่งเศสซึ่งสร้างด้วยหินสีขาว&nbsp;<b>"แต่ก่อนที่นี่มีแต่ม้งอาศัย พอฝรั่งเศสได้ข่าวว่ามีแร่ ก็ตัดถนนขึ้นมา สร้างบ้านพัก สร้างโบสถ์ ต่อมาก็กลายเป็นเป็นที่พักผ่อนด้วย เพราะบางปีมีหิมะตกอากาศเย็นสบาย"</b>&nbsp;ลุงหยุงเล่า ขณะพาเดินชมรอบ ๆ ซาปา&nbsp;</span></div><div><br></div><div style="text-align: center; "><img width="500" height="333" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/10.jpg_2.jpg" style="width: 500px; height: 333px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; เราไปเยือนบ้านชาวม้งหลายหมู่บ้าน ภาพนาชั้นบันไดโค้งขดลดหลั่นไปทั่วทิวเขานั้น กล่าวได้ว่างดงามตรึงตรา ถึง หมู่บ้านคัตคัต เราเดินไปชม น้ำตกเตือนซา หรือ&nbsp;<b>"ที่ประทับของเทพ"</b>&nbsp;ในความหมายของชาวม้ง&nbsp;</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(128, 0, 128); ">&nbsp;&nbsp;<b>&nbsp;เตือนซาหมดจดสมดั่งชื่อ แม้จะถูกรบกวนด้วยสิ่งก่อสร้างอย่างโรงปั่นไฟ</b>&nbsp;เสาไฟฟ้าในยุคขุดหาแร่&nbsp;<b>ลุงหยุงเล่าว่า ดีบุกและสังกะสีเนรมิตสิ่งเหล่านี้ ไฟฟ้าถูกส่งไปป้อนเครื่องทำน้ำอุ่นหลอดไฟ และสิ่งอำนวยความสะดวกเท่าที่จะมีได้</b>&nbsp;ขณะชาวม้งรอบ ๆ อาศัยในกระท่อมไม้ไผ่มุงหญ้าคา</span></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<b>&nbsp;เราจากที่นั่นมาขณะหมอกยังห่อคลุม ชั่วโมงถัดมาเราก็ล่ำลา คุณอุดม และ ลุงหยุง มิตรอาวุโสแห่งซาปา คล้ายวันแรกที่มาเยือน จู่ ๆ ซาปาก็เหมือนเลือนหาย ม่านหมอกกลืนกลบทุกสิ่งไว้เบื้องหลัง</b></div><div><br></div><div style="text-align: center; "><img width="500" height="268" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/14.jpg" style="width: 500px; height: 268px; "></div><div><br></div><div><b><img border="0" alt="" src="http://img.kapook.com/image/icon/icon_1/48.gif">&nbsp;<span style="background-color: rgb(255, 255, 0); ">ดอกไม้</span></b></div><div><b><br></b></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ล่วงวันที่ 6 แล้ว ที่เราเดินทางมาจากเชียงราย ออกจากซาปาถนนก็ไต่ความสูงขึ้นไปเรื่อย ๆ ถึงลาวกาย-เมืองใหญ่ชายแดน เราข้ามแม่น้ำแดงไปประทับตราหนังสือเดินทาง ผ่านด่านเหอโข่ว เราก็เข้าสู่ สาธารณรัฐประชาชนจีน เพียงข้ามแม่น้ำแดงมาผมก็พบว่าอารมณ์รับรู้ของตนเปลี่ยนไป ตลอด 4 วัน ที่รอนแรมในแผ่นดินจีน คล้ายกำลังเดินทางยาวไกลไร้จุดสิ้นสุด แม้สองข้างทางจะงดงามด้วยขุนเขา ผลิพราวไปด้วยดอกไม้&nbsp;</div><div><br></div><div style="text-align: center; "><img width="500" height="356" alt="ขุนเขาและดอกไม้ บนเส้นทางเชียงราย-ยูนนาน" class="img-mobile" src="http://img.kapook.com/u/thitima/travel/13.jpg" style="width: 500px; height: 356px; "></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp;<b>&nbsp;&nbsp;<span style="color: rgb(0, 0, 128); ">&nbsp;ไร่ท้ออวดดอกสีชมพูอ่อนหวาน ทุ่งมัสตาร์ดบาน ดอกดารดาษ ปูลาดผืนแผ่นดินกว้างใหญ่ด้วยสีเหลืองสดใส ทว่าความรู้สึกดังกล่าวก็ไม่คลี่คลาย...</span></b></div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ในวันที่ 10 ขณะเดินทางบนภูเขาสูงชันถึงทางโค้ง ช่วงหนึ่งรถของเราก็ตกจากไหล่ถนน ทำอย่างไรก็ไต่กลับขึ้นไปไม่ได้ ทำได้เพียงรอคอยรถสักคันผ่านมา เราทยอยลงจากรถ ทันทีที่เปิดประตู ลมหนาวเหน็บก็กระโชกอาบเนื้อตัว</div><div><br></div><div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style="color: rgb(128, 0, 128); "><b>&nbsp;"เรามาไกลมากแล้ว"</b>&nbsp;จู่ ๆ ความรู้สึกนี้ก็แวบผ่านเข้ามา ขณะขุนเขาห่มหิมะปรากฏเบื้องหน้า ทิวสนเอนไหว ดอกไม้ป่าเอนลู่ราวกับจะปลิดปลิว โมงยามนั้นเองที่ผมอยากถนอมดอกไม้ทุกดอกตรงหน้าไว้ รับรู้ว่า<b>&nbsp;"ดอกไม้"&nbsp;</b>ที่รอคอยอยู่มีความหมายมากเพียงใด</span></div></span>', '2012821204216-2_38_1345538563.jpg', 1, '2012-08-21 20:42:16', 0);
INSERT INTO `post` VALUES (21, 10, 'สถานที่ท่องเที่ยว ไปเที่ยว 10 Unseen Thailand กันดีกว่า', 'Unseen Thailand ท่องเที่ยวไทย ไปยลโฉมกับ 10 อันซีนไทยแลนด์ ที่เรารวบรวมมาฝาก งานนี้ขอบอกว่าไม่ควรพลาดไปสัมผัสกับความงดงามจริง ๆ', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><div align="center"><img border="0" width="550" height="550" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/page.jpg" style="width: 550px; height: 550px; "><br></div><br><a target="_blank" href="http://www.kapook.com/" style="text-decoration: none; ">เรียบเรียงข้อมูลโดยกระปุกดอทคอม</a><br>ขอขอบคุณภาพประกอบจาก&nbsp;<a target="_blank" href="http://thai.tourismthailand.org/" style="text-decoration: none; ">ททท.</a>,&nbsp;<a target="_blank" href="http://www.unseentourthailand.com/link/unseenthailand/gr03_ne03.html" style="text-decoration: none; ">unseentourthailand.com</a>,&nbsp;<a target="_blank" href="http://www.visitphayao.com/th/destination/watpranangdin" style="text-decoration: none; ">visitphayao.com</a>&nbsp;และ Instagram Oaddybeing<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(0, 0, 255); "><span style="color: rgb(128, 0, 128); ">ประเทศไทยขึ้นชื่อว่ามีแหล่งท่องเที่ยวที่สวยสดงดงาม ทั้งทางธรรมชาติ วิถีชีวิต และวัฒนธรรม โดยเฉพาะความอัศจรรย์ของแหล่งท่องเที่ยวตามพื้นที่ต่าง ๆ ทั่วประเทศ เพราะฉะนั้น วันนี้กระปุกท่องเที่ยวเลยขออาสาเป็นไกด์ พาเพื่อน ๆ ไปยลโฉมกับ 10 ความมหัศจรรย์ใจที่ไม่ควรพลาดไปสัมผัสกับความงดงามกันค่ะ เอ้า! พร้อมแล้วก็ไปตะลุยสุดยอดแหล่งท่องเที่ยวของเมืองไทย ที่โดนการันตีว่าสวยสุด ๆ จากการท่องเที่ยวแห่งประเทศไทย (ททท.) กันเลย โดยเริ่มที่...<br></span><b><br></b></span><div align="center"><span style="color: rgb(0, 0, 255); "><img border="0" width="600" height="400" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/1.jpg" style="width: 600px; height: 400px; "></span><br></div><span style="color: rgb(0, 0, 255); "><b><br><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; ">1. ทะเลแหวก จังหวัดกระบี่</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ทะเลแหวก เป็นปรากฏการณ์ที่เกิดจากอิทธิพลของน้ำขึ้นและน้ำลง ทำให้สันทรายของเกาะทั้ง 3 คือ เกาะทับ เกาะหม้อ และเกาะไก่ ปรากฏขึ้นกลายเป็นหาดทรายขาวเชื่อมกันทั้ง 3 เกาะ อย่างน่าอัจศจรรย์ใจ โดยจะเกิดเฉพาะในวันก่อนและหลังวันขึ้น 15 ค่ำ ราว 5 วัน ทำให้สามารถเดินเล่นไป-มาระหว่างเกาะได้ สำหรับฤดูกาลท่องเที่ยว คือ เดือนพฤศจิกายนถึงต้นเดือนพฤษภาคมของทุกปี สามารถเช่าเรือได้จากบริเวณอ่าวนาง ใช้เวลาเดินทางประมาณ 25 นาที<br><br><div align="center"><img border="0" width="600" height="450" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/phanon.jpg" style="width: 600px; height: 450px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>2. พระนอนหงาย จังหวัดสุพรรณบุรี</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ณ วัดพระนอน ตำบลพิหารแดง มีพระพุทธรูปปางไสยาสน์สลักจากหิน มีลักษณะแปลกกว่าที่อื่น คือ เป็นพระพุทธรูปอยู่ในลักษณะนอนหงายขนาดเท่าคนโบราณ ยาวประมาณ 2 เมตร ลักษณะคล้ายกับพระนอนที่เมืองกุสินารา ประเทศอินเดีย สถานที่ปรินิพพานของพระพุทธเจ้า แปลกจนกลายยังเป็น Unseen Thailand<br><br><div align="center"><img border="0" width="500" height="478" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/22.jpg" style="width: 500px; height: 478px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>3. ค้างคาวร้อยล้าน วัดเขาช่องพราน</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; บริเวณวัดเขาช่องพราน ตำบลเตาปูน อำเภอโพธาราม มีถ้ำที่สวยงาม คือ "ถ้ำพระนอน" ซึ่งภายในถ้ำมีพระพุทธรูปมากกว่าร้อยองค์ และมีพระพุทธรูปปางไสยาสน์องค์ใหญ่ ความยาว 9 เมตร แต่สิ่งที่สร้างชื่อให้เขาช่องพราน คือ ฝูงค้างคาวนับล้านตัว ที่กรูกันบินออกจากถ้ำพวยพุ่งเป็นสายสีดำนานนับชั่วโมง ทุกเย็นในช่วงใกล้พลบค่ำ ประมาณ 17.30 น. ในช่วงฤดูร้อนฝูงค้างคาวจะบินไปทางทิศตะวันออก ในช่วงฤดูหนาวฝูงค้างคาวจะบินไปทางทิศตะวันตก<br><br><div align="center"><img border="0" width="400" height="561" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/%E0%B8%95%E0%B9%89%E0%B8%99%E0%B8%94%E0%B8%B4%E0%B8%81%E0%B9%80%E0%B8%94%E0%B8%B5%E0%B8%A2%E0%B8%A1%20%E0%B8%A7%E0%B8%B1%E0%B8%94%E0%B8%9B%E0%B8%A3%E0%B8%B2%E0%B8%87%E0%B8%84%E0%B9%8C.jpg" style="width: 400px; height: 561px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>4. ต้นดิกเดียม จังหวัดน่าน&nbsp;</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ต้นไม้อะไรใครรู้ดูประหลาดผิดธรรมชาติ พันธุ์พฤกษาน่าฉงน แค่เห็นเป็นต้นไม้หันหลังให้แดดหันหน้าเข้าวัดก็แปลกเหลือหลายอยู่แล้ว แต่ใครจะเชื่อว่าต้นไม้ประหลาดต้นนี้เป็นต้นอารมณ์ขัน ใบไม้จะไหวสั่นทุกครั้งที่ถูกคนสัมผัส ^^ ทั้งนี้ ต้นดิกเดียม เป็นต้นไม้ใหญ่ มีดอกเป็นสีเหลืองอ่อน โคนดอกจะเป็นสีเหลืองแก่ ลักษณะคล้ายกับดอกลั่นทม ปลูกอยู่หน้าวิหารวัดปรางค์ อำเภอปัว จังหวัดน่าน สามารถไปชมได้ทุกวัน แต่ไม่ควรไปลูบคลำเนื่องจากในประเทศไทยมีอยู่ต้นเดียว เจ้าอาวาสที่วัดท่านจะลูบให้ดู<br><br><div align="center"><img border="0" width="320" height="240" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/128.jpg" style="width: 320px; height: 240px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>5. กุ้งเดินขบวน จังหวัดอุบลราชธานี</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ทุกปีช่วงปลายเดือนสิงหาคมถึงกลางเดือนกันยายน ณ บริเวณลานพันรู น้ำตกแก่งลำดวน ในพื้นที่ดูแลรับผิดชอบของสถานีพัฒนาและส่งเสริมการอนุรักษ์สัตว์ป่าอุบลราชธานี ตั้งอยู่ที่ หมู่ 5 บ้านหนองขอน ตำบลโดมประดิษฐ์ อำเภอน้ำยืน จะเกิดปรากฏเหตุการณ์ที่แสนมหัศจรรย์ เมื่อบรรดา กุ้งน้ำจืด (กุ้งฝอย) นับล้านตัว พากันพร้อมใจเดินพาเหรดผ่านลานหินเลียบแก่งน้ำมุ่งหน้าสู่ยอดเขาสูงบนเทือกเขาพนมดงรัก ในเวลากลางคืน เวลาประมาณ 19.00 - 04.00 น. ของอีกวันหนึ่ง ซึ่งการที่กุ้งเหล่านี้มาเดินบนพลาญหินบริเวณลานพันรู มีสาเหตุมาจากการที่จะเดินทางไปยังแหล่งต้นน้ำที่เป็นบ้านเกิดบนเทือกเขาพนมดงรัก ต้องฝ่าฟันอุปสรรค์ต่าง ๆ มากมายจนกลายเป็นเหตุการณ์มหัศจรรย์ของธรรมชาติ<br><br><div align="center"><img border="0" width="400" height="554" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/j1.jpg" style="width: 400px; height: 554px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>6. โบสถ์ในต้นไม้ จังหวัดสมุทรสงคราม</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ภายในวัดบางกุ้ง ตำบลบางกุ้ง อำเภอบางคนที มีสิ่งที่ทำให้อัศจรรย์ใจอยู่ นั่นคือ โบสถ์ของวัดถูกปกคลุมด้วยต้นไม้ขนาดใหญ่ ทำให้ได้รับการยกย่องให้เป็นหนึ่งใน Unseen Thailand&nbsp; หรือที่รู้จักกันว่า "โบสถ์ในต้นไม้" หรือ "โบสถ์ปรกโพธ์" ซึ่งในโบสถ์เก่ายังประดิษฐาน "หลวงพ่อพุทธมณีนิล" หรือที่ชาวบ้านเรียก "หลวงพ่อดำ" เป็นพระพุทธรูปปางมารวิชัยขนาดใหญ่ สมัยอยุธยาตอนปลาย เป็นที่เคารพบูชาของคนในท้องถิ่น และมีภาพจิตรกรรมฝาผนังสมัยปลายกรุงศรีอยุธยาแสดงเรื่องราวเกี่ยวกับพุทธประวัติ เป็นภาพพระพุทธเจ้าทรงแสดงธรรม และภาพพระพุทธเจ้าประทับนั่งอยู่ในซุ้มขนาบข้างด้วยอัครสาวกนั่งพนมมือ&nbsp;<br><br><div align="center"><img border="0" width="600" height="333" alt="พระเจ้านั่งดิน" title="พระเจ้านั่งดิน" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/001_2_d.jpg" style="width: 600px; height: 333px; "><br></div><span style="color: rgb(0, 0, 255); "><b><br><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>7. พระเจ้านั่งดิน จังหวัดพะเยา</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; หากมีโอกาสไปเที่ยวจังหวัดพะเยา ไม่ควรพลาดไปสักการะ "พระเจ้านั่งดิน" วัดพระนั่งดิน ตำบลเวียง อำเภอเชียงคำ เพราะเป็นพระพุทธเก่าแก่ สร้างตั้งแต่ครั้งสมเด็จพระสัมมาสัมพุทธเจ้ายังทรงพระชนม์ชีพอยู่ โดยมีตำนานเล่าว่า ในอดีตเคยมีชาวบ้านได้พากันสร้างฐานชุกชีแล้วได้อันเชิญพระเจ้านั่งดินขึ้นประทับ แต่ได้เกิดเหตุอัศจรรย์ฟ้าได้ผ่าลงมาที่กลางพระวิหารถึง 3 ครั้ง ดังนั้น ชาวบ้านจึงอาราธนาพระเจ้านั่งดินมาประดิษฐานบนพื้นดินดังเดิมตราบจนทุกวันนี้ และนั่นจึงเป็นที่มาของชื่อ "พระเจ้านั่งดิน"<br><br><div align="center"><img border="0" width="400" height="292" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/uu.jpg" style="width: 400px; height: 292px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>8. เกาะกระดาด จังหวัดตราด</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; เกาะกระดาด ตั้งอยู่ทางทิศตะวันออกเฉียงเหนือของเกาะหมาก มีพื้นที่ 1,200 ไร่ เป็นเกาะกลางทะเลกว้างที่มีสัณฐานแบนราบราวกับแผ่นกระดาษ มีต้นกระดาดขึ้นอยู่เป็นจำนวนมาก จึงได้ชื่อว่า "เกาะกระดาด"&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ซึ่งนับเป็นเกาะเดียวในประเทศไทยที่มีการออกโฉนดถูกต้องตามกฏหมาย ตั้งแต่สมัยรัชกาลที่ 5 เนื่องจากฝรั่งเศสได้เข้ามาล่าอาณานิคมในแถบเอเชียอาคเนย์ และพยายามยึดครองดินแดนของไทย เกาะกระดาด ก็เป็นที่หมายหนึ่งของฝรั่งเศสด้วย รัชกาลที่ 5 จึงทรงพระกรุณาโปรดเกล้าฯ ให้ออกโฉนดที่ดินของเกาะ อีกทั้งยังมีหาดทรายยาวขาวสะอาด มีแนวปะการังที่สวยงามตลอดชายฝั่ง จนได้รับการคัดเลือกให้เป็นหนึ่งใน Unseen Thailand ทั้งนี้ ช่วงเวลาที่เหมาะท่องเที่ยว คือ ช่วงเดือนตุลาคมถึงพฤษภาคม เพราะมีคลื่นลมน้อยที่สุด&nbsp;<br><br><div align="center"><img border="0" width="600" height="450" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/ds.jpg" style="width: 600px; height: 450px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>9. ผจญภัยสุดขอบฟ้าที่เทือกเขาโมโกจู จังหวัดกำแพงเพชร</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "โมโกจู" เป็นยอดเขาที่สูงที่สุดในอุทยานแห่งชาติแม่วงก์ และสูงที่สุดในผืนป่าตะวันตก เป็นยอดเขาที่นักนิยมการท่องเที่ยวแบบเดินป่า ปีนเขา ต้องการที่จะไปเยือนสักครั้ง ด้วยความสูง 1,964 เมตร โดยคำว่า "โมโกจู" เป็นภาษากะเหรี่ยง แปลว่า เหมือนฝนจะตก เนื่องจากบนยอดเขามักถูกปกคลุมด้วยเมฆหมอกและมีอากาศหนาวเย็นตลอดเวลา&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ทั้งนี้ ผู้สนใจจะไปสัมผัสยอดเขาโมโกจูต้องเตรียมร่างกายให้แข็งแรง เพราะทางเดินขึ้นเขามีความลาดชันไม่ต่ำกว่า 60 องศา ใช้เวลาในการเดินทางไป-กลับ 5 วัน อีกทั้งยังต้องพักแรมในป่าตามจุดที่กำหนด นอกจากนั้นควรศึกษาสภาพเส้นทาง สภาพอากาศ และติดต่อเจ้าหน้าที่นำทางจากอุทยานฯ ซึ่งเปิดให้เดินขึ้นยอดเขาโมโกจูในเดือนพฤศจิกายน-กุมภาพันธ์ของทุกปี<br><br><div align="center"><img border="0" width="456" height="327" title="สถานที่ท่องเที่ยวในประเทศไทย" alt="สถานที่ท่องเที่ยวในประเทศไทย" class="img-mobile" src="http://img.kapook.com/u/sutasinee/travel/ttt/gr03_ne03.jpg" style="width: 456px; height: 327px; "><br></div><br><span style="color: rgb(0, 0, 255); "><b><img border="0" width="25" height="25" alt="" src="http://img.kapook.com/image/icon/cute-rabbit-emoticon-018.gif" style="width: 25px; height: 25px; "></b></span><span style="color: rgb(0, 0, 255); "><b>10. สุริยปฏิทินแห่งสยามประเทศ ปราสาทภูเพ็ก จังหวัดสกลนคร</b></span><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ยามแสงเรื่อเรืองอาบไล้ปราสาทหินเก่าแก่ที่อวลด้วยกลิ่นอายศิลปะขอม ไม่ใช่เพียงแค่ความงดงามศักดิ์สิทธิ์ หากยังปรากฏความอัศจรรย์ลงบนแท่งหินอายุนับ 1,000 ปี ที่เป็นเครื่องบ่งบอกคืนวันและฤกษ์ยามในการดำเนินชีวิตของบรรรพชนที่ผูกร้อยอยู่กับดวงตะวัน นั่นคือ สุริยปฏิทิน ณ ปราสาทภูเพ็ก อำเภอพรรณนานิคม จังหวัดสกลนคร ศาสตร์โบราณในการคำนวณดาราศาสตร์ที่ปรากฏบนแท่งหินพันปี ขนาด 56 X 56 เซนติเมตร สูง 60 เซนติเมตร ด้านบนมีการแกะสลักเป็นรูปสี่เหลี่ยมเรียงรอบเป็นรูปทรงเรขาคณิตจำนวน 16 ช่อง ใช้ตามติดตามตำแหน่งของดวงอาทิตย์ จากทิศทางของแสง จะได้ทราบฤดูกาลและกำหนดเวลาทำการเกษตร การก่อสร้างได้&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ทั้งนี้ คุณสรรค์สนธิ บุณโยทยาน นักพิภพวิทยา ผู้ซึ่งสนใจศึกษาการใช้แท่งหินนี้ และนำเทคโนโลยีคอมพิวเตอร์มาประมวลผลด้านดาราศาสตร์ พบว่า เมื่อใส่ข้อมูลพิกัดตำแหน่งของปราสาท เส้นรุ้งที่ 17 องศาเหนือ และใส่วันที่ปรากฏการณ์สำคัญต่าง ๆ จะได้มุมกวาดเป็น 65, 90 และ 115 องศา ซึ่งเป็นตำแหน่งดวงอาทิตย์ขึ้นหมุนเวียน โดยในวันเริ่มต้นของปี จะเป็นวันที่เวลากลางวันจะเท่ากับเวลากลางคืน ดวงอาทิตย์ทำมุมฉากกับเส้นศูนย์สูตรของโลก ตรงกับวันที่ 21 มีนาคม เป็นฤดูใบไม้ผลิ หลังจากนั้นดวงอาทิตย์จะค่อย ๆ เคลื่อนไปทางทิศเหนือ จนถึงจุดที่ไกลที่สุด ซึ่งจะเป็นวันที่กลางวันจะยาวที่สุด ตรงกับวันที่ 21 มิถุนายน จัดว่าเป็นฤดูร้อน จังหวัดสกลนครอยู่เส้นรุ้ง 17 องศาเหนือ กลางวันจะมี 13 ชั่วโมง ขณะเดียวกันเมื่อดวงอาทิตย์เคลื่อนมาถึงจุดที่ไกลที่สุดทางทิศใต้ ช่วงกลางวันจะสั้นที่สุดในรอบปี ตรงกับวันที่ 21 ธันวาคม ซึ่งเป็นช่วงฤดูหนาว จังหวัดสกลนครจะมี 11 ชั่วโมง ได้มีการทดสอบแนวแสงอาทิตย์ด้วยลูกดิ่งหลายครั้งพบว่า แนวแสงจะพาดผ่านช่องสี่เหลี่ยมช่องใดช่องหนึ่งพอดี เมื่อตรงกับวันที่ดังกล่าวข้างต้น<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; สำหรับการสร้างปราสาทหินภูเพ็ก สันนิษฐานว่าสร้างขึ้นในยุคสมัยของพระเจ้าชัยวรมันที่ 7 ประมาณปี พ.ศ. 1742 สร้างจากก้อนหินทรายทรงสี่เหลี่ยม วางทำมุมฉากตามแนวทิศตะวันออก-ตก มีศิวลึงค์อยู่ด้านตะวันออก โดยมีด้านเปิดให้แสงอาทิตย์เข้าไปเพียงด้านเดียว ให้แสงสาดตรงที่ ณ ตำแหน่งของสิ่งศักดิ์สิทธิ์ภายในปราสาท ซึ่งแสดงถึงศาสตร์แห่งสุริยปฏิทินของขอมโบราณ&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<b>และนี่คือ 10 สถานที่สุด Unseen Thailand ที่เราหยิบมาแนะนำกัน หากใครมีโอกาสก็ลองไปสัมผัสกันดูจ้า อะ ๆ แต่จริง ๆ แล้วสถานที่ท่องเที่ยวในประเทศไทยสวย ๆ งาม ๆ ยังมีอยู่มากมาย ใครสะดวกที่ไหนก็ไม่ควรพลาด!</b></span>', '2012821204406-1_50_1345027910.jpg', 1, '2012-08-21 20:44:06', 0);
INSERT INTO `post` VALUES (22, 10, 'Reflection Of Thailand สะท้อนมุมมองความเป็นไทย', 'ท่องเที่ยวไทย ภาพถ่าย Reflection Of Thailand สะท้อนมุมมองความเป็นไทย', '<span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); "><div align="center"><img border="0" width="600" height="451" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-0.jpg" style="width: 600px; height: 451px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-14.jpg" style="width: 600px; height: 449px; "></div><div align="center"><br></div></span><span class="Apple-style-span" style="font-family: Tahoma; font-size: 16px; background-color: rgb(255, 255, 255); ">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<b><span style="color: rgb(0, 0, 128); ">เมื่อกาลเวลาไม่เคยหยุดนิ่ง แน่นอนทุก ๆ สิ่งต้องมีการเปลี่ยนแปลง ถ้าหากให้กล่าวถึงประเทศไทย ถึงแม้ว่าทุกวันนี้จะมีการเปลี่ยนแปลงเกิดขึ้นตลอดเวลา ทั้งทางด้านสังคม หรือเทคโนโลยี แต่สิ่งที่ไม่เคยเปลี่ยนเลยก็คือ... เรื่องราวของวัฒนธรรม ศาสนา และพระมหากษัตริย์ ที่ยั่งยืนอยู่คู่ความศิวิไลซ์ของบ้านเรามาช้านาน</span></b><br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ในวันนี้กระปุกดอทคอมขอนำภาพสวย ๆ ของ&nbsp;<a target="_blank" href="http://travel.kapook.com/www.facebook.com/bonzai.pix" style="text-decoration: none; ">คุณสันต์ทัศน์ บุญสรรค์สร้าง (Bonzai)</a>&nbsp;ในภาพชุด&nbsp;<span style="color: rgb(128, 0, 128); "><b>"Reflection of Thailand"</b></span>&nbsp;ซึ่งเขาได้ถ่ายทอดเรื่องราวผ่านมุมมองดี ๆ ที่สะท้อนให้เราได้เห็นว่า...&nbsp;<span style="color: rgb(128, 0, 128); ">ไม่ว่าสภาพแวดล้อมจะเปลี่ยนแปลงไปแค่ไหน แต่ความเป็นไทยก็ยังฝั่งลึกอยู่ในใจของทุกคนแน่นอน<br></span><br><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-1.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="451" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-2.jpg" style="width: 600px; height: 451px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-3.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="451" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-8.jpg" style="width: 600px; height: 451px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-15.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="353" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-16.jpg" style="width: 600px; height: 353px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-17.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="451" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-18.jpg" style="width: 600px; height: 451px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="style2" src="http://img.kapook.com/u/fontip/Travel/O12477167-19.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="451" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-20.jpg" style="width: 600px; height: 451px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-21.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-23.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-24.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-25.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="449" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-26.jpg" style="width: 600px; height: 449px; "><br></div><br><div align="center"><img border="0" width="600" height="353" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-27.jpg" style="width: 600px; height: 353px; "><br></div><br><div align="center"><img border="0" width="500" height="807" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-28.jpg" style="width: 500px; height: 807px; "><br></div><br><div align="center"><img border="0" width="500" height="808" alt="Reflection Of Thailand สะท้อนมุมมองความเป็นไทย" class="img-mobile" src="http://img.kapook.com/u/fontip/Travel/O12477167-22.jpg" style="width: 500px; height: 808px; "></div></span>', '2012821204644-6_27_1345520253.jpg', 1, '2012-08-21 20:46:44', 1);
INSERT INTO `post` VALUES (23, 12, 'วลีประหลาด มึงไทยมาก ความหมายคืออะไร', 'ติดแท็กกันเต็มไปหมด กับวลีฮิต มึงไทยมาก ที่จู่ ๆ ก็ระบาดว่อนโลกไซเบอร์เมืองไทยเมื่อสัปดาห์ที่ผ่านมา ทำเอาชาวเน็ตงงสุด ๆ ว่า วลีประหลาด ๆ นี้มีความหมายว่าอย่างไร', '<span class="Apple-style-span" style="font-family: Tahoma; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img src="http://news.impaqmsn.com/imagelib/082012/7448005701d35f7.jpg" alt="" style="border-top-style: none !important; border-right-style: none !important; border-bottom-style: none !important; border-left-style: none !important; border-width: initial !important; border-color: initial !important; "></div><p>ติดแท็กกันเต็มไปหมด กับวลีฮิต “มึงไทยมาก” ที่จู่ ๆ ก็ระบาดว่อนโลกไซเบอร์เมืองไทยเมื่อสัปดาห์ที่ผ่านมา ทำเอาชาวเน็ตงงสุด ๆ ว่า วลีประหลาด ๆ นี้มีความหมายว่าอย่างไร</p><p>แต่เพื่อความอินเทรนด์ก็เลยขอติดแท็ก “มึงไทยมาก” ใช้ตาม ๆ เขาไปบ้าง ทั้งที่ไม่รู้เหมือนกันว่า คำนี้มันคืออะไรกันแน่<br>&nbsp;<br>หลังจากแท็กนี้ผุดขึ้นมาเป็นดอกเห็ด มีชาวเน็ตหลายคนวิพากษ์วิจารณ์กันตามเว็บบอร์ด โดยมองว่า “มึงไทยมาก” ท่าทางจะไม่ใช่คำชมเสียแล้วล่ะ น่าจะเป็นคำต่อว่า หรือคำดูถูกมากกว่า อารมณ์คล้าย ๆ กับที่เราไปหยิบชื่อประเทศเพื่อนบ้านมาใช้เสียดสีคนอื่นนั่นแหละ<br>&nbsp;<br>มีชาวไทยคนหนึ่งอธิบายในโลกออนไลน์ว่า “มึงไทยมาก” คือคำด่าเก๋ ๆ ที่กำลังมาแรงมากในหมู่ชาวฟิลิปปินส์ ใช้สำหรับเปรียบเปรยแนวเสียดสีคนไทย</p><p>โดยเขาได้ไปอ่านเจอข้อความที่ชาวฟิลิปปินส์เขียนต่อว่าประเทศไทยไว้ 15 ข้อ ในเพจที่อ้างว่าเป็นของประชาคมอาเซียน หรือ AEC ซึ่งแต่ละข้อล้วนเจ็บจี๊ดมาก ๆ และได้นำข้อความเหล่านี้มาสรุปเป็นวลีดังกล่าว<br>&nbsp;<br>ข้อความที่อ้างว่า ชาวฟิลิปปินส์เป็นคนเขียนต่อว่าประเทศไทย และถูกแชร์ต่อกันนั้น มีดังนี้<br>&nbsp;<br>Let me tell you 15 reasons why Thais sucks!!!!! (ขอบอก 15 เหตุผล ที่ทำไมคนไทยถึงห่วยแตก)<br>&nbsp;<br>1. They′re bad at english. They′re terrible!! (คนไทยไม่เก่งภาษาอังกฤษ เรียกได้ว่าแย่มาก ๆ เลยล่ะ)<br>&nbsp;<br>2. They think their country is better than other countries (or best in the world) (คนไทยคิดว่าประเทศของตัวเองดีกว่าประเทศอื่น ๆ หรือ ดีที่สุดในโลก)<br>&nbsp;<br>3. They use Thai language on global websites because they′re too dumb and they have no manners. (คนไทยใช้ภาษาไทยในเว็บไซต์ระดับโลก เพราะพวกเขาโง่เกินกว่าจะเข้าใจ และไม่มีมารยาทด้วย)<br>&nbsp;<br>4. They′re Steve Jobs′ slaves. (คนไทยเป็นทาสของ สตีฟ จ็อบส์)<br>&nbsp;<br>5. They′re retarded. very close to the north korean level. (ประเทศของเขาไม่พัฒนาไปไหน ใกล้จะเป็นเหมือนประเทศเกาหลีเหนือไปแล้วทุกที)<br>&nbsp;<br>6. They use pirated softwares. (คนไทยใช้ซอฟท์แวร์ละเมิดลิขสิทธิ์)<br>&nbsp;<br>7. They hate democracy. They don′t even have it right now. (คนไทยเกลียดประชาธิปไตย และแม้แต่ตอนนี้เขาเองก็ยังไม่มีประชาธิปไตยเลย)<br>&nbsp;<br>8. Feudalism!! (พวกเขายังยึดถือระบบเจ้าขุนมูลนาย)</p><p>9. They snub other countries in ASEAN. especially Laos and Cambodia. They think their country is better… no They′re not. (คนไทยดูแคลนประเทศอื่นในอาเซียน โดยเฉพาะ ลาว และ กัมพูชา พวกเขาคิดว่าประเทศตัวเองดีกว่าประเทศอื่น แต่มันไม่ใช่เลย)<br>&nbsp;<br>10. Faith is bigger than reasons. (คนไทยมองว่า ความจริงสำคัญกว่าเหตุผล)<br>&nbsp;<br>11. They destroyed Buddhist. (คนไทยทำลายล้างพระพุทธศาสนา)<br>&nbsp;<br>12. They′re hypocrites. Their country is one of the biggest sex market in the world, They still refuse to admit it. (คนไทยยังคงหลอกตัวเอง ทั้ง ๆ ที่ ประเทศของเขาเป็นตลาดค้ามนุษย์ที่ใหญ่ที่สุดในโลก แต่กลับไม่ยอมรับ)<br>&nbsp;<br>13. Their football team sucks!!! (ฟุตบอลทีมชาติไทยเป็นทีมที่ห่วยแตกมาก)</p><p>14. Their gov killed their own citizens!! (รัฐบาลของเขา ฆ่าประชาชนของตัวเอง)<br>&nbsp;<br>15. ………..<br>&nbsp;</p><p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: 0pt; margin-left: 0in; "><span style="font-family: ''Cordia New'', sans-serif; color: rgb(31, 73, 125); font-size: 14pt; ">ข้อมูลโดย</span><span style="color: rgb(31, 73, 125); "><span style="font-size: 12pt; "><span style="font-family: Calibri; ">&nbsp;:&nbsp;&nbsp;</span></span></span><a href="http://www.talkystory.com/" target="_blank" style="text-decoration: none; background-color: transparent; color: rgb(7, 81, 154); "><span style="font-family: ''MS Sans Serif''; color: blue; font-size: 10pt; "><u>www.talkystory.com/</u></span></a></p></span>', '2012822100755-7448005701d35f7.jpg', 2, '2012-08-22 10:07:55', 2);
INSERT INTO `post` VALUES (24, 12, 'แค้นน้องสะใภ้ จับหลานโยนตึกดับ', 'ที่เมืองจ้านเจียง มณฑลกวางตุ้งทางตอนใต้ของจีน เกิดเหตุการณ์ระทึกเมื่อ นางเซิงเฟ่ย สาวใหญ่ชาวจีนรายนี้ กำลังพยายามโดดลงจากตึก 9 เนื่องจากก่อนหน้านี้ มีปากเสียงกับน้องสะใภ้อย่างรุนแรง', '<span class="Apple-style-span" style="font-family: Tahoma; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img src="http://news.impaqmsn.com/imagelib/082012/82322bbe3f366ad.jpg" alt="" style="border-top-style: none !important; border-right-style: none !important; border-bottom-style: none !important; border-left-style: none !important; border-width: initial !important; border-color: initial !important; "></div><p>ที่เมืองจ้านเจียง มณฑลกวางตุ้งทางตอนใต้ของจีน เกิดเหตุการณ์ระทึกเมื่อ นางเซิงเฟ่ย สาวใหญ่ชาวจีนรายนี้ กำลังพยายามโดดลงจากตึก 9 เนื่องจากก่อนหน้านี้ มีปากเสียงกับน้องสะใภ้อย่างรุนแรง</p><p>จึงเกิดบันดาลโทสะ บังคับหลานชายวัย 4 ขวบ ซึ่งเป็นลูกชายของน้องสะใภ้ขึ้นไปที่บนดาดฟ้า และโยนเด็กคนดังกล่าวลงมาจากตึกจนถึงแก่ความตายเพื่อเป็นการแก้แค้น</p><p>ขณะที่เธอ พยายามกระโดดลงมาจากตึกเช่นกัน แต่เจ้าหน้าที่และญาติต้องช่วยกันดึงจับร่างของเธอเอาไว้ โดยตำรวจได้ตั้งข้อหาฆาตกรรม หลังจากที่เธอสารภาพได้โยนหลานวัย 4 ขวบลงมาจริง</p><p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: 0pt; margin-left: 0in; "><span style="font-family: ''Cordia New'', sans-serif; color: rgb(31, 73, 125); font-size: 14pt; ">ข้อมูลโดย</span><span style="color: rgb(31, 73, 125); "><span style="font-size: 12pt; "><span style="font-family: Calibri; ">&nbsp;:&nbsp;&nbsp;</span></span></span><a href="http://www.talkystory.com/" target="_blank" style="text-decoration: none; background-color: transparent; color: rgb(7, 81, 154); "><span style="font-family: ''MS Sans Serif''; color: blue; font-size: 10pt; "><u>www.talkystory.com/</u></span></a></p></span>', '2012822100929-82322bbe3f366ad.jpg', 1, '2012-08-22 10:09:29', 0);
INSERT INTO `post` VALUES (25, 12, 'คนค้านปรองดองกลัวผลเลือกตั้ง', 'สำนักข่าว โกเรียจุนยาง เดลี่ ลงบทสัมภาษณ์พิเศษ พ.ต.ท.ทักษิณ ชิณวัตร ที่เผยกับ น.ส.พ.จุนยางอิลโบ และ สำนักข่าว เจทีบีซี เมื่อวันเสาร์ที่ผ่านมา', '<span class="Apple-style-span" style="font-family: Tahoma; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img src="http://news.impaqmsn.com/imagelib/082012/140b7a7db144a12.jpg" alt="" style="border-top-style: none !important; border-right-style: none !important; border-bottom-style: none !important; border-left-style: none !important; border-width: initial !important; border-color: initial !important; "></div><p>สำนักข่าว โกเรียจุนยาง เดลี่ ลงบทสัมภาษณ์พิเศษ พ.ต.ท.ทักษิณ ชิณวัตร ที่เผยกับ น.ส.พ.จุนยางอิลโบ และ สำนักข่าว เจทีบีซี เมื่อวันเสาร์ที่ผ่านมา&nbsp; โดยผู้สื่อข่าวได้สัมภาษณ์ในประเด็นต่าง ๆ ซึ่งคำถามแรก ถามถึง 7 ปี ในการลี้ภัยอยู่นอกประเทศไทย โดย พ.ต.ท.ทักษิณ เผยว่า ปีแรก ๆ รู้สึกได้ไม่ดีจริง ๆ แต่พอเข้าปี 2 และ 3 เริ่มที่จะปรับตัวได้ และตอนนี้ยังรู้สึกขอบคุณสำหรับโอกาสที่ได้เรียนรู้เพิ่มเติมเกี่ยวกับโลกมากขึ้น&nbsp; แต่ก็คิดถึงครอบครัวและบ้าน ต้องการที่จะนอนหลับอยู่บนเตียงของตัวเองเร็ว ๆ นี้&nbsp; ทั้งนี้ได้ไปเยือนสหรัฐ<br>และได้พูดคุยกับ ดร.เฮนรี คิสซิงเจอร์ อดีต รมต.ต่างประเทศสหรัฐ เกี่ยวกับนโยบายของสหรัฐในเอเชียด้วย</p><p>นักข่าว : ทำไมถึงเยือนเกาหลีใต้ ครั้งที่ 3 ในปีนี้&nbsp;<br>พ.ต.ท.ทักษิณ : ต้องการที่จะเรียนรู้เพิ่มเติมเกี่ยวกับประเทศเกาหลี โดยเฉพาะอย่างยิ่งเกี่ยวกับโครงการฟื้นฟูแม่น้ำ เพราะมีความคล้ายคลึงกันในการวางแผนการจัดการน้ำ หลายประเทศรวมทั้งเกาหลีและจีน เป็นผู้ชนะในการประมูลที่สนใจสำหรับโครงการก่อสร้างในประเทศไทย</p><p>นักข่าว : คุณไม่เป็นหนี้ คุณประสบความสำเร็จทางการเมืองประชาธิปไตยนโยบาย<br>พ.ต.ท.ทักษิณ : ผมเป็นคนใจกว้างมากที่จะจัดสรรงบประมาณเพื่อกระตุ้นเศรษฐกิจจากด้านล่างขึ้น ผมไม่ได้จ้างนโยบายประชานิยม ทุนนิยมเป็นสิ่งที่ดี จริงอยู่อาจไม่ช่วยได้ทั้งหมด แต่ก็นั่นเป็นเหตุผลที่พวกเขาต้องการให้ผมกลับ</p><p>นักข่าว : คุณยังคงนโยบายประชานิยม เช่น 30 บาท ในการดูแลทางการแพทย์<br>พ.ต.ท.ทักษิณ : ให้โอกาสสำหรับคนยากจน ก็เหมือนเติมพลังให้ร่างกายของคุณ คุณจะแข็งแรง คุณจะต้องกระตุ้นเซลล์ที่ตาย นั่นคือสิ่งที่ผมพยายามทำ ผมเป็นเหยื่อทางการเมือง เพราะว่าเป็นคนที่ผลักดันเพื่อประชาธิปไตย เป็นตัวแทนของชนชั้นแรงงาน คนพื้นที่ชนบท</p><p>นักข่าว : เกี่ยวข้องอย่างยิ่งกับข้อกล่าวหาการทุจริต<br>พ.ต.ท.ทักษิณ : ปีที่กล่าวหา เป็นที่นิยมมากในทุกความขัดแย้งที่จะโจมตีด้านอื่น ๆ การยึดทรัพย์สินของผม คือเป้าหมายระยะสั้น พวกเขาควรจะดำเนินการทางอาญาครั้งแรก พวกเขาเพียงแค่เอาเงินไปก่อน ไม่ว่าถูกหรือผิด</p><p>นักข่าว : น้องสาวของคุณ ได้เสนอ พ.ร.บ.ปรองดองแห่งชาติ ที่จะช่วยให้คุณสามารถกลับบ้านได้เร็วขึ้น<br>พ.ต.ท.ทักษิณ : พ.ร.บ.เป็นยาสำหรับความโชคร้ายที่เจ็บปวดที่ผ่านมา เรานำทั้ง 2 ฝ่าย ร่วมกันหารือ ซึ่งวัตถุประสงค์นั้น ฝ่ายค้านกังวลมากเกินไปเกี่ยวกับการเลือกตั้งครั้งถัดไป&nbsp; ซึ่งวันหนึ่งพวกเขาจะเข้าใจ&nbsp; ในขณะที่ผมยังคงเดินทางอยู่บนอากาศ</p><p>นักข่าว : อะไรเป็นแรงบันดาลใจทางการเมืองของคุณ&nbsp;<br>พ.ต.ท.ทักษิณ : ผมต้องการที่จะสนับสนุนน้องสาว อย่างที่ มหาตมะ คานธี กล่าวว่า ความรุนแรงให้กำเนิดความรุนแรงมากขึ้น&nbsp; เมื่อคุณเป็นผู้นำ คุณก็จะเรียนรู้ที่จะให้อภัย กำปั้นเหล็กจะเล่นเกมกลยุทธ์ของฝ่ายค้าน ก็ถึงเวลาสำหรับการใช้ถุงมือกำมะหยี่</p><p>ทั้งนี้ ตามที่สื่อไทยรายงานจากการเปิดเผยของ นายนพดล ปัทมะ ที่ปรึกษาทางกฎหมายของ พ.ต.ท.ทักษิณ ระบุว่า หลังจากเยือน เกาหลีใต้แล้ว&nbsp; พ.ต.ท.ทักษิณ ก็เดินทางไปเยือนจีน ต่อไป</p><p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: 0pt; margin-left: 0in; "><span style="color: rgb(255, 0, 0); "><span style="color: rgb(51, 51, 51); font-family: ''Microsoft Sans Serif''; font-size: 14pt; "><a href="http://www.innnews.co.th/" target="_blank" style="text-decoration: none; background-color: transparent; color: rgb(7, 81, 154); ">INNNEWS.CO.TH</a></span></span></p></span>', '2012822101055-140b7a7db144a12.jpg', 1, '2012-08-22 10:10:55', 1);
INSERT INTO `post` VALUES (26, 12, 'หมื่นคันต่อวัน ขอคืนภาษีรถคันแรก', 'ขณะนี้ยอดการขอใช้สิทธิ์ในโครงการคืนภาษีรถยนต์คันแรกไม่เกิน 1 แสนบาท ได้ปรับเพิ่มขึ้นอย่างรวดเร็วจากสัปดาห์ละ 100 คัน เป็น 1,000 คัน', '<span class="Apple-style-span" style="font-family: Tahoma; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img src="http://news.impaqmsn.com/imagelib/082012/c510219f190bb36.jpg" alt="" style="border-top-style: none !important; border-right-style: none !important; border-bottom-style: none !important; border-left-style: none !important; border-width: initial !important; border-color: initial !important; "></div>&nbsp;<p>ขณะนี้ยอดการขอใช้สิทธิ์ในโครงการคืนภาษีรถยนต์คันแรกไม่เกิน 1 แสนบาท ได้ปรับเพิ่มขึ้นอย่างรวดเร็วจากสัปดาห์ละ 100 คัน เป็น 1,000 คัน</p><p>และล่าสุดเป็นสัปดาห์ละ 1 หมื่นคัน ทำให้ยอดการขอใช้สิทธิ์ในโครงการดังกล่าวสูงถึง 1.45 แสนคัน เป็นเงินภาษีที่ต้องจ่ายคืน 1 หมื่นล้านบาท</p><p>คาดว่าถึงสิ้นปีจะมีผู้ขอใช้สิทธิ์ไม่น้อยกว่า 5 แสนคัน เป็นเงินที่ต้องจ่ายคืนไม่น้อยกว่า 3 หมื่นล้านบาท</p><p>รถยนต์ที่ขอใช้สิทธิ์คันแรก ประกอบด้วย รถยนต์นั่งทั่วไป 8 หมื่นคัน เป็นเงินที่ต้อง 7,336 ล้านบาท รถกระบะ 3.2 หมื่นคัน เป็นเงินที่ต้องคืน 530 ล้านบาท</p><p>และรถยนต์นั่งมีกระบะ 3.16 หมื่นคัน เป็นเงินที่ต้องคืน 2,482 ล้านบาท ปีงบประมาณ พ.ศ. 2556 ของบเพื่อจ่ายคืนรถคันแรก 7,000 ล้านบาท หากไม่พอก็จะขอให้รัฐบาลจัดสรรงบกลางมาจ่ายไปก่อน</p><p>รถยนต์คันแรกที่จะได้สิทธิ์คืนเงิน จะต้องเป็นรถยนต์ที่มีการประกอบการและขายได้จริง ค่ายรถยนต์ไม่สามารถไปนำรถยนต์ต้นแบบมาให้เปิดจอง โดยที่ยังไม่มีการผลิตเพื่อใช้สิทธิ์คันแรกได้</p><p>กระทรวงการคลังยังเตรียมเสนอ ครม. พิจารณาต่ออายุมาตรการลดการจัดเก็บภาษีสรรพสามิตน้ำมันดีเซลออกไปอีก 1 เดือน จากเดิมสิ้นสุดวันที่ 31 สิงหาคมนี้ เป็นวันที่ 30 กันยายน 2555</p><p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: 0pt; margin-left: 0in; "><span style="font-family: ''Cordia New'', sans-serif; color: rgb(31, 73, 125); font-size: 14pt; ">ข้อมูลโดย</span><span style="color: rgb(31, 73, 125); "><span style="font-size: 12pt; "><span style="font-family: Calibri; ">&nbsp;:&nbsp;&nbsp;</span></span></span><a href="http://www.talkystory.com/" target="_blank" style="text-decoration: none; background-color: transparent; color: rgb(7, 81, 154); "><span style="font-family: ''MS Sans Serif''; color: blue; font-size: 10pt; "><u>www.talkystory.com/</u></span></a></p></span>', '2012822101223-c510219f190bb36.jpg', 1, '2012-08-22 10:12:23', 0);
INSERT INTO `post` VALUES (27, 12, 'นายกฯ เมินละเว้นจับทักษิณ ย้ำยึดกฎหมาย', 'น.ส.ยิ่งลักษณ์ ชินวัตร นายกรัฐมนตรี กล่าวถึงกรณีที่กลุ่มกรีน เตรียมยื่น ป.ป.ช. กรณีละเว้นการปฏิบัติหน้าที่ตามมาตรา 157 ที่ไม่สั่งการให้เจ้าหน้าที่ดำเนินการจับกุม พ.ต.ท.ทักษิณ ชินวัตร อดีตนายกรัฐมนตรี มาลงโทษ ', '<span class="Apple-style-span" style="font-family: Tahoma; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img src="http://news.impaqmsn.com/imagelib/082012/77bcc4d3e5b4e05.jpg" alt="" style="border-top-style: none !important; border-right-style: none !important; border-bottom-style: none !important; border-left-style: none !important; border-width: initial !important; border-color: initial !important; "></div><p>น.ส.ยิ่งลักษณ์ ชินวัตร นายกรัฐมนตรี กล่าวถึงกรณีที่กลุ่มกรีน เตรียมยื่น ป.ป.ช. กรณีละเว้นการปฏิบัติหน้าที่ตามมาตรา 157 ที่ไม่สั่งการให้เจ้าหน้าที่ดำเนินการจับกุม พ.ต.ท.ทักษิณ ชินวัตร อดีตนายกรัฐมนตรี มาลงโทษ ว่า</p><p>ให้เป็นไปตามขั้นตอนของกฎหมาย ส่วนการแถลงผลงานของรัฐบาลอยู่ระหว่างการรวบรวมรายละเอียด โดยสภาที่ปรึกษาเศรษฐกิจและสังคมแห่งชาติ ก่อนจัดทำเป็นรูปเล่ม เพื่อเสนอต่อรัฐสภา ส่วนการจะแถลงนโยบายเมื่อไหร่ จะแจ้งให้ทราบอีกครั้ง</p><p>ทั้งนี้ ในวันที่ 25 - 26 ส.ค. นี้ จะเป็นการแถลง โดย นายทนุศักดิ์ เล็กอุทัย รัฐมนตรีช่วยว่าการกระทรวงการคลัง เพื่อชี้แจงการเข้าถึงแหล่งเงินทุนในโครงการต่างๆ ของรัฐบาล เพื่อให้ประชาชนและการนำไปใช้ให้ตรงตามวัตถุประสงค์ และเป็นไปตามระเบียบ ซึ่งที่ผ่านมารัฐบาลได้ชี้แจงมาโดยตลอด</p><p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: 0pt; margin-left: 0in; "><span style="color: rgb(255, 0, 0); "><span style="color: rgb(51, 51, 51); font-family: ''Microsoft Sans Serif''; font-size: 14pt; "><a href="http://www.innnews.co.th/" target="_blank" style="text-decoration: none; background-color: transparent; color: rgb(7, 81, 154); ">INNNEWS.CO.TH</a></span></span></p></span>', '2012822101335-77bcc4d3e5b4e05.jpg', 1, '2012-08-22 10:13:35', 0);
INSERT INTO `post` VALUES (28, 12, '6 กองร้อย ดูแลศาลถอนประกัน 18 แกนนำแดง', 'พล.ต.ต.ปริญญา จันทร์สุริยา รองผู้บัญชาการตำรวจนคราบาล กล่าวถึงมาตรการดูแลความสงบเรียบร้อยและความปลอดภัยศาลอาญา ในวันที่ 22 ส.ค.นี้ ที่จะมีการพิจารญาถอนประกัน 18 แกนนำเสื้อแดง', '<span class="Apple-style-span" style="font-family: Tahoma; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img src="http://news.impaqmsn.com/imagelib/082012/2ff2943fc45e8bc.jpg" alt="" style="border-top-style: none !important; border-right-style: none !important; border-bottom-style: none !important; border-left-style: none !important; border-width: initial !important; border-color: initial !important; "></div><p>พล.ต.ต.ปริญญา จันทร์สุริยา รองผู้บัญชาการตำรวจนคราบาล กล่าวถึงมาตรการดูแลความสงบเรียบร้อยและความปลอดภัยศาลอาญา ในวันที่ 22 ส.ค.นี้ ที่จะมีการพิจารญาถอนประกัน 18 แกนนำเสื้อแดง ว่า</p><p>ได้สั่งการให้จัดกำลังกองร้อยควบคุมฝูงชนจำนวนเท่าเดิม คือ 3 กองร้อย และเตรียมไว้ในที่ตั้งอีก 3 กองร้อย รวมทั้งหมดเป็น 6 กองร้อย และหน่วยสนับสนุน ได้แก่ ตำรวจจราจร สายสืบ และสายตรวจ อีกจำนวนหนึ่ง โดยจะเริ่มวางกำลังด้านใน</p><p>และตั้งด่านบริเวณหน้าศาลตั้งแต่ช่วงเย็นเป็นต้นไปตลอดทั้งคืน จนจบการพิจารณาในวันที่ 22 ส.ค. โดยมี พ.ต.อ.พีระพงศ์ วงษ์สมาน รอง ผบก.น.2 เป็น ผบ.เหตุการณ์ คอยดูแลกำลังและสั่งการในพื้นที่อย่างใกล้ชิด</p><p>ทั้งนี้ ในช่วงเวลาประมาณ 20.00 น. จะเดินทางไปดูความพร้อมและกำชับในการปฏิบัติหน้าที่ด้วยตนเอง ซึ่งจนถึงขณะนี้ รายงานด้านการข่าวยังไม่มีเรื่องที่น่าวิตกกังวลแต่อย่างใด ทั้งในส่วนของมือที่ 3 ที่จ้องจะป่วน หรือรายงานเหตุรุนแรง เชื่อว่าจะจบด้วยดีเช่นเดียวกับครั้งที่ผ่านมา</p><p>อธิบดีผู้พิพากษาศาลอาญา กล่าวถึง กรณีการนัดฟังคำสั่งตัดสินเพิกถอนการประกันตัวแกนนำ นปช. ทั้ง 19 คน ในวันที่ 22 สิงหาคมนี้ ว่า สำหรับคำสั่งเพิกถอนการประกันตัวนั้น ขึ้นอยู่กับองค์คณะผู้พิพากษาเจ้าของสำนวน จะใช้ดุลพินิจว่า จะมีคำสั่งออกมาอย่างไร โดยอาจมี 3 แนวทาง ที่องค์คณะ จะนำมาพิจารณา คือ มีคำสั่งยกคำร้อง ไม่เพิกถอนการประกันตัวจำเลยทุกคน หรือศาลอาจมีคำสั่งให้เพิกถอนประกันตัวบางส่วน หรือมองในแง่ร้ายที่สุด คือ เพิกถอนการประกันตัวจำเลยทั้ง 19 คน เพราะถือว่าเป็นจำเลยชุดเดียวกัน มีข้อกำหนดเงื่อนไขปล่อยตัวชั่วคราวใกล้เคียงกัน แต่ตามหลักกฎหมาย บุคคลใดกระทำผิด บุคคลนั้นต้องรับผิดชอบ</p><p>ทั้งนี้เป็นไปได้ว่า หากศาลมีคำสั่งยกคำร้อง อาจมีคำสั่งกำหนดเงื่อนไขการปล่อยชั่วคราวเพิ่มเติมให้เข้มงวด เพราะหลักการกำหนดเงื่อนไขปล่อยชั่วคราวจำเลย เพื่อป้องกันจำเลยไม่ให้กระทำผิดซ้ำ ป้องกันผู้เสียหายจากภยันตรายใดๆ และเพื่อให้สังคมมีความปลอดภัย</p><p>นอกจากนี้ อธิบดีผู้พิพากษาศาลอาญา ยังกล่าวถึงกรณีศาลที่ศาลนัดสอบถาม นายยศวริศ ชูกล่อม หรือ เจ๋ง ดอกจิก แกนนำ นปช. จำเลยในคดีร่วมกันก่อการร้าย ว่า จนถึงขณะนี้ ทั้ง นายเจ๋ง และ นายนิพิฏฐ์ อินทรสมบัติ ส.ส.พัทลุง พรรคประชาธิปัตย์ ในฐานะผู้ร้อง ยังไม่ได้ยื่นหลักฐานเพิ่มเติมต่อศาลแต่อย่างใด นอกจากนี้ อธิบดีผู้พิพากษาศาลอาญา ยังกล่าวถึงกรณีของจำเลยในคดีก่อการร้าย อย่าง นายอริสมันต์ พงษ์เรืองรอง จำเลยที่ 24 ว่า หลัง นายอริสมันต์ ได้รับปล่อยชั่วคราวไป 2 วัน นายอริสมันต์ ได้ขึ้นปราศรัยที่ จ.นครราชสีมา แต่ศาลก็ได้ตักเตือนไปแล้ว โดย นายอริสมันต์ ก็ให้ความร่วมมือเป็นอย่างดี</p><p>อย่างไรก็ตาม จำเลยทุกคนที่ศาลอนุญาตให้ประกันตัวไปแล้วนั้น ศาลย่อมต้องมีหน้าที่ควบคุมตรวจสอบพฤติการณ์จำเลย ว่า ได้ปฏิบัติตามเงื่อนไขที่ศาลกำหนดไว้หรือไม่ โดยศาลเองจะทำเป็นไม่รับรู้ หรือรอให้บุคคลอื่นมายื่นคำร้องต่อศาลอย่างเดียวไม่ได้ ในเมื่อศาลเป็นคนปล่อย ศาลก็ต้องตรวจสอบ แต่ก็ต้องยอมรับว่า ในกรณีการปราศรัยตามจังหวัดต่างๆ นั้น อาจจะยากลำบากต่อการตรวจสอบบ้าง แต่ศาลก็ต้องดำเนินการ</p><p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: 0pt; margin-left: 0in; "><span style="color: rgb(255, 0, 0); "><span style="color: rgb(51, 51, 51); font-family: ''Microsoft Sans Serif''; font-size: 14pt; "><a href="http://www.innnews.co.th/" target="_blank" style="text-decoration: none; background-color: transparent; color: rgb(7, 81, 154); ">INNNEWS.CO.TH</a></span></span></p></span>', '2012822101549-544sdfsdtggf5.jpg', 1, '2012-08-22 10:15:49', 0);
INSERT INTO `post` VALUES (29, 12, 'หนุ่มพิการเข็นรถเข็น เดินทางหาแม่ กว่า 200 กิโล', 'ชาวบ้านได้พบเห็นหนุ่มพิการใจสู้คนหนึ่ง เข็นรถเข็นจาก อ.กาบเชิง จ.สุรินทร์ เพื่อเดินทางไปหาแม่ที่ จ.ศรีสะเกษ ตามลำพัง เป็นระยะทางกว่า 200 กิโลเมตร', '<span class="Apple-style-span" style="font-family: Tahoma; background-color: rgb(255, 255, 255); "><div style="text-align: center; "><img src="http://news.impaqmsn.com/imagelib/082012/d099fc1982ad37b.jpg" alt="" style="border-top-style: none !important; border-right-style: none !important; border-bottom-style: none !important; border-left-style: none !important; border-width: initial !important; border-color: initial !important; "></div><p>ชาวบ้านได้พบเห็นหนุ่มพิการใจสู้คนหนึ่ง เข็นรถเข็นจาก อ.กาบเชิง จ.สุรินทร์ เพื่อเดินทางไปหาแม่ที่ จ.ศรีสะเกษ ตามลำพัง เป็นระยะทางกว่า 200 กิโลเมตร โดยทราบชื่อต่อมาคือ นายกฤตวิช จันทระ อายุ 40 ปี<br><br>แต่เดิมประกอบอาชีพขายไอศกรีม แต่ประสบอุบัติเหตุโดนรถบรรทุกชนจนอาการสาหัส แพทย์จึงตัดขาขวาของตนออก โดยที่ตน และแม่ต้องแบกรับค่ารักษาร่วมแสนบาท</p><p>เนื่องจากคู่กรณีไม่ได้ให้ความช่วยเหลือใดๆ เลย ซึ่งหลังจากที่ตนกลายเป็นคนพิการ ภรรยาที่อยู่ร่วมกันมา ก็ทนกับความพิการของตนไม่ไหว ก่อนจะหอบลูกหนีออกจากบ้านไป ทำให้ตนต้องใช้ชีวิตอยู่คนเดียวตามลำพัง<br><br>ปัจจุบันนี้ตนอยู่ได้เพราะได้รับเบี้ยยังชีพของคนพิการ เดือนละ 500 บาท ถ้าหากวันไหนตนทนคิดถึงภรรยา และลูกไม่ไหว ก็จะเข็นรถไปหาแม่ที่ จ.ศรีสะเกษ ซึ่งถึงแม้ตนจะเคยท้อแท้ หมดหวัง แต่ก็จะพยายามสู้ชีวิตต่อไป</p><p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: 0pt; margin-left: 0in; "><span style="font-family: ''Cordia New'', sans-serif; color: rgb(31, 73, 125); font-size: 14pt; ">ข้อมูลโดย</span><span style="color: rgb(31, 73, 125); "><span style="font-size: 12pt; "><span style="font-family: Calibri; ">&nbsp;:&nbsp;&nbsp;</span></span></span><a href="http://www.talkystory.com/" target="_blank" style="text-decoration: none; background-color: transparent; color: rgb(7, 81, 154); "><span style="font-family: ''MS Sans Serif''; color: blue; font-size: 10pt; "><u>www.talkystory.com/</u></span></a></p></span>', '2012822101650-d099fc1982ad37b.jpg', 1, '2012-08-22 10:16:50', 0);
INSERT INTO `post` VALUES (30, 13, 'ช่วยชีวิตแมวหนีตายซุกใต้ห้องเครื่องรถยนต์', 'เจ้าหน้าที่กู้ภัยไตรคุณธรรมช่วยแมวเปอร์เซียติดอยุ่ในห้องเครื่องรถยนต์ เตรียมติดต่อหาผู้ใจบุญมาเลี้ยงต่อไป', '<div style="text-align: center;"><img src="http://www.dailynews.co.th/sites/default/files/imagecache/620x245/cover/150790.jpg"></div><div style="text-align: center; "><br></div><div style="text-align: left;"><span class="Apple-style-span" style="color: rgb(102, 102, 102); font-family: Thonburi, Tahoma, Arial, sans-serif, serif; background-color: rgb(255, 255, 255); "><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; ">วันนี้ (21 ส.ค.)&nbsp; เมื่อเวลาประมาณ 9.00 น.&nbsp; เจ้าหน้าที่หน่วยกู้ภัยไตรคุณธรรม&nbsp; ได้รับแจ้งเหตุจากนางชนิดา&nbsp; ศิริกิจ&nbsp; อายุ 52 ปี&nbsp; ว่ามีแมวเปอร์เซียขนาดใหญ่สีน้ำตาลมุดเข้าใต้ท้องรถของตนและเข้าไปอยู่ในห้องเครื่องรถยนต์ยี่ห้อฮอนด้า&nbsp; รุ่่นซีวิค&nbsp; สีดำ&nbsp; หมายเลขทะเบียน ฌป9748 กรุงเทพมหานคร&nbsp;&nbsp;&nbsp; เจ้าหน้าที่จึงเร่งช่วยชีวิตเจ้าแมวโชคร้ายอย่างเร่งด่วน<br>&nbsp;</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; ">จากการสำรวจพบว่าแมวตัวดังกล่าวติดอยู่กระโปรงหน้ารถในห้องเครื่องระหว่างห้องเครื่องกับห้องโดยสาร&nbsp; ส่วนหัวของแมวติดอยู่กับเพลาเกียร์&nbsp; เจ้าหน้าที่กู้ภัยต้องใช้เวลานานกว่า 2 ชั่วโมงจึงช่วยออกมาได้&nbsp; ในสภาพตื่นตกใจ&nbsp; เนื้อตัวเปรอะเปื้อนแต่ก็ยังคงความน่ารักอยู่&nbsp; เป็นแมวพันธุ์เปอร์เซีย&nbsp; เพศผู้&nbsp; สีน้ำตาล&nbsp; ขนฟู&nbsp; ค่อนข้างมีอายุแล้ว&nbsp;<br>&nbsp;</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; ">สอบถามนางชนิดา&nbsp; ทราบว่า&nbsp; ตนกำลังนั่งอยู่ภายในบ้านจากนั้นเห็นแมวตัวดังกล่าวกระโดดลงมาจากรถกระบะที่วิ่งอยู่กลางถนน&nbsp; ด้วยความตกใจจากเสียงรถและการถูกรถจักรยานยนต์เฉี่ยวบริเวณหาง&nbsp; จึงหนีตายลนลานเข้าห้องเครื่องรถยนต์ &nbsp;เบื้องต้นเจ้าหน้าที่กู้ภัยได้นำแมวตัวดังกล่าวไปเลี้ยงเพื่อรอเจ้าของหรือผู้ใจบุญมาติดต่อขอรับเลี้ยงต่อไป</p><p style="text-align: center;margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); "><img src="http://www.dailynews.co.th/sites/default/files/imagecache/620x245/photos/150790/0.jpg"></p><p style="text-align: center;margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); "><img src="http://www.dailynews.co.th/sites/default/files/imagecache/620x245/photos/150790/1.jpg"></p></span></div>', '2012822104449-150790.jpg', 1, '2012-08-22 10:44:49', 0);
INSERT INTO `post` VALUES (31, 13, 'ขี้เมา-สิงห์อมควัน ทรุด ครม.แอบขึ้นภาษี เหล้า-บุหรี่', 'ขี้เมา-สิงห์อมควัน ทรุดครม.แอบขึ้นภาษี เหล้า-บุหรี่ มีผลพรุ่งนี้ เหล้าขาว เหล้าผสม เพิ่มลิตรละ150 และ 350 บาท', '<div style="text-align: center;"><img src="http://www.dailynews.co.th/sites/default/files/imagecache/620x245/cover/150758.jpg"></div><div style="text-align: center;"><br></div><div style="text-align: left;"><span class="Apple-style-span" style="color: rgb(102, 102, 102); font-family: Thonburi, Tahoma, Arial, sans-serif, serif; background-color: rgb(255, 255, 255); "><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; "><strong style="margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 13px; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; background-position: initial initial; background-repeat: initial initial; ">ส่วนบุหรี่ขึ้นอีกซอง 6-8 บาท อ้างช่วยคนอบายมุก วงในเผยใช้เวลาถกไม่นาน ก่อนดึงเอกสารกลับไม่มีแถลงข่าว</strong></p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; ">วันนี้ ( 21 ส.ค.) รายงานข่าวจากทำเนียบรัฐบาล เปิดเผยว่า ที่ประชุม ครม.ได้เห็นชอบการปรับขึ้นภาษีสรรพสามิตสุราและยาสูบเพิ่มขึ้น ตามที่กระทรวงการคลังเสนอ โดยให้มีผลในวันที่ 22 ส.ค.นี้ ซึ่งการดำเนินการดังกล่าวกระทรวงการคลังได้ให้เหตุผลว่า จะทำให้รายได้ในการจัดเก็บภาษีของรัฐเพิ่มขึ้น ขณะเดียวกันจะช่วยให้ประชาชนลดการบริโภคสุราและบุหรี่ลงจากเดิม&nbsp; เนื่องจากเมื่อเพิ่มราคาแล้วจะทำให้แรงจูงใจในการซื้อลดลง ซึ่งในเรื่องดังกล่าวที่ประชุมได้ใช้เวลาในการพิจารณาอย่างรวดเร็ว จากนั้นจึงเก็บเอกสารรายละเอียดกลับทันที และไม่ได้มีการแถลงข่าวออกมาให้รับทราบ เพราะเกรงว่า ผู้ประกอบการจะกักตุนสินค้าเอาไว้</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; ">นางเบญจา หลุยเจริญ อธิบดีกรมสรรพสามิต กล่าวว่า การปรับขึ้นภาษีสรรพสามิตสุรา ทำให้ราคาปรับขึ้นจากเดิมที่กรมฯเก็บภาษีสุรากลั่นตามปริมาณต่อลิตรแอลกอฮอล์ โดยสุราขาวจากเดิมที่เก็บ 120 บาทต่อลิตรเพิ่มเป็น 150 บาทต่อลิตร สุราผสมจากเดิมที่เก็บ 300 บาทต่อลิตรเป็น 350 บาทต่อลิตร ส่วนภาษียาสูบ ที่เป็นซิการ์แรต ที่จัดเก็บตามมูลค่า 85% แต่ไม่มีการจัดเก็บตามปริมาณ ซึ่งทุกซองต้องเสียภาษีสรรพสามิตอย่างน้อยซองละ 20 บาท การขึ้นราคา ส่งผลให้บุหรี่ทั้งไทยและต่างประเทศรวม 8 ยี่ห้อต้องเสียภาษีเพิ่มขึ้นซองละ 6-8 บาท.</p></span></div>', '2012822104611-150758.jpg', 1, '2012-08-22 10:46:11', 0);
INSERT INTO `post` VALUES (32, 13, 'เตือนภัยหาดพัทยา โดนทะเลเซาะหมด 22 ปี', 'นักวิชาการเสนอรายงานปัญหาชายหาดพัทยา พบหาดแคบลงเรื่อยๆ จากการก่อสร้างและมีการกัดเซาะชายฝั่ง คาดไม่รีบแก้ปัญหา อีก 22 ปี พัทยาจะไม่มีหาด', '<div style="text-align: center;"><img src="http://www.dailynews.co.th/sites/default/files/imagecache/620x245/cover/150792.jpg"></div><div style="text-align: center;"><br></div><div style="text-align: left;"><span class="Apple-style-span" style="color: rgb(102, 102, 102); font-family: Thonburi, Tahoma, Arial, sans-serif, serif; background-color: rgb(255, 255, 255); "><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; ">วันนี้ (21 ส.ค.) ที่ศาลากลางจังหวัดชลบุรี&nbsp; มีการนำเสนอรายงานคณะกรรมการกำกับดูแลและติดตามการคุ้มครองสิ่งแวดล้อมในบริเวณพื้นที่อำเภอบางละมุงบและสัตหีบ&nbsp; จังหวัดชลบุรี&nbsp; ต่อนายพงษ์ศักดิ์&nbsp; ปรีชาวิทย์&nbsp; รองผู้ว่าราชการจังหวัดชลบุรี&nbsp; พบว่า ปัจจุบันชายหาดเมืองพัทยาประสบปัญหาต่าง ๆ มากมาย&nbsp; อาทิ ความสกปรกของชายหาด&nbsp; และชายหาดแคบมากเมื่อเวลาน้ำทะเลสูงขึ้น&nbsp; เพราะพื้นที่ชายหาดเกือบทั้งหมดจมน้ำ&nbsp; ทำให้ไม่มีพื้นที่ที่จะเดินชมชายหาดได้&nbsp; ซึ่งปัญหาที่เกิดขึ้นมีสาเหตุมาจากการก่อสร้างถนน&nbsp; แนวกำแพงป้องกันตลิ่ง&nbsp; ท่าเรือ&nbsp; และประชาชนสร้างบ้านพักและร้านอาหารรุกล้ำพื้นที่ชายหาด&nbsp; ทำให้คลื่นที่เข้าปะทะสิ่งกีดขวางชายหาดต่าง ๆ โดยเฉพาะในช่วงน้ำขึ้นเต็มที่และเกิดการสะท้อนกลับของคลื่น&nbsp; ทำให้มวลทราบเกิดการเคลื่อนที่ออกจากฝั่งมากขึ้น&nbsp; โดยเฉพาะในช่วงมรสุมตะวันตกเฉียงใต้&nbsp; ก่อให้เกิดปัญหาของการกัดเซาะชายฝั่ง&nbsp;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 0px; border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; outline-width: 0px; outline-style: initial; outline-color: initial; font-size: 1em; vertical-align: baseline; background-image: initial; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: transparent; color: rgb(51, 51, 51); background-position: initial initial; background-repeat: initial initial; ">จากปัญหาดังกล่าวที่เกิดขึ้นนักวิจัยและนักวิชาการจากสถาบันวิจัยทรัพยากรทางน้ำ&nbsp; จุฬาลงกรณ์มหาวิทยาลัย&nbsp; จึงให้ความเห็นว่าหากไม่รีบแก้ไขปัญหาที่เกิดขึ้นอีกไม่เกิน 22 ปีนับจากนี้&nbsp; เมืองพัทยาจะไม่มีชายหาดไห้นักท่องเที่ยวได้เดินชมทัศนียภาพและพักผ่อนอีกต่อไป</p></span></div>', '2012822110510-150792.jpg', 1, '2012-08-22 10:47:37', 0);
INSERT INTO `post` VALUES (34, 1, 'Total Recall คนทะลุโลก', 'Total Recall เคยถูกสร้างเป็นหนังมาแล้วครั้งหนึ่งเมื่อปี 1990 ภายใต้การกำกับของคนทำหนังชาวเนเธอร์แลนด์ พอล เวอร์โฮเวน', '<span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><iframe width="656" height="336" src="http://www.majortrailers.com/embed.php?mid=985&amp;w=640&amp;h=320" frameborder="0" scrolling="NO" style="margin-top: -8px; margin-right: 0px; margin-bottom: 0px; margin-left: -8px; overflow-x: hidden; overflow-y: hidden; "></iframe></span><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><br></span></div><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">วันที่เข้าฉาย</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">02/08/2012</p><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">เรื่องย่อ</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">Total Recall ฉบับใหม่ได้ เคิร์ต วิมเมอร์ (Salt) มารับหน้าที่เขียนบท และ เลน ไวซ์แมน (Live Free or Die Hard, Underworld) เป็นผู้กำกับ โดยผู้สร้างอยู่ระหว่างเจรจาให้พระเอกชาวไอริช โคลิน ฟาร์เรลล์ วัย 34 มารับบทนำในหนังเรื่องนี้ ขณะเดียวกันยังมีตัวเลือกอื่น ๆ รวมถึง ไมเคิล ฟาสเบนเดอร์ และ ทอม ฮาร์ดี้ ในบทนำหาก ฟาร์เรลล์ ปฏิเสธ หนังวางแผนไว้ว่าจะเปิดกล้องภายในเดือน มี.ค. 2011 เพื่อเข้าฉายในปี 2012</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">Total Recall เคยถูกสร้างเป็นหนังมาแล้วครั้งหนึ่งเมื่อปี 1990 ภายใต้การกำกับของคนทำหนังชาวเนเธอร์แลนด์ พอล เวอร์โฮเวน มีนักแสดงแอ็กชั่นคนดัง อาร์โนลด์ ชวาร์ตเซเนคเกอร์ รับบทเป็นชายหนุ่มในโลกอนาคต ที่ความทรงจำในอดีตของเขา ได้ถูกปลุกให้ตื่นขึ้นมา ภายหลังเข้าไปใช้บริการของเครื่องสร้างความฝัน เครื่องมือเพื่อความบันเทิงในโลกยุคนั้น</p></span></div>', '2012822111610-Total_Recall_poster1.jpg', 1, '2012-08-22 11:16:10', 0);
INSERT INTO `post` VALUES (35, 1, 'The Bourne Legacy พลิกแผนล่ายอดจารชน', 'The Bourne Legacy พลิกแผนล่ายอดจารชน โทนี่ กิลรอยที่เป็นทั้งมือเขียนบทและผู้กำกับของภาพยนตร์', '<span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><iframe width="656" height="336" src="http://www.majortrailers.com/embed.php?mid=1104&amp;w=640&amp;h=320" frameborder="0" scrolling="NO" style="margin-top: -8px; margin-right: 0px; margin-bottom: 0px; margin-left: -8px; overflow-x: hidden; overflow-y: hidden; "></iframe></span><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><br></span></div><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">วันที่เข้าฉาย</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">09/08/2012</p><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">เรื่องย่อ</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">โทนี่ กิลรอย นักเล่าเรื่องผู้อยู่เบื้องหลังภาพยนตร์ชุด Bourne ทำหน้าที่กำกับบทต่อไปของภาพยนตร์แฟรนไชส์แนวสายลับจารกรรมยอดนิยมที่ประสบความสำเร็จมหาศาล จนทำรายได้จากทั่วโลกไปแล้วเกือบ 1 พันล้าน ในตอนที่มีชื่อว่า The Bourne Legacy โทนี่ กิลรอยที่เป็นทั้งมือเขียนบทและผู้กำกับของภาพยนตร์เรื่องนี้ได้ขยับขยายโลกของ Bourne ที่ให้กำเนิดโดย โรเบิร์ต ลัดลั่ม ออกไปด้วยเรื่องราวที่มีเอกลักษณ์ที่จะแนะนำให้เราได้รู้จักกับฮีโร่คนใหม่ (เจอเรมี่ เร็นเนอร์) ผู้ซึ่งสถานการณ์เสี่ยงเป็นเสี่ยงตาย ได้ถูกจุดประกายขึ้นโดยเหตุการณ์จากภาพยนตร์สามภาคแรก</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">สำหรับ The Bourne Legacy นี้ เรนเนอร์ยังได้เพื่อนร่วมจอหน้าใหม่ (ใหม่สำหรับภาพยนตร์ชุดนี้ แต่ฝีมือฉมังทั้งสิ้น) อย่าง เรเชล ไวสซ์, เอ๊ดเวิร์ด นอร์ตัน, สเตซี่ย์ คีช และออสการ์ ไอแซ็ค ขณะที่หน้าเก่าจากภาพยนตร์ภาคก่อนๆ อย่าง อัลเบิร์ต ฟินนี่ย์, โจน อัลเลน, เดวิด สเตแธร์น และสก็อตต์ เกลน ยังคงกลับมารับบทเดิม</p></span></div>', '2012822111855-The-Bourne-Legacy.jpg', 1, '2012-08-22 11:18:55', 0);
INSERT INTO `post` VALUES (36, 1, 'Always on my mind คิดถึงทุกคืน', 'เรื่องราวความรัก 4 เรื่อง ของคน 4 คู่ ที่ได้มาพบกันในช่วงวันขึ้นปีใหม่ที่เวียงจัน', '<div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><iframe width="640" height="330" src="http://www.youtube.com/embed/clrwvlxAHDo?wmode=opaque" frameborder="0" allowfullscreen=""></iframe></span></div><div><br></div><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">วันที่เข้าฉาย</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">09/08/2012</p><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">เรื่องย่อ</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">เรื่องราวความรัก 4 เรื่อง ของคน 4 คู่ ที่ได้มาพบกันในช่วงวันขึ้นปีใหม่ที่เวียงจัน</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; "><strong style="font-weight: bold; ">งานเลี้ยงรุ่น</strong>-เรื่องของสาวออฟฟิศท่าทางเหงาๆ ที่เตรียมตัวจะไปงานเลี้ยงรุ่นของเพื่อนร่วมวิทยาลัย แล้วเพิ่งพบว่าตัวเองมีความรู้สึกดีๆกับเพื่อนหนุ่มร่วมห้องเรียน ที่ตอนนั้นมักจะอายๆที่ถูกล้อว่าเป็นแฟนกัน แต่คราวนี้ไม่รู้จะทำยังไงได้ เพราะมีข่าวลือว่าหนุ่มคนนี้จะเอาการ์ดแต่งงานมาแจก</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; "><strong style="font-weight: bold; ">พรหมลิขิต</strong>-ช่างภาพหนุ่มจากเมืองไทย มาถ่ายรูปงานเคาดาวน์ แล้วพบเจอสาวคนหนึ่งอย่างบังเอิญในหลายๆที่ ไม่ว่าจะเป็นวัด ตลาด หรือตามสถานที่เที่ยว จนวันเคาดาวน์ส่งท้ายปี เขาจึงตัดสินใจที่จะต้องหาทางทำความรู้จักกับเธอให้ได้</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; "><strong style="font-weight: bold; ">ฝนดาวตก</strong>-หนุ่มวัยรุ่นมาดูฝนดาวตกย่านแคมของ เขาได้พบกับสาวลาวน่ารักคนหนึ่ง คุยกันอย่างประทับใจ แต่เขาเผลอหลับไปเสียก่อน ไม่ได้ขอเบอร์โทรหรืออีเมลล์กันไว้ พออีกปีที่มีข่าวว่าจะมีฝนดาวตกแถวที่เดิม เจ้าหนุ่มจึงมารอด้วยความหวังว่าสาวคนนั้นจะกลับมาอีกครั้ง</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; "><strong style="font-weight: bold; ">จดหมายรัก</strong>-หนุ่มจากต่างแขวง เขียนจดหมายมาหาสาวที่เวียงจันตั้งแต่ตอนมัธยม ผ่านมาเจ็ดแปดปีแล้ว จนเมื่อเจ้าหนุ่มเดินทางมาหาเธอ ความรู้สึกของทั้งสองนั้นผูกพันกันอย่างไม่รู้ตัว แต่ทว่าเมื่อมีเพื่อนสาวมาชอบเจ้าหนุ่มคนนี้เข้า ทำให้เกิดความรู้สึกแบบรักสามเส้าไปตลอดทริปท่องเที่ยวด้วยกัน</p><h2 style="font-size: 26px; margin-top: 0px; margin-right: 0px; margin-bottom: 0.75em; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1.2em; "><span style="color: rgb(239, 69, 15); ">เข้าฉายที่เอสพลานาด รัชดา วันที่ 9 สิงหา 2555 เป็นต้นไป</span></h2></span></div>', '2012822112037-alwaysonmymind.jpg', 1, '2012-08-22 11:20:37', 0);
INSERT INTO `post` VALUES (37, 1, 'Sat2Mon คืนวันเสาร์ถึงเช้าวันจันทร์', 'เรื่องราวของชายหนุ่มที่แอบหลงรักหญิงสาวในดวงใจ แต่ความรักของเขาจะไม่มีทางเกิดขึ้นเลยถ้าไม่ได้ตัวช่วยซึ่งคือหญิงสาวอีกคน', '<span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><iframe width="656" height="336" src="http://www.majortrailers.com/embed.php?tid=655&amp;w=640&amp;h=320" frameborder="0" scrolling="NO" style="margin-top: -8px; margin-right: 0px; margin-bottom: 0px; margin-left: -8px; overflow-x: hidden; overflow-y: hidden; "></iframe></span><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><div id="movie-content"></div></span><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><br></span></div><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">วันที่เข้าฉาย</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">09/08/2012</p><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">เรื่องย่อ</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">“คืนวันเสาร์ถึงเช้าวันจันทร์” เป็นเรื่องราวของชายหนุ่มที่แอบหลงรักหญิงสาวในดวงใจ แต่ความรักของเขาจะไม่มีทางเกิดขึ้นเลยถ้าไม่ได้ตัวช่วยซึ่งคือหญิงสาวอีกคน ที่เป็นเพื่อนสนิทของเจ้าหล่อนในขณะที่ตัวเพื่อนสาวเองก็จะมีคนรักเป็นชาย หนุ่มที่ทุกอย่างในชีวิตต้องเพอร์เฟ็คต์ และเรื่อราวทุกอย่างก็เริ่มต้นขึ้นเมื่อเส้นทางความรักของคนทั้งสี่ที่เดิน เข้ามาบรรจบกันก็เกิดเรื่องราวชุลมุนจนเกิดคำถามถึงความลงตัวของ “จังหวะความรัก” ว่าเราจะรัก “คนที่ใช่ในวันที่ผิด” ได้ไหมนะ</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; text-align: center; ">บางทีอาจจะเป็นเพราะสนามแม่เหล็กของพรหมลิขิต ที่ดึงเรามาเจอกันผิดเวลา<br>บางทีอาจจะเป็นเพราะเราต้องรอถามตัวเองว่าเขาใช่หรือไม่<br>บางทีอาจจะเป็นเพราะเราต้องการหาคนที่จะมี&nbsp;<strong style="font-weight: bold; ">“จังหวะ”</strong>&nbsp;ตรงกับหัวใจ<br>บางทีอาจจะเป็นมนต์เสน่ห์ใน…<strong style="font-weight: bold; ">“คืนวันเสาร์ถึงเช้าวันจันทร์”</strong></p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; text-align: center; ">ภาพยนตร์ รั่วกันมันส์ รักกันมาก โดย แดน-วรเวช<br><strong style="font-weight: bold; "><span style="color: rgb(238, 52, 17); ">9 สิงหาคมนี้</span></strong></p></span></div>', '2012822112254-Poster-THEME-MEEYEN__resize.jpg', 1, '2012-08-22 11:22:54', 0);
INSERT INTO `post` VALUES (38, 1, 'Yes Or No 2 รักไม่รัก อย่ากั๊กเลย 2', 'เรื่องราวในภาคนี้ก็ต่อเนื่องจากภาคแรก ความรักของ คิม (ติ๊นา – ศุภนาฏ จิตตลีลา) และ พาย (ออม – สุชาร์ มานะยิ่ง) ที่คบหาดูใจกันมาถึง 3 ปี และปีที่ 4', '<span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><iframe width="656" height="336" src="http://www.majortrailers.com/embed.php?tid=666&amp;w=640&amp;h=320" frameborder="0" scrolling="NO" style="margin-top: -8px; margin-right: 0px; margin-bottom: 0px; margin-left: -8px; overflow-x: hidden; overflow-y: hidden; "></iframe></span><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><br></span></div><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">วันที่เข้าฉาย</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">16/08/2012</p><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">เรื่องย่อ</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">เรื่องราวในภาคนี้ก็ต่อเนื่องจากภาคแรก ความรักของ คิม (ติ๊นา – ศุภนาฏ จิตตลีลา) และ พาย (ออม – สุชาร์ มานะยิ่ง) ที่คบหาดูใจกันมาถึง 3 ปี และปีที่ 4 ทั้งสองคน ต่างต้องแยกไปฝึกงาน ทำให้ความรักต้องพิสูจน์ด้วยระยะทาง คิม ขึ้นไปฝึกงานที่จังหวัดน่าน ในไร่ของเพื่อนพ่อ เพราะมีแผนว่าเรียนจบจะกลับไปดูแลไร่ของพ่อต่อ ส่วน พาย เด็กประมง จึงต้องไปฝึกงานที่จันทบุรี ทั้งคิมและพายต่างก็ไม่เคยห่างกันเลย และครั้งนี้ถือเป็นครั้งแรกที่ทั้งสองคนต้องห่างไกลกัน ความรักจึงถูกพิสูจน์ด้วยระยะทางและเวลาซึ่งต้องมาลุ้นกันว่า ความห่างไกลกันครั้งนี้จะทำให้ความรักของทั้งคู่สั่นคลอนหรือรักกันมากขึ้นกว่าเดิม</p></span></div>', '2012822112408-yesorno.jpg', 1, '2012-08-22 11:24:08', 0);
INSERT INTO `post` VALUES (39, 1, 'The Expendables 2 โคตรคนทีมเอ็กซ์เพนเดเบิ้ล 2', 'ทีมโคตรคนใน The Expendables 2 นั้นเรียกได้ว่ารวมตัวพ่อของพระเอกบู๊ๆแถวหน้ามาไว้เพียบ', '<span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><iframe width="656" height="336" src="http://www.majortrailers.com/embed.php?mid=1172&amp;w=640&amp;h=320" frameborder="0" scrolling="NO" style="margin-top: -8px; margin-right: 0px; margin-bottom: 0px; margin-left: -8px; overflow-x: hidden; overflow-y: hidden; "></iframe></span><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><br></span></div><div><span class="Apple-style-span" style="color: rgb(34, 34, 34); font-family: ''Helvetica Neue'', Arial, Helvetica, Geneva, sans-serif; line-height: 20px; background-color: rgb(255, 255, 255); "><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">วันที่เข้าฉาย</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">16/08/2012</p><h3 style="font-size: 24px; margin-top: 0px; margin-right: 0px; margin-bottom: 8px; margin-left: 0px; font-family: DBHelvethaicaX55Regular, sans-serif; line-height: 1em; ">เรื่องย่อ</h3><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">ภาพยนตร์ภาคต่อเรื่อง The Expendables 2 ของผู้กำกับ Simon West ที่ขณะนี้กำลังตั้งหน้าตั้งตาถ่ายทำกันอยู่ด้วย ถึงแม้จะยังไม่มีกำหนดการฉายออกมาอย่างเป็นทางการ แต่ก็ปล่อยโฉมทีมโครตคนมหากาฬมาอวดโฉมแรกให้แฟนๆได้ชมกันก่อนแล้ว</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">ทีมโคตรคนใน The Expendables 2 นั้นเรียกได้ว่ารวมตัวพ่อของพระเอกบู๊ๆแถวหน้ามาไว้เพียบ ไม่ว่าจะเป็น Sylvester Stallone, Bruce Willis, Chuck Norris, Jean-Claude Van Damme และคนเหล็ก Arnold Schwarzenegger รวมถึงรุ่นเล็กอย่างหนุ่มหล่อ Liam Hemsworth เข้าร่วมทีมคนมหากาฬในภาคนี้ด้วย</p><p style="margin-top: 0px; margin-right: 0px; margin-bottom: 1.5em; margin-left: 0px; ">The Expendables 2 จะเข้าฉายในปี 2012 นำแสดงโดย Sylvester Stallone, Bruce Willis, Chuck Norris, Jean-Claude Van Damme, Arnold Schwarzenegger, Liam Hemsworth, Scott Adkins กำกับโดย Simon West</p></span></div>', '2012822112608-exp2.png', 1, '2012-08-22 11:26:08', 0);
